INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-004', 'en', 'What is the minimum safe temperature for hot holding food?', '[{"text":"55°C","correct":false},{"text":"60°C","correct":false},{"text":"65°C","correct":true},{"text":"70°C","correct":false}]', 'Hot food must be maintained at 65°C or above to prevent bacterial growth, as specified in RD 3484/2000.', 1),
('G-FS-004', 'en', 'How long can food remain below 65°C before it must be discarded?', '[{"text":"1 hour","correct":false},{"text":"2 hours","correct":true},{"text":"3 hours","correct":false},{"text":"4 hours","correct":false}]', 'Food that falls below 65°C for more than 2 hours must be discarded to prevent bacterial growth in the danger zone.', 2),
('G-FS-004', 'en', 'What core temperature must be reached when reheating food?', '[{"text":"65°C","correct":false},{"text":"70°C","correct":false},{"text":"75°C","correct":true},{"text":"80°C","correct":false}]', 'When reheating previously cooked food, it must reach a core temperature of 75°C to ensure all harmful bacteria are destroyed.', 3),
('G-FS-004', 'en', 'What type of thermometer should be used for checking hot holding temperatures?', '[{"text":"Infrared only","correct":false},{"text":"Calibrated thermometer","correct":true},{"text":"Visual inspection","correct":false},{"text":"Any kitchen thermometer","correct":false}]', 'Calibrated thermometers must be used to ensure accurate temperature readings for food safety compliance.', 4),
('G-FS-004', 'en', 'How frequently should hot holding temperatures be recorded?', '[{"text":"Once per shift","correct":false},{"text":"Regularly throughout service","correct":true},{"text":"Only at the start of service","correct":false},{"text":"Recording is optional","correct":false}]', 'Hot holding temperatures must be monitored and recorded regularly throughout service to ensure continuous compliance with safety standards.', 5),
('G-FS-004', 'en', 'What regulation governs hot holding temperature standards in Spain?', '[{"text":"RD 109/2010","correct":false},{"text":"RD 3484/2000","correct":true},{"text":"EU 852/2004","correct":false},{"text":"RD 140/2003","correct":false}]', 'RD 3484/2000 establishes hygiene standards including specific requirements for hot holding temperatures in food establishments.', 6),
('G-FS-004', 'en', 'What should you do if hot holding equipment malfunctions during service?', '[{"text":"Continue using it","correct":false},{"text":"Assess food safety and potentially discard affected items","correct":true},{"text":"Lower the temperature setting","correct":false},{"text":"Mix hot and cold food","correct":false}]', 'Equipment malfunction requires immediate assessment of food safety. Items held at unsafe temperatures for too long must be discarded.', 7),
('G-FS-004', 'en', 'Why is 65°C the minimum temperature for hot holding?', '[{"text":"It saves energy","correct":false},{"text":"It prevents most bacterial growth","correct":true},{"text":"It is easier to maintain","correct":false},{"text":"Customers prefer this temperature","correct":false}]', 'At 65°C or above, most pathogenic bacteria cannot multiply, making this the critical minimum temperature for safe hot holding.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-004', 'es', '¿Cuál es la temperatura mínima segura para mantener alimentos calientes?', '[{"text":"55°C","correct":false},{"text":"60°C","correct":false},{"text":"65°C","correct":true},{"text":"70°C","correct":false}]', 'Los alimentos calientes deben mantenerse a 65°C o más para evitar la proliferación bacteriana, según especifica el RD 3484/2000.', 1),
('G-FS-004', 'es', '¿Cuánto tiempo pueden permanecer los alimentos por debajo de 65°C antes de ser desechados?', '[{"text":"1 hora","correct":false},{"text":"2 horas","correct":true},{"text":"3 horas","correct":false},{"text":"4 horas","correct":false}]', 'Los alimentos que descienden por debajo de 65°C durante más de 2 horas deben desecharse para evitar la proliferación bacteriana en la zona de peligro.', 2),
('G-FS-004', 'es', '¿Qué temperatura interna debe alcanzarse al recalentar alimentos?', '[{"text":"65°C","correct":false},{"text":"70°C","correct":false},{"text":"75°C","correct":true},{"text":"80°C","correct":false}]', 'Al recalentar alimentos previamente cocinados, deben alcanzar una temperatura interna de 75°C para asegurar la destrucción de todas las bacterias dañinas.', 3),
('G-FS-004', 'es', '¿Qué tipo de termómetro debe utilizarse para comprobar las temperaturas de mantenimiento en caliente?', '[{"text":"Solo infrarrojo","correct":false},{"text":"Termómetro calibrado","correct":true},{"text":"Inspección visual","correct":false},{"text":"Cualquier termómetro de cocina","correct":false}]', 'Deben utilizarse termómetros calibrados para asegurar lecturas de temperatura precisas para el cumplimiento de la seguridad alimentaria.', 4),
('G-FS-004', 'es', '¿Con qué frecuencia deben registrarse las temperaturas de mantenimiento en caliente?', '[{"text":"Una vez por turno","correct":false},{"text":"Regularmente durante el servicio","correct":true},{"text":"Solo al inicio del servicio","correct":false},{"text":"El registro es opcional","correct":false}]', 'Las temperaturas de mantenimiento en caliente deben monitorearse y registrarse regularmente durante el servicio para asegurar el cumplimiento continuo de los estándares de seguridad.', 5),
('G-FS-004', 'es', '¿Qué normativa regula los estándares de temperatura de mantenimiento en caliente en España?', '[{"text":"RD 109/2010","correct":false},{"text":"RD 3484/2000","correct":true},{"text":"UE 852/2004","correct":false},{"text":"RD 140/2003","correct":false}]', 'El RD 3484/2000 establece los estándares de higiene incluyendo requisitos específicos para las temperaturas de mantenimiento en caliente en establecimientos alimentarios.', 6),
('G-FS-004', 'es', '¿Qué debe hacerse si el equipo de mantenimiento en caliente falla durante el servicio?', '[{"text":"Continuar usándolo","correct":false},{"text":"Evaluar la seguridad alimentaria y potencialmente desechar los artículos afectados","correct":true},{"text":"Bajar la configuración de temperatura","correct":false},{"text":"Mezclar alimentos calientes y fríos","correct":false}]', 'La avería del equipo requiere una evaluación inmediata de la seguridad alimentaria. Los artículos mantenidos a temperaturas inseguras durante demasiado tiempo deben desecharse.', 7),
('G-FS-004', 'es', '¿Por qué 65°C es la temperatura mínima para el mantenimiento en caliente?', '[{"text":"Ahorra energía","correct":false},{"text":"Previene la mayoría del crecimiento bacteriano","correct":true},{"text":"Es más fácil de mantener","correct":false},{"text":"Los clientes prefieren esta temperatura","correct":false}]', 'A 65°C o más, la mayoría de las bacterias patógenas no pueden multiplicarse, lo que hace de esta la temperatura mínima crítica para el mantenimiento seguro en caliente.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-004', 'nl', 'Wat is de minimale veilige temperatuur voor het warmhouden van voedsel?', '[{"text":"55°C","correct":false},{"text":"60°C","correct":false},{"text":"65°C","correct":true},{"text":"70°C","correct":false}]', 'Warm voedsel moet op 65°C of hoger worden gehouden om bacteriegroei te voorkomen, zoals gespecificeerd in RD 3484/2000.', 1),
('G-FS-004', 'nl', 'Hoe lang kan voedsel onder de 65°C blijven voordat het moet worden weggegooid?', '[{"text":"1 uur","correct":false},{"text":"2 uur","correct":true},{"text":"3 uur","correct":false},{"text":"4 uur","correct":false}]', 'Voedsel dat langer dan 2 uur onder de 65°C komt, moet worden weggegooid om bacteriegroei in de gevarenzone te voorkomen.', 2),
('G-FS-004', 'nl', 'Welke kerntemperatuur moet worden bereikt bij het opwarmen van voedsel?', '[{"text":"65°C","correct":false},{"text":"70°C","correct":false},{"text":"75°C","correct":true},{"text":"80°C","correct":false}]', 'Bij het opwarmen van eerder gekookt voedsel moet een kerntemperatuur van 75°C worden bereikt om alle schadelijke bacteriën te vernietigen.', 3),
('G-FS-004', 'nl', 'Welk type thermometer moet worden gebruikt voor het controleren van warmhoudtemperaturen?', '[{"text":"Alleen infrarood","correct":false},{"text":"Gekalibreerde thermometer","correct":true},{"text":"Visuele inspectie","correct":false},{"text":"Elke keukenthermometer","correct":false}]', 'Gekalibreerde thermometers moeten worden gebruikt om nauwkeurige temperatuurmetingen te waarborgen voor naleving van de voedselveiligheid.', 4),
('G-FS-004', 'nl', 'Hoe vaak moeten warmhoudtemperaturen worden geregistreerd?', '[{"text":"Eenmaal per dienst","correct":false},{"text":"Regelmatig tijdens de dienst","correct":true},{"text":"Alleen bij het begin van de dienst","correct":false},{"text":"Registratie is optioneel","correct":false}]', 'Warmhoudtemperaturen moeten regelmatig tijdens de dienst worden gecontroleerd en geregistreerd om continue naleving van veiligheidsnormen te waarborgen.', 5),
('G-FS-004', 'nl', 'Welke regelgeving regelt warmhoudtemperatuurnormen in Spanje?', '[{"text":"RD 109/2010","correct":false},{"text":"RD 3484/2000","correct":true},{"text":"EU 852/2004","correct":false},{"text":"RD 140/2003","correct":false}]', 'RD 3484/2000 stelt hygiënenormen vast, inclusief specifieke vereisten voor warmhoudtemperaturen in horecagelegenheden.', 6),
('G-FS-004', 'nl', 'Wat moet u doen als warmhoudapparatuur tijdens de dienst defect raakt?', '[{"text":"Blijven gebruiken","correct":false},{"text":"Voedselveiligheid beoordelen en mogelijk getroffen items weggooien","correct":true},{"text":"Temperatuurinstelling verlagen","correct":false},{"text":"Warm en koud voedsel mengen","correct":false}]', 'Een defect aan apparatuur vereist onmiddellijke beoordeling van de voedselveiligheid. Items die te lang op onveilige temperaturen zijn gehouden, moeten worden weggegooid.', 7),
('G-FS-004', 'nl', 'Waarom is 65°C de minimumtemperatuur voor warmhouden?', '[{"text":"Het bespaart energie","correct":false},{"text":"Het voorkomt de meeste bacteriegroei","correct":true},{"text":"Het is gemakkelijker te handhaven","correct":false},{"text":"Klanten geven de voorkeur aan deze temperatuur","correct":false}]', 'Bij 65°C of hoger kunnen de meeste pathogene bacteriën zich niet vermenigvuldigen, waardoor dit de kritische minimumtemperatuur is voor veilig warmhouden.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-004', 'de', 'Was ist die minimale sichere Temperatur für das Warmhalten von Speisen?', '[{"text":"55°C","correct":false},{"text":"60°C","correct":false},{"text":"65°C","correct":true},{"text":"70°C","correct":false}]', 'Warme Speisen müssen bei 65°C oder höher gehalten werden, um Bakterienwachstum zu verhindern, wie in RD 3484/2000 festgelegt.', 1),
('G-FS-004', 'de', 'Wie lange können Speisen unter 65°C bleiben, bevor sie entsorgt werden müssen?', '[{"text":"1 Stunde","correct":false},{"text":"2 Stunden","correct":true},{"text":"3 Stunden","correct":false},{"text":"4 Stunden","correct":false}]', 'Speisen, die länger als 2 Stunden unter 65°C fallen, müssen entsorgt werden, um Bakterienwachstum in der Gefahrenzone zu verhindern.', 2),
('G-FS-004', 'de', 'Welche Kerntemperatur muss beim Aufwärmen von Speisen erreicht werden?', '[{"text":"65°C","correct":false},{"text":"70°C","correct":false},{"text":"75°C","correct":true},{"text":"80°C","correct":false}]', 'Beim Aufwärmen von zuvor gekochten Speisen muss eine Kerntemperatur von 75°C erreicht werden, um alle schädlichen Bakterien zu zerstören.', 3),
('G-FS-004', 'de', 'Welche Art von Thermometer sollte für die Überprüfung der Warmhaltetemperaturen verwendet werden?', '[{"text":"Nur Infrarot","correct":false},{"text":"Kalibriertes Thermometer","correct":true},{"text":"Visuelle Inspektion","correct":false},{"text":"Jedes Küchenthermometer","correct":false}]', 'Kalibrierte Thermometer müssen verwendet werden, um genaue Temperaturmessungen zur Einhaltung der Lebensmittelsicherheit zu gewährleisten.', 4),
('G-FS-004', 'de', 'Wie häufig sollten Warmhaltetemperaturen aufgezeichnet werden?', '[{"text":"Einmal pro Schicht","correct":false},{"text":"Regelmäßig während des Betriebs","correct":true},{"text":"Nur zu Beginn des Betriebs","correct":false},{"text":"Aufzeichnung ist optional","correct":false}]', 'Warmhaltetemperaturen müssen regelmäßig während des Betriebs überwacht und aufgezeichnet werden, um die kontinuierliche Einhaltung der Sicherheitsstandards zu gewährleisten.', 5),
('G-FS-004', 'de', 'Welche Verordnung regelt die Warmhaltetemperatur-Standards in Spanien?', '[{"text":"RD 109/2010","correct":false},{"text":"RD 3484/2000","correct":true},{"text":"EU 852/2004","correct":false},{"text":"RD 140/2003","correct":false}]', 'RD 3484/2000 legt Hygienestandards fest, einschließlich spezifischer Anforderungen für Warmhaltetemperaturen in Lebensmittelbetrieben.', 6),
('G-FS-004', 'de', 'Was sollten Sie tun, wenn die Warmhalteausrüstung während des Betriebs ausfällt?', '[{"text":"Weiter benutzen","correct":false},{"text":"Lebensmittelsicherheit bewerten und möglicherweise betroffene Artikel entsorgen","correct":true},{"text":"Temperatureinstellung senken","correct":false},{"text":"Warme und kalte Speisen mischen","correct":false}]', 'Ein Geräteausfall erfordert eine sofortige Bewertung der Lebensmittelsicherheit. Artikel, die zu lange bei unsicheren Temperaturen gehalten wurden, müssen entsorgt werden.', 7),
('G-FS-004', 'de', 'Warum ist 65°C die Mindesttemperatur für das Warmhalten?', '[{"text":"Es spart Energie","correct":false},{"text":"Es verhindert das meiste Bakterienwachstum","correct":true},{"text":"Es ist einfacher zu halten","correct":false},{"text":"Kunden bevorzugen diese Temperatur","correct":false}]', 'Bei 65°C oder höher können sich die meisten pathogenen Bakterien nicht vermehren, was dies zur kritischen Mindesttemperatur für sicheres Warmhalten macht.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-005', 'en', 'What does L+D stand for in Spanish food safety context?', '[{"text":"Legal Documents","correct":false},{"text":"Limpieza y Desinfección (Cleaning and Disinfection)","correct":true},{"text":"Location and Distribution","correct":false},{"text":"Labor and Development","correct":false}]', 'L+D refers to Limpieza y Desinfección, the mandatory cleaning and disinfection plan required by EU Regulation 852/2004.', 1),
('G-FS-005', 'en', 'What information must be specified in a cleaning plan?', '[{"text":"Only the cleaning schedule","correct":false},{"text":"Products, dilutions, contact times, and responsible staff","correct":true},{"text":"Only the cleaning products used","correct":false},{"text":"Just the frequency of cleaning","correct":false}]', 'A comprehensive cleaning plan must specify products, dilution rates, contact times, frequency, responsible staff, and documentation procedures.', 2),
('G-FS-005', 'en', 'Why should cleaning equipment be color-coded by zone?', '[{"text":"For aesthetic reasons","correct":false},{"text":"To prevent cross-contamination between areas","correct":true},{"text":"To identify ownership","correct":false},{"text":"It is not necessary","correct":false}]', 'Color coding prevents cross-contamination by ensuring equipment used in high-risk areas (kitchens) is not used in low-risk areas (dining rooms) and vice versa.', 3),
('G-FS-005', 'en', 'What type of cleaning products must be used in food areas?', '[{"text":"Any commercial cleaner","correct":false},{"text":"Food-safe cleaning products","correct":true},{"text":"Household cleaners","correct":false},{"text":"Industrial solvents","correct":false}]', 'Only food-safe cleaning products approved for use in food preparation areas must be used to prevent chemical contamination.', 4),
('G-FS-005', 'en', 'How often must cleaning activities be documented?', '[{"text":"Weekly","correct":false},{"text":"Daily","correct":true},{"text":"Monthly","correct":false},{"text":"Only when inspectors visit","correct":false}]', 'Daily cleaning records must be maintained showing what was cleaned, by whom, with what products, and at what time.', 5),
('G-FS-005', 'en', 'What EU regulation requires a documented cleaning and disinfection plan?', '[{"text":"EU Regulation 178/2002","correct":false},{"text":"EU Regulation 852/2004 Annex II","correct":true},{"text":"EU Regulation 1169/2011","correct":false},{"text":"EU Regulation 2073/2005","correct":false}]', 'EU Regulation 852/2004, specifically Annex II, mandates documented cleaning and disinfection procedures for all food establishments.', 6),
('G-FS-005', 'en', 'What is the purpose of specifying contact times in a cleaning plan?', '[{"text":"To schedule staff breaks","correct":false},{"text":"To ensure disinfectants work effectively","correct":true},{"text":"To calculate costs","correct":false},{"text":"For customer information","correct":false}]', 'Contact time is the minimum time a disinfectant must remain on a surface to effectively kill pathogens. This is critical for food safety.', 7),
('G-FS-005', 'en', 'What should a deep cleaning schedule include?', '[{"text":"Only visible surfaces","correct":false},{"text":"Equipment and hard-to-reach areas","correct":true},{"text":"Customer dining areas only","correct":false},{"text":"Just floors and walls","correct":false}]', 'Deep cleaning schedules must include periodic thorough cleaning of equipment, behind and under equipment, ventilation systems, and all hard-to-reach areas.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-005', 'es', '¿Qué significa L+D en el contexto de seguridad alimentaria español?', '[{"text":"Documentos Legales","correct":false},{"text":"Limpieza y Desinfección","correct":true},{"text":"Localización y Distribución","correct":false},{"text":"Laboral y Desarrollo","correct":false}]', 'L+D se refiere a Limpieza y Desinfección, el plan obligatorio requerido por el Reglamento UE 852/2004.', 1),
('G-FS-005', 'es', '¿Qué información debe especificarse en un plan de limpieza?', '[{"text":"Solo el calendario de limpieza","correct":false},{"text":"Productos, diluciones, tiempos de contacto y personal responsable","correct":true},{"text":"Solo los productos de limpieza utilizados","correct":false},{"text":"Solo la frecuencia de limpieza","correct":false}]', 'Un plan de limpieza integral debe especificar productos, tasas de dilución, tiempos de contacto, frecuencia, personal responsable y procedimientos de documentación.', 2),
('G-FS-005', 'es', '¿Por qué debe codificarse por colores el material de limpieza por zonas?', '[{"text":"Por razones estéticas","correct":false},{"text":"Para prevenir la contaminación cruzada entre áreas","correct":true},{"text":"Para identificar la propiedad","correct":false},{"text":"No es necesario","correct":false}]', 'La codificación por colores previene la contaminación cruzada asegurando que el material usado en áreas de alto riesgo (cocinas) no se use en áreas de bajo riesgo (comedores) y viceversa.', 3),
('G-FS-005', 'es', '¿Qué tipo de productos de limpieza deben utilizarse en áreas alimentarias?', '[{"text":"Cualquier limpiador comercial","correct":false},{"text":"Productos de limpieza aptos para uso alimentario","correct":true},{"text":"Limpiadores domésticos","correct":false},{"text":"Disolventes industriales","correct":false}]', 'Solo deben utilizarse productos de limpieza aptos para uso alimentario aprobados para áreas de preparación de alimentos para prevenir la contaminación química.', 4),
('G-FS-005', 'es', '¿Con qué frecuencia deben documentarse las actividades de limpieza?', '[{"text":"Semanalmente","correct":false},{"text":"Diariamente","correct":true},{"text":"Mensualmente","correct":false},{"text":"Solo cuando visitan los inspectores","correct":false}]', 'Deben mantenerse registros diarios de limpieza mostrando qué se limpió, por quién, con qué productos y a qué hora.', 5),
('G-FS-005', 'es', '¿Qué reglamento de la UE requiere un plan documentado de limpieza y desinfección?', '[{"text":"Reglamento UE 178/2002","correct":false},{"text":"Reglamento UE 852/2004 Anexo II","correct":true},{"text":"Reglamento UE 1169/2011","correct":false},{"text":"Reglamento UE 2073/2005","correct":false}]', 'El Reglamento UE 852/2004, específicamente el Anexo II, exige procedimientos documentados de limpieza y desinfección para todos los establecimientos alimentarios.', 6),
('G-FS-005', 'es', '¿Cuál es el propósito de especificar los tiempos de contacto en un plan de limpieza?', '[{"text":"Para programar descansos del personal","correct":false},{"text":"Para asegurar que los desinfectantes funcionen eficazmente","correct":true},{"text":"Para calcular costos","correct":false},{"text":"Para información del cliente","correct":false}]', 'El tiempo de contacto es el tiempo mínimo que un desinfectante debe permanecer en una superficie para matar eficazmente los patógenos. Esto es crítico para la seguridad alimentaria.', 7),
('G-FS-005', 'es', '¿Qué debe incluir un calendario de limpieza a fondo?', '[{"text":"Solo superficies visibles","correct":false},{"text":"Equipos y áreas de difícil acceso","correct":true},{"text":"Solo áreas de comedor de clientes","correct":false},{"text":"Solo suelos y paredes","correct":false}]', 'Los calendarios de limpieza a fondo deben incluir limpieza periódica exhaustiva de equipos, detrás y debajo de equipos, sistemas de ventilación y todas las áreas de difícil acceso.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-005', 'nl', 'Wat betekent L+D in de Spaanse voedselveiligheidscontext?', '[{"text":"Juridische Documenten","correct":false},{"text":"Limpieza y Desinfección (Reiniging en Desinfectie)","correct":true},{"text":"Locatie en Distributie","correct":false},{"text":"Arbeid en Ontwikkeling","correct":false}]', 'L+D verwijst naar Limpieza y Desinfección, het verplichte reinigings- en desinfectieplan dat wordt vereist door EU-verordening 852/2004.', 1),
('G-FS-005', 'nl', 'Welke informatie moet in een reinigingsplan worden gespecificeerd?', '[{"text":"Alleen het reinigingsschema","correct":false},{"text":"Producten, verdunningen, contacttijden en verantwoordelijk personeel","correct":true},{"text":"Alleen de gebruikte reinigingsproducten","correct":false},{"text":"Alleen de reinigingsfrequentie","correct":false}]', 'Een uitgebreid reinigingsplan moet producten, verdunningsratio\'s, contacttijden, frequentie, verantwoordelijk personeel en documentatieprocedures specificeren.', 2),
('G-FS-005', 'nl', 'Waarom moet reinigingsmateriaal per zone kleurgecodeerd zijn?', '[{"text":"Om esthetische redenen","correct":false},{"text":"Om kruisbesmetting tussen gebieden te voorkomen","correct":true},{"text":"Om eigendom te identificeren","correct":false},{"text":"Het is niet nodig","correct":false}]', 'Kleurcodering voorkomt kruisbesmetting door ervoor te zorgen dat materiaal gebruikt in hoog-risicogebieden (keukens) niet wordt gebruikt in laag-risicogebieden (eetzalen) en vice versa.', 3),
('G-FS-005', 'nl', 'Welk type reinigingsproducten moet in voedselgebieden worden gebruikt?', '[{"text":"Elk commercieel schoonmaakmiddel","correct":false},{"text":"Voedselveilige reinigingsproducten","correct":true},{"text":"Huishoudelijke schoonmaakmiddelen","correct":false},{"text":"Industriële oplosmiddelen","correct":false}]', 'Alleen voedselveilige reinigingsproducten goedgekeurd voor gebruik in voedselbereidingsgebieden mogen worden gebruikt om chemische besmetting te voorkomen.', 4),
('G-FS-005', 'nl', 'Hoe vaak moeten reinigingsactiviteiten worden gedocumenteerd?', '[{"text":"Wekelijks","correct":false},{"text":"Dagelijks","correct":true},{"text":"Maandelijks","correct":false},{"text":"Alleen wanneer inspecteurs op bezoek komen","correct":false}]', 'Dagelijkse reinigingsregistraties moeten worden bijgehouden met informatie over wat er is gereinigd, door wie, met welke producten en op welk tijdstip.', 5),
('G-FS-005', 'nl', 'Welke EU-verordening vereist een gedocumenteerd reinigings- en desinfectieplan?', '[{"text":"EU-verordening 178/2002","correct":false},{"text":"EU-verordening 852/2004 Bijlage II","correct":true},{"text":"EU-verordening 1169/2011","correct":false},{"text":"EU-verordening 2073/2005","correct":false}]', 'EU-verordening 852/2004, specifiek Bijlage II, verplicht gedocumenteerde reinigings- en desinfectieprocedures voor alle horecagelegenheden.', 6),
('G-FS-005', 'nl', 'Wat is het doel van het specificeren van contacttijden in een reinigingsplan?', '[{"text":"Om personeelspauzes in te plannen","correct":false},{"text":"Om ervoor te zorgen dat desinfectiemiddelen effectief werken","correct":true},{"text":"Om kosten te berekenen","correct":false},{"text":"Voor klantinformatie","correct":false}]', 'Contacttijd is de minimale tijd dat een desinfectiemiddel op een oppervlak moet blijven om pathogenen effectief te doden. Dit is cruciaal voor voedselveiligheid.', 7),
('G-FS-005', 'nl', 'Wat moet een grondige reinigingsschema bevatten?', '[{"text":"Alleen zichtbare oppervlakken","correct":false},{"text":"Apparatuur en moeilijk bereikbare gebieden","correct":true},{"text":"Alleen eetgebieden voor klanten","correct":false},{"text":"Alleen vloeren en muren","correct":false}]', 'Grondige reinigingsschema\'s moeten periodieke grondige reiniging bevatten van apparatuur, achter en onder apparatuur, ventilatiesystemen en alle moeilijk bereikbare gebieden.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-005', 'de', 'Was bedeutet L+D im spanischen Lebensmittelsicherheitskontext?', '[{"text":"Rechtliche Dokumente","correct":false},{"text":"Limpieza y Desinfección (Reinigung und Desinfektion)","correct":true},{"text":"Standort und Verteilung","correct":false},{"text":"Arbeit und Entwicklung","correct":false}]', 'L+D bezieht sich auf Limpieza y Desinfección, den obligatorischen Reinigungs- und Desinfektionsplan, der von der EU-Verordnung 852/2004 gefordert wird.', 1),
('G-FS-005', 'de', 'Welche Informationen müssen in einem Reinigungsplan angegeben werden?', '[{"text":"Nur der Reinigungsplan","correct":false},{"text":"Produkte, Verdünnungen, Kontaktzeiten und verantwortliches Personal","correct":true},{"text":"Nur die verwendeten Reinigungsprodukte","correct":false},{"text":"Nur die Reinigungshäufigkeit","correct":false}]', 'Ein umfassender Reinigungsplan muss Produkte, Verdünnungsraten, Kontaktzeiten, Häufigkeit, verantwortliches Personal und Dokumentationsverfahren spezifizieren.', 2),
('G-FS-005', 'de', 'Warum sollten Reinigungsgeräte nach Zonen farbcodiert sein?', '[{"text":"Aus ästhetischen Gründen","correct":false},{"text":"Um Kreuzkontamination zwischen Bereichen zu verhindern","correct":true},{"text":"Um Eigentum zu identifizieren","correct":false},{"text":"Es ist nicht notwendig","correct":false}]', 'Farbcodierung verhindert Kreuzkontamination, indem sichergestellt wird, dass Geräte, die in Hochrisikobereichen (Küchen) verwendet werden, nicht in Niedrigrisikobereichen (Esssälen) verwendet werden und umgekehrt.', 3),
('G-FS-005', 'de', 'Welche Art von Reinigungsprodukten muss in Lebensmittelbereichen verwendet werden?', '[{"text":"Jeder kommerzielle Reiniger","correct":false},{"text":"Lebensmittelsichere Reinigungsprodukte","correct":true},{"text":"Haushaltsreiniger","correct":false},{"text":"Industrielle Lösungsmittel","correct":false}]', 'Nur lebensmittelsichere Reinigungsprodukte, die für den Einsatz in Lebensmittelzubereitungsbereichen zugelassen sind, dürfen verwendet werden, um chemische Kontamination zu verhindern.', 4),
('G-FS-005', 'de', 'Wie oft müssen Reinigungsaktivitäten dokumentiert werden?', '[{"text":"Wöchentlich","correct":false},{"text":"Täglich","correct":true},{"text":"Monatlich","correct":false},{"text":"Nur wenn Inspektoren kommen","correct":false}]', 'Tägliche Reinigungsaufzeichnungen müssen geführt werden, die zeigen, was gereinigt wurde, von wem, mit welchen Produkten und zu welcher Zeit.', 5),
('G-FS-005', 'de', 'Welche EU-Verordnung verlangt einen dokumentierten Reinigungs- und Desinfektionsplan?', '[{"text":"EU-Verordnung 178/2002","correct":false},{"text":"EU-Verordnung 852/2004 Anhang II","correct":true},{"text":"EU-Verordnung 1169/2011","correct":false},{"text":"EU-Verordnung 2073/2005","correct":false}]', 'Die EU-Verordnung 852/2004, speziell Anhang II, schreibt dokumentierte Reinigungs- und Desinfektionsverfahren für alle Lebensmittelbetriebe vor.', 6),
('G-FS-005', 'de', 'Was ist der Zweck der Angabe von Kontaktzeiten in einem Reinigungsplan?', '[{"text":"Um Personalpausen zu planen","correct":false},{"text":"Um sicherzustellen, dass Desinfektionsmittel effektiv wirken","correct":true},{"text":"Um Kosten zu berechnen","correct":false},{"text":"Für Kundeninformation","correct":false}]', 'Kontaktzeit ist die Mindestzeit, die ein Desinfektionsmittel auf einer Oberfläche bleiben muss, um Krankheitserreger effektiv abzutöten. Dies ist entscheidend für die Lebensmittelsicherheit.', 7),
('G-FS-005', 'de', 'Was sollte ein Grundreinigungsplan beinhalten?', '[{"text":"Nur sichtbare Oberflächen","correct":false},{"text":"Geräte und schwer zugängliche Bereiche","correct":true},{"text":"Nur Kundenessbereich","correct":false},{"text":"Nur Böden und Wände","correct":false}]', 'Grundreinigungspläne müssen regelmäßige gründliche Reinigung von Geräten, hinter und unter Geräten, Lüftungssystemen und allen schwer zugänglichen Bereichen beinhalten.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-006', 'en', 'How many major allergens are regulated by EU law?', '[{"text":"10","correct":false},{"text":"12","correct":false},{"text":"14","correct":true},{"text":"16","correct":false}]', 'EU Regulation 1169/2011 requires clear communication of 14 major allergens in all food served: cereals with gluten, crustaceans, eggs, fish, peanuts, soybeans, milk, nuts, celery, mustard, sesame, sulphites, lupin, and molluscs.', 1),
('G-FS-006', 'en', 'What EU regulation governs allergen information to consumers?', '[{"text":"EU Regulation 852/2004","correct":false},{"text":"EU Regulation 1169/2011","correct":true},{"text":"EU Regulation 178/2002","correct":false},{"text":"EU Regulation 2073/2005","correct":false}]', 'EU Regulation 1169/2011 on food information to consumers mandates allergen labeling and communication requirements.', 2),
('G-FS-006', 'en', 'How must allergen information be made available to customers?', '[{"text":"Only verbally","correct":false},{"text":"In writing and staff must be trained","correct":true},{"text":"Only on request","correct":false},{"text":"It is optional","correct":false}]', 'Allergen information must be available in writing (menus, allergen matrix, etc.) and all service staff must be trained to communicate allergens accurately.', 3),
('G-FS-006', 'en', 'Which of the following is one of the 14 regulated allergens?', '[{"text":"Tomatoes","correct":false},{"text":"Celery","correct":true},{"text":"Garlic","correct":false},{"text":"Onions","correct":false}]', 'Celery is one of the 14 EU-regulated allergens. Even small amounts must be declared to customers.', 4),
('G-FS-006', 'en', 'What is the primary concern regarding cross-contamination with allergens?', '[{"text":"Cost increase","correct":false},{"text":"Potentially life-threatening allergic reactions","correct":true},{"text":"Food spoilage","correct":false},{"text":"Aesthetic appearance","correct":false}]', 'Cross-contamination with allergens can trigger severe allergic reactions including anaphylaxis, which can be life-threatening for sensitive individuals.', 5),
('G-FS-006', 'en', 'When must allergen information be updated?', '[{"text":"Annually","correct":false},{"text":"When recipes change","correct":true},{"text":"Only when requested by customers","correct":false},{"text":"Never","correct":false}]', 'Allergen information must be updated immediately whenever recipes change, new ingredients are added, or suppliers are changed, as this may affect allergen content.', 6),
('G-FS-006', 'en', 'What should be in place for allergic reaction emergencies?', '[{"text":"Nothing special is required","correct":false},{"text":"A protocol and emergency contact numbers","correct":true},{"text":"Only customer insurance information","correct":false},{"text":"Just allergen menus","correct":false}]', 'Establishments must have an emergency protocol for allergic reactions, including knowing how to call emergency services (112) and potentially having staff trained in first aid.', 7),
('G-FS-006', 'en', 'How should allergen-free preparation be handled in the kitchen?', '[{"text":"Same equipment as regular prep","correct":false},{"text":"Dedicated equipment or thorough cleaning between uses","correct":true},{"text":"It is not necessary to separate","correct":false},{"text":"Only use separate cutting boards","correct":false}]', 'Allergen-free preparation requires dedicated equipment or thorough cleaning and sanitization between uses to prevent cross-contamination.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-006', 'es', '¿Cuántos alérgenos principales están regulados por la legislación de la UE?', '[{"text":"10","correct":false},{"text":"12","correct":false},{"text":"14","correct":true},{"text":"16","correct":false}]', 'El Reglamento UE 1169/2011 requiere la comunicación clara de 14 alérgenos principales en todos los alimentos servidos: cereales con gluten, crustáceos, huevos, pescado, cacahuetes, soja, leche, frutos de cáscara, apio, mostaza, sésamo, sulfitos, altramuces y moluscos.', 1),
('G-FS-006', 'es', '¿Qué reglamento de la UE regula la información de alérgenos a los consumidores?', '[{"text":"Reglamento UE 852/2004","correct":false},{"text":"Reglamento UE 1169/2011","correct":true},{"text":"Reglamento UE 178/2002","correct":false},{"text":"Reglamento UE 2073/2005","correct":false}]', 'El Reglamento UE 1169/2011 sobre información alimentaria facilitada al consumidor establece los requisitos de etiquetado y comunicación de alérgenos.', 2),
('G-FS-006', 'es', '¿Cómo debe ponerse a disposición de los clientes la información sobre alérgenos?', '[{"text":"Solo verbalmente","correct":false},{"text":"Por escrito y el personal debe estar formado","correct":true},{"text":"Solo a petición","correct":false},{"text":"Es opcional","correct":false}]', 'La información sobre alérgenos debe estar disponible por escrito (menús, matriz de alérgenos, etc.) y todo el personal de servicio debe estar formado para comunicar los alérgenos con precisión.', 3),
('G-FS-006', 'es', '¿Cuál de los siguientes es uno de los 14 alérgenos regulados?', '[{"text":"Tomates","correct":false},{"text":"Apio","correct":true},{"text":"Ajo","correct":false},{"text":"Cebollas","correct":false}]', 'El apio es uno de los 14 alérgenos regulados por la UE. Incluso pequeñas cantidades deben declararse a los clientes.', 4),
('G-FS-006', 'es', '¿Cuál es la principal preocupación respecto a la contaminación cruzada con alérgenos?', '[{"text":"Aumento de costos","correct":false},{"text":"Reacciones alérgicas potencialmente mortales","correct":true},{"text":"Deterioro de alimentos","correct":false},{"text":"Apariencia estética","correct":false}]', 'La contaminación cruzada con alérgenos puede desencadenar reacciones alérgicas graves incluyendo anafilaxia, que puede ser mortal para personas sensibles.', 5),
('G-FS-006', 'es', '¿Cuándo debe actualizarse la información sobre alérgenos?', '[{"text":"Anualmente","correct":false},{"text":"Cuando cambian las recetas","correct":true},{"text":"Solo cuando los clientes lo solicitan","correct":false},{"text":"Nunca","correct":false}]', 'La información sobre alérgenos debe actualizarse inmediatamente cuando cambian las recetas, se añaden nuevos ingredientes o se cambian proveedores, ya que esto puede afectar el contenido de alérgenos.', 6),
('G-FS-006', 'es', '¿Qué debe existir para emergencias por reacciones alérgicas?', '[{"text":"No se requiere nada especial","correct":false},{"text":"Un protocolo y números de contacto de emergencia","correct":true},{"text":"Solo información del seguro del cliente","correct":false},{"text":"Solo menús de alérgenos","correct":false}]', 'Los establecimientos deben tener un protocolo de emergencia para reacciones alérgicas, incluyendo saber cómo llamar a servicios de emergencia (112) y potencialmente tener personal formado en primeros auxilios.', 7),
('G-FS-006', 'es', '¿Cómo debe manejarse la preparación libre de alérgenos en la cocina?', '[{"text":"Mismo equipo que la preparación regular","correct":false},{"text":"Equipo dedicado o limpieza exhaustiva entre usos","correct":true},{"text":"No es necesario separar","correct":false},{"text":"Solo usar tablas de cortar separadas","correct":false}]', 'La preparación libre de alérgenos requiere equipo dedicado o limpieza y desinfección exhaustiva entre usos para prevenir la contaminación cruzada.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-006', 'nl', 'Hoeveel belangrijke allergenen worden gereguleerd door EU-wetgeving?', '[{"text":"10","correct":false},{"text":"12","correct":false},{"text":"14","correct":true},{"text":"16","correct":false}]', 'EU-verordening 1169/2011 vereist duidelijke communicatie van 14 belangrijke allergenen in al het geserveerde voedsel: granen met gluten, schaaldieren, eieren, vis, pinda\'s, soja, melk, noten, selderij, mosterd, sesam, sulfieten, lupine en weekdieren.', 1),
('G-FS-006', 'nl', 'Welke EU-verordening regelt allergeneninformatie aan consumenten?', '[{"text":"EU-verordening 852/2004","correct":false},{"text":"EU-verordening 1169/2011","correct":true},{"text":"EU-verordening 178/2002","correct":false},{"text":"EU-verordening 2073/2005","correct":false}]', 'EU-verordening 1169/2011 inzake voedselinformatie aan consumenten verplicht etikettering en communicatie-eisen voor allergenen.', 2),
('G-FS-006', 'nl', 'Hoe moet allergeneninformatie beschikbaar worden gesteld aan klanten?', '[{"text":"Alleen mondeling","correct":false},{"text":"Schriftelijk en personeel moet zijn getraind","correct":true},{"text":"Alleen op verzoek","correct":false},{"text":"Het is optioneel","correct":false}]', 'Allergeneninformatie moet schriftelijk beschikbaar zijn (menu\'s, allergenenmatrix, etc.) en al het servicepersoneel moet zijn getraind om allergenen nauwkeurig te communiceren.', 3),
('G-FS-006', 'nl', 'Welke van de volgende is een van de 14 gereguleerde allergenen?', '[{"text":"Tomaten","correct":false},{"text":"Selderij","correct":true},{"text":"Knoflook","correct":false},{"text":"Uien","correct":false}]', 'Selderij is een van de 14 door de EU gereguleerde allergenen. Zelfs kleine hoeveelheden moeten aan klanten worden gemeld.', 4),
('G-FS-006', 'nl', 'Wat is de voornaamste zorg met betrekking tot kruisbesmetting met allergenen?', '[{"text":"Kostenstijging","correct":false},{"text":"Potentieel levensbedreigende allergische reacties","correct":true},{"text":"Voedselbederf","correct":false},{"text":"Esthetisch uiterlijk","correct":false}]', 'Kruisbesmetting met allergenen kan ernstige allergische reacties veroorzaken, waaronder anafylaxie, die levensbedreigende kan zijn voor gevoelige personen.', 5),
('G-FS-006', 'nl', 'Wanneer moet allergeneninformatie worden bijgewerkt?', '[{"text":"Jaarlijks","correct":false},{"text":"Wanneer recepten veranderen","correct":true},{"text":"Alleen wanneer klanten erom vragen","correct":false},{"text":"Nooit","correct":false}]', 'Allergeneninformatie moet onmiddellijk worden bijgewerkt wanneer recepten veranderen, nieuwe ingrediënten worden toegevoegd of leveranciers worden gewijzigd, omdat dit de allergeneninhoud kan beïnvloeden.', 6),
('G-FS-006', 'nl', 'Wat moet er zijn voor noodgevallen bij allergische reacties?', '[{"text":"Niets speciaals is vereist","correct":false},{"text":"Een protocol en noodcontactnummers","correct":true},{"text":"Alleen verzekeringsinformatie van de klant","correct":false},{"text":"Alleen allergenenmenu\'s","correct":false}]', 'Vestigingen moeten een noodprotocol hebben voor allergische reacties, inclusief weten hoe nooddiensten (112) te bellen en mogelijk personeel hebben dat is getraind in eerste hulp.', 7),
('G-FS-006', 'nl', 'Hoe moet allergenenvrije bereiding in de keuken worden behandeld?', '[{"text":"Dezelfde apparatuur als reguliere bereiding","correct":false},{"text":"Toegewijde apparatuur of grondige reiniging tussen gebruik","correct":true},{"text":"Het is niet nodig om te scheiden","correct":false},{"text":"Alleen aparte snijplanken gebruiken","correct":false}]', 'Allergenenvrije bereiding vereist toegewijde apparatuur of grondige reiniging en desinfectie tussen gebruik om kruisbesmetting te voorkomen.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-006', 'de', 'Wie viele Hauptallergene werden durch EU-Recht reguliert?', '[{"text":"10","correct":false},{"text":"12","correct":false},{"text":"14","correct":true},{"text":"16","correct":false}]', 'Die EU-Verordnung 1169/2011 verlangt eine klare Kommunikation von 14 Hauptallergenen in allen servierten Speisen: glutenhaltige Getreide, Krebstiere, Eier, Fisch, Erdnüsse, Sojabohnen, Milch, Schalenfrüchte, Sellerie, Senf, Sesam, Sulfite, Lupinen und Weichtiere.', 1),
('G-FS-006', 'de', 'Welche EU-Verordnung regelt Allergeninformationen an Verbraucher?', '[{"text":"EU-Verordnung 852/2004","correct":false},{"text":"EU-Verordnung 1169/2011","correct":true},{"text":"EU-Verordnung 178/2002","correct":false},{"text":"EU-Verordnung 2073/2005","correct":false}]', 'Die EU-Verordnung 1169/2011 über Lebensmittelinformation für Verbraucher schreibt Anforderungen zur Allergenkennzeichnung und -kommunikation vor.', 2),
('G-FS-006', 'de', 'Wie müssen Allergeninformationen Kunden zur Verfügung gestellt werden?', '[{"text":"Nur mündlich","correct":false},{"text":"Schriftlich und Personal muss geschult sein","correct":true},{"text":"Nur auf Anfrage","correct":false},{"text":"Es ist optional","correct":false}]', 'Allergeninformationen müssen schriftlich verfügbar sein (Menüs, Allergenmatrix usw.) und alle Servicekräfte müssen geschult sein, um Allergene genau zu kommunizieren.', 3),
('G-FS-006', 'de', 'Welches der folgenden ist eines der 14 regulierten Allergene?', '[{"text":"Tomaten","correct":false},{"text":"Sellerie","correct":true},{"text":"Knoblauch","correct":false},{"text":"Zwiebeln","correct":false}]', 'Sellerie ist eines der 14 von der EU regulierten Allergene. Selbst kleine Mengen müssen den Kunden mitgeteilt werden.', 4),
('G-FS-006', 'de', 'Was ist das Hauptanliegen bezüglich Kreuzkontamination mit Allergenen?', '[{"text":"Kostensteigerung","correct":false},{"text":"Potenziell lebensbedrohliche allergische Reaktionen","correct":true},{"text":"Lebensmittelverderb","correct":false},{"text":"Ästhetisches Erscheinungsbild","correct":false}]', 'Kreuzkontamination mit Allergenen kann schwere allergische Reaktionen einschließlich Anaphylaxie auslösen, die für empfindliche Personen lebensbedrohlich sein kann.', 5),
('G-FS-006', 'de', 'Wann müssen Allergeninformationen aktualisiert werden?', '[{"text":"Jährlich","correct":false},{"text":"Wenn sich Rezepte ändern","correct":true},{"text":"Nur wenn Kunden danach fragen","correct":false},{"text":"Nie","correct":false}]', 'Allergeninformationen müssen sofort aktualisiert werden, wenn sich Rezepte ändern, neue Zutaten hinzugefügt werden oder Lieferanten gewechselt werden, da dies den Allergengehalt beeinflussen kann.', 6),
('G-FS-006', 'de', 'Was sollte für Notfälle bei allergischen Reaktionen vorhanden sein?', '[{"text":"Nichts Besonderes ist erforderlich","correct":false},{"text":"Ein Protokoll und Notfallkontaktnummern","correct":true},{"text":"Nur Kundenversicherungsinformationen","correct":false},{"text":"Nur Allergenmenüs","correct":false}]', 'Betriebe müssen ein Notfallprotokoll für allergische Reaktionen haben, einschließlich zu wissen, wie man Rettungsdienste (112) ruft und möglicherweise Personal zu haben, das in Erster Hilfe geschult ist.', 7),
('G-FS-006', 'de', 'Wie sollte allergenfreie Zubereitung in der Küche gehandhabt werden?', '[{"text":"Gleiche Ausrüstung wie bei normaler Zubereitung","correct":false},{"text":"Dedizierte Ausrüstung oder gründliche Reinigung zwischen Verwendungen","correct":true},{"text":"Es ist nicht notwendig zu trennen","correct":false},{"text":"Nur separate Schneidebretter verwenden","correct":false}]', 'Allergenfreie Zubereitung erfordert dedizierte Ausrüstung oder gründliche Reinigung und Desinfektion zwischen Verwendungen, um Kreuzkontamination zu verhindern.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-007', 'en', 'What Spanish law requires food waste prevention plans in hospitality?', '[{"text":"Ley 31/1995","correct":false},{"text":"Ley 1/2025","correct":true},{"text":"RD 3484/2000","correct":false},{"text":"RD 109/2010","correct":false}]', 'Ley 1/2025 de prevención de las pérdidas y el desperdicio alimentario (food waste law) mandates prevention plans for hospitality establishments.', 1),
('G-FS-007', 'en', 'What must establishments offer customers for unfinished meals?', '[{"text":"Nothing required","correct":false},{"text":"Take-away containers","correct":true},{"text":"Discounts on next visit","correct":false},{"text":"Only for large portions","correct":false}]', 'The food waste law requires establishments to offer take-away options for customers to take home unfinished meals.', 2),
('G-FS-007', 'en', 'What is the priority order for surplus food according to the law?', '[{"text":"Disposal first","correct":false},{"text":"Donation before disposal","correct":true},{"text":"Always freeze it","correct":false},{"text":"Give to staff only","correct":false}]', 'Establishments must prioritize donation of surplus food to authorized organizations (food banks) before disposal.', 3),
('G-FS-007', 'en', 'What records must be maintained regarding food waste?', '[{"text":"No records required","correct":false},{"text":"Quantities of food waste","correct":true},{"text":"Only customer complaints","correct":false},{"text":"Just disposal costs","correct":false}]', 'Establishments must maintain records of food waste quantities to monitor and demonstrate compliance with waste reduction goals.', 4),
('G-FS-007', 'en', 'Which practice helps reduce food waste through inventory management?', '[{"text":"Buying in bulk always","correct":false},{"text":"Portion control and monitoring overproduction","correct":true},{"text":"Keeping old stock indefinitely","correct":false},{"text":"Ignoring expiry dates","correct":false}]', 'Proper portion control and monitoring to reduce overproduction are key strategies to minimize food waste in hospitality.', 5),
('G-FS-007', 'en', 'What type of organization can receive donated surplus food?', '[{"text":"Any individual","correct":false},{"text":"Authorized food banks or donation platforms","correct":true},{"text":"Only private charities","correct":false},{"text":"Donation is not allowed","correct":false}]', 'Surplus food must be donated to authorized organizations such as registered food banks or approved donation platforms.', 6),
('G-FS-007', 'en', 'Why is staff training important for food waste prevention?', '[{"text":"It is not necessary","correct":false},{"text":"To understand portion sizes and waste reduction techniques","correct":true},{"text":"Only for managers","correct":false},{"text":"To increase sales","correct":false}]', 'Training all staff on correct portion sizes and waste reduction techniques is essential to implementing an effective food waste prevention plan.', 7),
('G-FS-007', 'en', 'What is the environmental benefit of reducing food waste?', '[{"text":"None","correct":false},{"text":"Reduces greenhouse gas emissions and resource consumption","correct":true},{"text":"Only saves money","correct":false},{"text":"Improves taste","correct":false}]', 'Reducing food waste decreases methane emissions from landfills, conserves water and energy used in food production, and reduces overall environmental impact.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-007', 'es', '¿Qué ley española requiere planes de prevención de desperdicio alimentario en hostelería?', '[{"text":"Ley 31/1995","correct":false},{"text":"Ley 1/2025","correct":true},{"text":"RD 3484/2000","correct":false},{"text":"RD 109/2010","correct":false}]', 'La Ley 1/2025 de prevención de las pérdidas y el desperdicio alimentario exige planes de prevención para establecimientos de hostelería.', 1),
('G-FS-007', 'es', '¿Qué deben ofrecer los establecimientos a los clientes para las comidas no consumidas?', '[{"text":"Nada requerido","correct":false},{"text":"Recipientes para llevar","correct":true},{"text":"Descuentos en la próxima visita","correct":false},{"text":"Solo para porciones grandes","correct":false}]', 'La ley de desperdicio alimentario requiere que los establecimientos ofrezcan opciones para llevar a casa las comidas no consumidas.', 2),
('G-FS-007', 'es', '¿Cuál es el orden de prioridad para los excedentes alimentarios según la ley?', '[{"text":"Eliminación primero","correct":false},{"text":"Donación antes de la eliminación","correct":true},{"text":"Siempre congelar","correct":false},{"text":"Dar solo al personal","correct":false}]', 'Los establecimientos deben priorizar la donación de excedentes alimentarios a organizaciones autorizadas (bancos de alimentos) antes de su eliminación.', 3),
('G-FS-007', 'es', '¿Qué registros deben mantenerse respecto al desperdicio alimentario?', '[{"text":"No se requieren registros","correct":false},{"text":"Cantidades de desperdicio alimentario","correct":true},{"text":"Solo quejas de clientes","correct":false},{"text":"Solo costos de eliminación","correct":false}]', 'Los establecimientos deben mantener registros de las cantidades de desperdicio alimentario para monitorear y demostrar el cumplimiento de los objetivos de reducción de residuos.', 4),
('G-FS-007', 'es', '¿Qué práctica ayuda a reducir el desperdicio alimentario a través de la gestión del inventario?', '[{"text":"Comprar siempre al por mayor","correct":false},{"text":"Control de porciones y monitoreo de la sobreproducción","correct":true},{"text":"Mantener stock antiguo indefinidamente","correct":false},{"text":"Ignorar fechas de caducidad","correct":false}]', 'El control adecuado de porciones y el monitoreo para reducir la sobreproducción son estrategias clave para minimizar el desperdicio alimentario en hostelería.', 5),
('G-FS-007', 'es', '¿Qué tipo de organización puede recibir alimentos excedentes donados?', '[{"text":"Cualquier individuo","correct":false},{"text":"Bancos de alimentos autorizados o plataformas de donación","correct":true},{"text":"Solo organizaciones benéficas privadas","correct":false},{"text":"No se permite la donación","correct":false}]', 'Los alimentos excedentes deben donarse a organizaciones autorizadas como bancos de alimentos registrados o plataformas de donación aprobadas.', 6),
('G-FS-007', 'es', '¿Por qué es importante la formación del personal para la prevención del desperdicio alimentario?', '[{"text":"No es necesaria","correct":false},{"text":"Para comprender los tamaños de porción y técnicas de reducción de residuos","correct":true},{"text":"Solo para gerentes","correct":false},{"text":"Para aumentar las ventas","correct":false}]', 'Formar a todo el personal en tamaños de porción correctos y técnicas de reducción de residuos es esencial para implementar un plan efectivo de prevención del desperdicio alimentario.', 7),
('G-FS-007', 'es', '¿Cuál es el beneficio ambiental de reducir el desperdicio alimentario?', '[{"text":"Ninguno","correct":false},{"text":"Reduce las emisiones de gases de efecto invernadero y el consumo de recursos","correct":true},{"text":"Solo ahorra dinero","correct":false},{"text":"Mejora el sabor","correct":false}]', 'Reducir el desperdicio alimentario disminuye las emisiones de metano de los vertederos, conserva el agua y la energía utilizadas en la producción de alimentos y reduce el impacto ambiental general.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-007', 'nl', 'Welke Spaanse wet vereist voedselverspillingspreventieplannen in de horeca?', '[{"text":"Ley 31/1995","correct":false},{"text":"Ley 1/2025","correct":true},{"text":"RD 3484/2000","correct":false},{"text":"RD 109/2010","correct":false}]', 'Ley 1/2025 de prevención de las pérdidas y el desperdicio alimentario (wet op voedselverspilling) verplicht preventieplannen voor horecagelegenheden.', 1),
('G-FS-007', 'nl', 'Wat moeten vestigingen klanten aanbieden voor niet-opgegeten maaltijden?', '[{"text":"Niets vereist","correct":false},{"text":"Afhaalbakjes","correct":true},{"text":"Kortingen bij het volgende bezoek","correct":false},{"text":"Alleen voor grote porties","correct":false}]', 'De wet op voedselverspilling vereist dat vestigingen afhaalopties aanbieden zodat klanten niet-opgegeten maaltijden mee naar huis kunnen nemen.', 2),
('G-FS-007', 'nl', 'Wat is de prioriteitsvolgorde voor overtollig voedsel volgens de wet?', '[{"text":"Afvoer eerst","correct":false},{"text":"Donatie voor afvoer","correct":true},{"text":"Altijd invriezen","correct":false},{"text":"Alleen aan personeel geven","correct":false}]', 'Vestigingen moeten prioriteit geven aan donatie van overtollig voedsel aan geautoriseerde organisaties (voedselbanken) boven afvoer.', 3),
('G-FS-007', 'nl', 'Welke gegevens moeten worden bijgehouden met betrekking tot voedselverspilling?', '[{"text":"Geen gegevens vereist","correct":false},{"text":"Hoeveelheden voedselverspilling","correct":true},{"text":"Alleen klachten van klanten","correct":false},{"text":"Alleen afvoerkosten","correct":false}]', 'Vestigingen moeten gegevens bijhouden over hoeveelheden voedselverspilling om naleving van doelen voor afvalvermindering te monitoren en aan te tonen.', 4),
('G-FS-007', 'nl', 'Welke praktijk helpt voedselverspilling te verminderen door voorraadbeheer?', '[{"text":"Altijd in bulk kopen","correct":false},{"text":"Portiecontrole en monitoring van overproductie","correct":true},{"text":"Oude voorraad voor onbepaalde tijd bewaren","correct":false},{"text":"Vervaldata negeren","correct":false}]', 'Goede portiecontrole en monitoring om overproductie te verminderen zijn belangrijke strategieën om voedselverspilling in de horeca te minimaliseren.', 5),
('G-FS-007', 'nl', 'Welk type organisatie kan gedoneerd overtollig voedsel ontvangen?', '[{"text":"Elk individu","correct":false},{"text":"Geautoriseerde voedselbanken of donatieplatforms","correct":true},{"text":"Alleen particuliere liefdadigheidsinstellingen","correct":false},{"text":"Donatie is niet toegestaan","correct":false}]', 'Overtollig voedsel moet worden gedoneerd aan geautoriseerde organisaties zoals geregistreerde voedselbanken of goedgekeurde donatieplatforms.', 6),
('G-FS-007', 'nl', 'Waarom is personeelstraining belangrijk voor het voorkomen van voedselverspilling?', '[{"text":"Het is niet nodig","correct":false},{"text":"Om portiegroottes en afvalverminderingstechnieken te begrijpen","correct":true},{"text":"Alleen voor managers","correct":false},{"text":"Om de verkoop te verhogen","correct":false}]', 'Het trainen van al het personeel in juiste portiegroottes en afvalverminderingstechnieken is essentieel voor het implementeren van een effectief plan voor het voorkomen van voedselverspilling.', 7),
('G-FS-007', 'nl', 'Wat is het milieuvoordeel van het verminderen van voedselverspilling?', '[{"text":"Geen","correct":false},{"text":"Vermindert broeikasgasemissies en grondstoffenverbruik","correct":true},{"text":"Bespaart alleen geld","correct":false},{"text":"Verbetert de smaak","correct":false}]', 'Het verminderen van voedselverspilling vermindert methaanuitstoot van stortplaatsen, bespaart water en energie die worden gebruikt in voedselproductie en vermindert de algehele milieubelasting.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-007', 'de', 'Welches spanische Gesetz verlangt Pläne zur Vermeidung von Lebensmittelverschwendung in der Gastronomie?', '[{"text":"Ley 31/1995","correct":false},{"text":"Ley 1/2025","correct":true},{"text":"RD 3484/2000","correct":false},{"text":"RD 109/2010","correct":false}]', 'Ley 1/2025 de prevención de las pérdidas y el desperdicio alimentario (Lebensmittelabfallgesetz) schreibt Präventionspläne für Gastronomiebetriebe vor.', 1),
('G-FS-007', 'de', 'Was müssen Betriebe Kunden für nicht aufgegessene Mahlzeiten anbieten?', '[{"text":"Nichts erforderlich","correct":false},{"text":"Mitnahmebehälter","correct":true},{"text":"Rabatte beim nächsten Besuch","correct":false},{"text":"Nur für große Portionen","correct":false}]', 'Das Lebensmittelabfallgesetz verlangt, dass Betriebe Mitnahmeoptionen anbieten, damit Kunden nicht aufgegessene Mahlzeiten mit nach Hause nehmen können.', 2),
('G-FS-007', 'de', 'Was ist die Prioritätsreihenfolge für überschüssige Lebensmittel laut Gesetz?', '[{"text":"Entsorgung zuerst","correct":false},{"text":"Spende vor Entsorgung","correct":true},{"text":"Immer einfrieren","correct":false},{"text":"Nur an Personal geben","correct":false}]', 'Betriebe müssen der Spende von überschüssigen Lebensmitteln an autorisierte Organisationen (Tafeln) vor der Entsorgung Priorität einräumen.', 3),
('G-FS-007', 'de', 'Welche Aufzeichnungen müssen bezüglich Lebensmittelabfall geführt werden?', '[{"text":"Keine Aufzeichnungen erforderlich","correct":false},{"text":"Mengen des Lebensmittelabfalls","correct":true},{"text":"Nur Kundenbeschwerden","correct":false},{"text":"Nur Entsorgungskosten","correct":false}]', 'Betriebe müssen Aufzeichnungen über Lebensmittelabfallmengen führen, um die Einhaltung von Abfallreduktionszielen zu überwachen und nachzuweisen.', 4),
('G-FS-007', 'de', 'Welche Praxis hilft, Lebensmittelverschwendung durch Bestandsverwaltung zu reduzieren?', '[{"text":"Immer in großen Mengen kaufen","correct":false},{"text":"Portionskontrolle und Überwachung der Überproduktion","correct":true},{"text":"Alte Bestände unbegrenzt aufbewahren","correct":false},{"text":"Verfallsdaten ignorieren","correct":false}]', 'Richtige Portionskontrolle und Überwachung zur Reduzierung der Überproduktion sind Schlüsselstrategien zur Minimierung von Lebensmittelverschwendung in der Gastronomie.', 5),
('G-FS-007', 'de', 'Welche Art von Organisation kann gespendete überschüssige Lebensmittel erhalten?', '[{"text":"Jede Einzelperson","correct":false},{"text":"Autorisierte Tafeln oder Spendenplattformen","correct":true},{"text":"Nur private Wohltätigkeitsorganisationen","correct":false},{"text":"Spende ist nicht erlaubt","correct":false}]', 'Überschüssige Lebensmittel müssen an autorisierte Organisationen wie registrierte Tafeln oder genehmigte Spendenplattformen gespendet werden.', 6),
('G-FS-007', 'de', 'Warum ist Personalschulung wichtig für die Vermeidung von Lebensmittelverschwendung?', '[{"text":"Es ist nicht notwendig","correct":false},{"text":"Um Portionsgrößen und Abfallreduktionstechniken zu verstehen","correct":true},{"text":"Nur für Manager","correct":false},{"text":"Um den Umsatz zu steigern","correct":false}]', 'Die Schulung aller Mitarbeiter in richtigen Portionsgrößen und Abfallreduktionstechniken ist wesentlich für die Umsetzung eines effektiven Plans zur Vermeidung von Lebensmittelverschwendung.', 7),
('G-FS-007', 'de', 'Was ist der Umweltvorteil der Reduzierung von Lebensmittelverschwendung?', '[{"text":"Keiner","correct":false},{"text":"Reduziert Treibhausgasemissionen und Ressourcenverbrauch","correct":true},{"text":"Spart nur Geld","correct":false},{"text":"Verbessert den Geschmack","correct":false}]', 'Die Reduzierung von Lebensmittelverschwendung verringert Methanemissionen von Deponien, spart Wasser und Energie, die in der Lebensmittelproduktion verwendet werden, und reduziert die gesamte Umweltbelastung.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-008', 'en', 'What EU regulation mandates food traceability?', '[{"text":"EU Regulation 852/2004","correct":false},{"text":"EU Regulation 178/2002","correct":true},{"text":"EU Regulation 1169/2011","correct":false},{"text":"EU Regulation 2073/2005","correct":false}]', 'EU Regulation 178/2002 establishes the general principles and requirements of traceability in food law.', 1),
('G-FS-008', 'en', 'How long must traceability records be kept?', '[{"text":"1 year","correct":false},{"text":"3 years","correct":false},{"text":"5 years minimum","correct":true},{"text":"10 years","correct":false}]', 'Traceability records including supplier details, lot numbers, and delivery dates must be maintained for a minimum of 5 years.', 2),
('G-FS-008', 'en', 'What is the "one step back, one step forward" principle?', '[{"text":"Cooking method","correct":false},{"text":"Ability to trace suppliers and customers for each product","correct":true},{"text":"Storage rotation","correct":false},{"text":"Temperature control","correct":false}]', 'The traceability system must allow identification of one step back (your suppliers) and one step forward (your customers) for all products.', 3),
('G-FS-008', 'en', 'What information should be recorded from delivery notes?', '[{"text":"Only the price","correct":false},{"text":"Lot numbers, delivery dates, and supplier details","correct":true},{"text":"Just the product name","correct":false},{"text":"Only the quantity","correct":false}]', 'Delivery notes must include lot numbers, delivery dates, supplier details, and product information for complete traceability.', 4),
('G-FS-008', 'en', 'Why is traceability important for food safety?', '[{"text":"It is not important","correct":false},{"text":"Enables rapid product recall if contamination occurs","correct":true},{"text":"Only for accounting purposes","correct":false},{"text":"Just for marketing","correct":false}]', 'Traceability allows rapid identification and recall of affected products in case of contamination or safety issues, protecting public health.', 5),
('G-FS-008', 'en', 'What must be in place for rapid product recall?', '[{"text":"Nothing special","correct":false},{"text":"A documented recall procedure","correct":true},{"text":"Only supplier contact information","correct":false},{"text":"Customer email list","correct":false}]', 'A documented rapid recall procedure must be established, including roles, responsibilities, communication protocols, and verification steps.', 6),
('G-FS-008', 'en', 'What details about suppliers must be recorded?', '[{"text":"Only their name","correct":false},{"text":"Company name, address, health registration number","correct":true},{"text":"Just phone number","correct":false},{"text":"Only email address","correct":false}]', 'Complete supplier information including company name, address, contact details, and health registration number must be recorded for traceability.', 7),
('G-FS-008', 'en', 'How does traceability support HACCP?', '[{"text":"It replaces HACCP","correct":false},{"text":"Allows verification of supplier quality and rapid response to hazards","correct":true},{"text":"It is unrelated to HACCP","correct":false},{"text":"Only for temperature control","correct":false}]', 'Traceability supports HACCP by enabling verification of supplier compliance and allowing rapid identification and removal of hazardous products.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-008', 'es', '¿Qué reglamento de la UE exige la trazabilidad alimentaria?', '[{"text":"Reglamento UE 852/2004","correct":false},{"text":"Reglamento UE 178/2002","correct":true},{"text":"Reglamento UE 1169/2011","correct":false},{"text":"Reglamento UE 2073/2005","correct":false}]', 'El Reglamento UE 178/2002 establece los principios y requisitos generales de trazabilidad en la legislación alimentaria.', 1),
('G-FS-008', 'es', '¿Durante cuánto tiempo deben conservarse los registros de trazabilidad?', '[{"text":"1 año","correct":false},{"text":"3 años","correct":false},{"text":"Mínimo 5 años","correct":true},{"text":"10 años","correct":false}]', 'Los registros de trazabilidad incluyendo datos de proveedores, números de lote y fechas de entrega deben mantenerse durante un mínimo de 5 años.', 2),
('G-FS-008', 'es', '¿Qué es el principio de "un paso atrás, un paso adelante"?', '[{"text":"Método de cocción","correct":false},{"text":"Capacidad de rastrear proveedores y clientes para cada producto","correct":true},{"text":"Rotación de almacenamiento","correct":false},{"text":"Control de temperatura","correct":false}]', 'El sistema de trazabilidad debe permitir la identificación de un paso atrás (sus proveedores) y un paso adelante (sus clientes) para todos los productos.', 3),
('G-FS-008', 'es', '¿Qué información debe registrarse de los albaranes de entrega?', '[{"text":"Solo el precio","correct":false},{"text":"Números de lote, fechas de entrega y datos del proveedor","correct":true},{"text":"Solo el nombre del producto","correct":false},{"text":"Solo la cantidad","correct":false}]', 'Los albaranes deben incluir números de lote, fechas de entrega, datos del proveedor e información del producto para una trazabilidad completa.', 4),
('G-FS-008', 'es', '¿Por qué es importante la trazabilidad para la seguridad alimentaria?', '[{"text":"No es importante","correct":false},{"text":"Permite la retirada rápida de productos si ocurre contaminación","correct":true},{"text":"Solo para fines contables","correct":false},{"text":"Solo para marketing","correct":false}]', 'La trazabilidad permite la identificación rápida y la retirada de productos afectados en caso de contaminación o problemas de seguridad, protegiendo la salud pública.', 5),
('G-FS-008', 'es', '¿Qué debe existir para la retirada rápida de productos?', '[{"text":"Nada especial","correct":false},{"text":"Un procedimiento de retirada documentado","correct":true},{"text":"Solo información de contacto del proveedor","correct":false},{"text":"Lista de correo electrónico de clientes","correct":false}]', 'Debe establecerse un procedimiento documentado de retirada rápida, incluyendo roles, responsabilidades, protocolos de comunicación y pasos de verificación.', 6),
('G-FS-008', 'es', '¿Qué detalles sobre los proveedores deben registrarse?', '[{"text":"Solo su nombre","correct":false},{"text":"Nombre de la empresa, dirección, número de registro sanitario","correct":true},{"text":"Solo número de teléfono","correct":false},{"text":"Solo dirección de correo electrónico","correct":false}]', 'Debe registrarse información completa del proveedor incluyendo nombre de la empresa, dirección, datos de contacto y número de registro sanitario para la trazabilidad.', 7),
('G-FS-008', 'es', '¿Cómo apoya la trazabilidad al APPCC?', '[{"text":"Reemplaza al APPCC","correct":false},{"text":"Permite verificar la calidad del proveedor y responder rápidamente a peligros","correct":true},{"text":"No está relacionada con APPCC","correct":false},{"text":"Solo para control de temperatura","correct":false}]', 'La trazabilidad apoya al APPCC permitiendo verificar el cumplimiento del proveedor y permitiendo la identificación y eliminación rápida de productos peligrosos.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-008', 'nl', 'Welke EU-verordening verplicht voedseltracering?', '[{"text":"EU-verordening 852/2004","correct":false},{"text":"EU-verordening 178/2002","correct":true},{"text":"EU-verordening 1169/2011","correct":false},{"text":"EU-verordening 2073/2005","correct":false}]', 'EU-verordening 178/2002 stelt de algemene principes en vereisten van traceerbaarheid in de voedselwetgeving vast.', 1),
('G-FS-008', 'nl', 'Hoe lang moeten traceerregistraties worden bewaard?', '[{"text":"1 jaar","correct":false},{"text":"3 jaar","correct":false},{"text":"Minimaal 5 jaar","correct":true},{"text":"10 jaar","correct":false}]', 'Traceerregistraties inclusief leveranciersgegevens, lotnummers en leverdatums moeten minimaal 5 jaar worden bewaard.', 2),
('G-FS-008', 'nl', 'Wat is het "één stap terug, één stap vooruit" principe?', '[{"text":"Kookmethode","correct":false},{"text":"Vermogen om leveranciers en klanten voor elk product te traceren","correct":true},{"text":"Opslagrotatie","correct":false},{"text":"Temperatuurcontrole","correct":false}]', 'Het traceerbaarheidssysteem moet identificatie van één stap terug (uw leveranciers) en één stap vooruit (uw klanten) mogelijk maken voor alle producten.', 3),
('G-FS-008', 'nl', 'Welke informatie moet van afleveringsbonnen worden geregistreerd?', '[{"text":"Alleen de prijs","correct":false},{"text":"Lotnummers, leverdatums en leveranciersgegevens","correct":true},{"text":"Alleen de productnaam","correct":false},{"text":"Alleen de hoeveelheid","correct":false}]', 'Afleveringsbonnen moeten lotnummers, leverdatums, leveranciersgegevens en productinformatie bevatten voor volledige traceerbaarheid.', 4),
('G-FS-008', 'nl', 'Waarom is traceerbaarheid belangrijk voor voedselveiligheid?', '[{"text":"Het is niet belangrijk","correct":false},{"text":"Maakt snelle productterugroeping mogelijk bij besmetting","correct":true},{"text":"Alleen voor boekhoudkundige doeleinden","correct":false},{"text":"Alleen voor marketing","correct":false}]', 'Traceerbaarheid maakt snelle identificatie en terugroeping van getroffen producten mogelijk in geval van besmetting of veiligheidsproblemen, waardoor de volksgezondheid wordt beschermd.', 5),
('G-FS-008', 'nl', 'Wat moet aanwezig zijn voor snelle productterugroeping?', '[{"text":"Niets speciaals","correct":false},{"text":"Een gedocumenteerde terugroepprocedure","correct":true},{"text":"Alleen leverancierscontactinformatie","correct":false},{"text":"Klante-maillijst","correct":false}]', 'Een gedocumenteerde snelle terugroepprocedure moet worden vastgesteld, inclusief rollen, verantwoordelijkheden, communicatieprotocollen en verificatiestappen.', 6),
('G-FS-008', 'nl', 'Welke details over leveranciers moeten worden geregistreerd?', '[{"text":"Alleen hun naam","correct":false},{"text":"Bedrijfsnaam, adres, gezondheidsregistratienummer","correct":true},{"text":"Alleen telefoonnummer","correct":false},{"text":"Alleen e-mailadres","correct":false}]', 'Volledige leveranciersinformatie inclusief bedrijfsnaam, adres, contactgegevens en gezondheidsregistratienummer moet worden geregistreerd voor traceerbaarheid.', 7),
('G-FS-008', 'nl', 'Hoe ondersteunt traceerbaarheid HACCP?', '[{"text":"Het vervangt HACCP","correct":false},{"text":"Maakt verificatie van leverancierskwaliteit en snelle reactie op gevaren mogelijk","correct":true},{"text":"Het is niet gerelateerd aan HACCP","correct":false},{"text":"Alleen voor temperatuurcontrole","correct":false}]', 'Traceerbaarheid ondersteunt HACCP door verificatie van naleving door leveranciers mogelijk te maken en snelle identificatie en verwijdering van gevaarlijke producten toe te staan.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-008', 'de', 'Welche EU-Verordnung schreibt Lebensmittelrückverfolgbarkeit vor?', '[{"text":"EU-Verordnung 852/2004","correct":false},{"text":"EU-Verordnung 178/2002","correct":true},{"text":"EU-Verordnung 1169/2011","correct":false},{"text":"EU-Verordnung 2073/2005","correct":false}]', 'Die EU-Verordnung 178/2002 legt die allgemeinen Grundsätze und Anforderungen der Rückverfolgbarkeit im Lebensmittelrecht fest.', 1),
('G-FS-008', 'de', 'Wie lange müssen Rückverfolgbarkeitsaufzeichnungen aufbewahrt werden?', '[{"text":"1 Jahr","correct":false},{"text":"3 Jahre","correct":false},{"text":"Mindestens 5 Jahre","correct":true},{"text":"10 Jahre","correct":false}]', 'Rückverfolgbarkeitsaufzeichnungen einschließlich Lieferantendetails, Chargennummern und Lieferdaten müssen mindestens 5 Jahre aufbewahrt werden.', 2),
('G-FS-008', 'de', 'Was ist das "Ein-Schritt-zurück, Ein-Schritt-vorwärts"-Prinzip?', '[{"text":"Kochmethode","correct":false},{"text":"Fähigkeit, Lieferanten und Kunden für jedes Produkt zu verfolgen","correct":true},{"text":"Lagerrotation","correct":false},{"text":"Temperaturkontrolle","correct":false}]', 'Das Rückverfolgbarkeitssystem muss die Identifizierung eines Schritts zurück (Ihre Lieferanten) und eines Schritts vorwärts (Ihre Kunden) für alle Produkte ermöglichen.', 3),
('G-FS-008', 'de', 'Welche Informationen sollten von Lieferscheinen erfasst werden?', '[{"text":"Nur der Preis","correct":false},{"text":"Chargennummern, Lieferdaten und Lieferantendetails","correct":true},{"text":"Nur der Produktname","correct":false},{"text":"Nur die Menge","correct":false}]', 'Lieferscheine müssen Chargennummern, Lieferdaten, Lieferantendetails und Produktinformationen für vollständige Rückverfolgbarkeit enthalten.', 4),
('G-FS-008', 'de', 'Warum ist Rückverfolgbarkeit für die Lebensmittelsicherheit wichtig?', '[{"text":"Es ist nicht wichtig","correct":false},{"text":"Ermöglicht schnellen Produktrückruf bei Kontamination","correct":true},{"text":"Nur für Buchhaltungszwecke","correct":false},{"text":"Nur für Marketing","correct":false}]', 'Rückverfolgbarkeit ermöglicht die schnelle Identifizierung und den Rückruf betroffener Produkte im Falle von Kontamination oder Sicherheitsproblemen und schützt die öffentliche Gesundheit.', 5),
('G-FS-008', 'de', 'Was muss für einen schnellen Produktrückruf vorhanden sein?', '[{"text":"Nichts Besonderes","correct":false},{"text":"Ein dokumentiertes Rückrufverfahren","correct":true},{"text":"Nur Lieferantenkontaktinformationen","correct":false},{"text":"Kunden-E-Mail-Liste","correct":false}]', 'Ein dokumentiertes schnelles Rückrufverfahren muss eingerichtet werden, einschließlich Rollen, Verantwortlichkeiten, Kommunikationsprotokollen und Verifizierungsschritten.', 6),
('G-FS-008', 'de', 'Welche Details über Lieferanten müssen erfasst werden?', '[{"text":"Nur ihr Name","correct":false},{"text":"Firmenname, Adresse, Gesundheitsregistrierungsnummer","correct":true},{"text":"Nur Telefonnummer","correct":false},{"text":"Nur E-Mail-Adresse","correct":false}]', 'Vollständige Lieferanteninformationen einschließlich Firmenname, Adresse, Kontaktdaten und Gesundheitsregistrierungsnummer müssen für die Rückverfolgbarkeit erfasst werden.', 7),
('G-FS-008', 'de', 'Wie unterstützt Rückverfolgbarkeit HACCP?', '[{"text":"Es ersetzt HACCP","correct":false},{"text":"Ermöglicht Überprüfung der Lieferantenqualität und schnelle Reaktion auf Gefahren","correct":true},{"text":"Es ist nicht mit HACCP verwandt","correct":false},{"text":"Nur für Temperaturkontrolle","correct":false}]', 'Rückverfolgbarkeit unterstützt HACCP, indem sie die Überprüfung der Lieferanteneinhaltung ermöglicht und eine schnelle Identifizierung und Entfernung gefährlicher Produkte erlaubt.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-009', 'en', 'What must be verified about food suppliers?', '[{"text":"Only their prices","correct":false},{"text":"Health registration and compliance with regulations","correct":true},{"text":"Just their location","correct":false},{"text":"Only delivery schedule","correct":false}]', 'EU Regulation 852/2004 requires verification that all food suppliers are registered and comply with food safety regulations.', 1),
('G-FS-009', 'en', 'What is a supplier health registration number?', '[{"text":"Their tax ID","correct":false},{"text":"Official registration (RGSEAA) proving compliance","correct":true},{"text":"Employee count","correct":false},{"text":"Business license number","correct":false}]', 'The RGSEAA (Registro General Sanitario de Empresas Alimentarias) number proves a supplier is officially registered and meets food safety standards.', 2),
('G-FS-009', 'en', 'What should be checked upon delivery arrival?', '[{"text":"Only the invoice","correct":false},{"text":"Vehicle temperatures and delivery conditions","correct":true},{"text":"Just the driver name","correct":false},{"text":"Only the time","correct":false}]', 'Delivery vehicle temperatures must be checked to ensure the cold chain has been maintained during transport.', 3),
('G-FS-009', 'en', 'What is an approved supplier list?', '[{"text":"A wish list","correct":false},{"text":"Documented list of verified suppliers meeting safety standards","correct":true},{"text":"A price comparison","correct":false},{"text":"Customer recommendations","correct":false}]', 'An approved supplier list documents all verified suppliers who have been assessed and approved based on food safety criteria.', 4),
('G-FS-009', 'en', 'How often should supplier performance be reviewed?', '[{"text":"Never","correct":false},{"text":"Periodically (at least annually)","correct":true},{"text":"Only when problems occur","correct":false},{"text":"Every 5 years","correct":false}]', 'Supplier performance should be reviewed periodically (at least annually) to ensure continued compliance with food safety standards.', 5),
('G-FS-009', 'en', 'What should be done if a delivery does not meet standards?', '[{"text":"Accept it anyway","correct":false},{"text":"Reject and document the non-conformity","correct":true},{"text":"Use it immediately","correct":false},{"text":"Store it separately without documentation","correct":false}]', 'Non-conforming deliveries must be rejected and the non-conformity must be documented for traceability and supplier performance records.', 6),
('G-FS-009', 'en', 'Why is supplier control important for HACCP?', '[{"text":"It is not related","correct":false},{"text":"Suppliers are the first step in controlling hazards","correct":true},{"text":"Only for cost control","correct":false},{"text":"Just for inventory","correct":false}]', 'Supplier control is critical to HACCP as it prevents hazards from entering the food chain at the first step - sourcing.', 7),
('G-FS-009', 'en', 'What documentation should be requested from new suppliers?', '[{"text":"Only business cards","correct":false},{"text":"Health registration, insurance, and food safety certificates","correct":true},{"text":"Just a price list","correct":false},{"text":"Only references","correct":false}]', 'New suppliers should provide health registration (RGSEAA), liability insurance, and relevant food safety certificates before approval.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-009', 'es', '¿Qué debe verificarse sobre los proveedores de alimentos?', '[{"text":"Solo sus precios","correct":false},{"text":"Registro sanitario y cumplimiento de normativa","correct":true},{"text":"Solo su ubicación","correct":false},{"text":"Solo el horario de entrega","correct":false}]', 'El Reglamento UE 852/2004 requiere verificar que todos los proveedores de alimentos estén registrados y cumplan con las normativas de seguridad alimentaria.', 1),
('G-FS-009', 'es', '¿Qué es el número de registro sanitario de un proveedor?', '[{"text":"Su NIF","correct":false},{"text":"Registro oficial (RGSEAA) que demuestra cumplimiento","correct":true},{"text":"Número de empleados","correct":false},{"text":"Número de licencia comercial","correct":false}]', 'El número RGSEAA (Registro General Sanitario de Empresas Alimentarias) demuestra que un proveedor está registrado oficialmente y cumple con los estándares de seguridad alimentaria.', 2),
('G-FS-009', 'es', '¿Qué debe comprobarse al llegar la entrega?', '[{"text":"Solo la factura","correct":false},{"text":"Temperaturas del vehículo y condiciones de entrega","correct":true},{"text":"Solo el nombre del conductor","correct":false},{"text":"Solo la hora","correct":false}]', 'Deben comprobarse las temperaturas del vehículo de entrega para asegurar que la cadena de frío se ha mantenido durante el transporte.', 3),
('G-FS-009', 'es', '¿Qué es una lista de proveedores homologados?', '[{"text":"Una lista de deseos","correct":false},{"text":"Lista documentada de proveedores verificados que cumplen estándares de seguridad","correct":true},{"text":"Una comparación de precios","correct":false},{"text":"Recomendaciones de clientes","correct":false}]', 'Una lista de proveedores homologados documenta todos los proveedores verificados que han sido evaluados y aprobados basándose en criterios de seguridad alimentaria.', 4),
('G-FS-009', 'es', '¿Con qué frecuencia debe revisarse el rendimiento del proveedor?', '[{"text":"Nunca","correct":false},{"text":"Periódicamente (al menos anualmente)","correct":true},{"text":"Solo cuando ocurren problemas","correct":false},{"text":"Cada 5 años","correct":false}]', 'El rendimiento del proveedor debe revisarse periódicamente (al menos anualmente) para asegurar el cumplimiento continuo de los estándares de seguridad alimentaria.', 5),
('G-FS-009', 'es', '¿Qué debe hacerse si una entrega no cumple los estándares?', '[{"text":"Aceptarla de todos modos","correct":false},{"text":"Rechazarla y documentar la no conformidad","correct":true},{"text":"Usarla inmediatamente","correct":false},{"text":"Almacenarla por separado sin documentación","correct":false}]', 'Las entregas no conformes deben rechazarse y la no conformidad debe documentarse para la trazabilidad y los registros de rendimiento del proveedor.', 6),
('G-FS-009', 'es', '¿Por qué es importante el control de proveedores para APPCC?', '[{"text":"No está relacionado","correct":false},{"text":"Los proveedores son el primer paso en el control de peligros","correct":true},{"text":"Solo para control de costos","correct":false},{"text":"Solo para inventario","correct":false}]', 'El control de proveedores es crítico para APPCC ya que previene que los peligros entren en la cadena alimentaria en el primer paso - el aprovisionamiento.', 7),
('G-FS-009', 'es', '¿Qué documentación debe solicitarse a nuevos proveedores?', '[{"text":"Solo tarjetas de visita","correct":false},{"text":"Registro sanitario, seguro y certificados de seguridad alimentaria","correct":true},{"text":"Solo lista de precios","correct":false},{"text":"Solo referencias","correct":false}]', 'Los nuevos proveedores deben proporcionar registro sanitario (RGSEAA), seguro de responsabilidad civil y certificados relevantes de seguridad alimentaria antes de su aprobación.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-009', 'nl', 'Wat moet over voedselleveranciers worden geverifieerd?', '[{"text":"Alleen hun prijzen","correct":false},{"text":"Gezondheidsregistratie en naleving van regelgeving","correct":true},{"text":"Alleen hun locatie","correct":false},{"text":"Alleen leveringsschema","correct":false}]', 'EU-verordening 852/2004 vereist verificatie dat alle voedselleveranciers geregistreerd zijn en voldoen aan voedselveiligheidsvoorschriften.', 1),
('G-FS-009', 'nl', 'Wat is een gezondheidsregistratienummer van een leverancier?', '[{"text":"Hun belastingnummer","correct":false},{"text":"Officiële registratie (RGSEAA) die naleving bewijst","correct":true},{"text":"Aantal werknemers","correct":false},{"text":"Bedrijfslicentienummer","correct":false}]', 'Het RGSEAA-nummer (Registro General Sanitario de Empresas Alimentarias) bewijst dat een leverancier officieel geregistreerd is en voldoet aan voedselveiligheidsnormen.', 2),
('G-FS-009', 'nl', 'Wat moet worden gecontroleerd bij aankomst van de levering?', '[{"text":"Alleen de factuur","correct":false},{"text":"Voertuigtemperaturen en leveringsomstandigheden","correct":true},{"text":"Alleen de naam van de chauffeur","correct":false},{"text":"Alleen de tijd","correct":false}]', 'Voertuigtemperaturen moeten worden gecontroleerd om ervoor te zorgen dat de koude keten tijdens transport is gehandhaafd.', 3),
('G-FS-009', 'nl', 'Wat is een lijst met goedgekeurde leveranciers?', '[{"text":"Een verlanglijstje","correct":false},{"text":"Gedocumenteerde lijst van geverifieerde leveranciers die aan veiligheidsnormen voldoen","correct":true},{"text":"Een prijsvergelijking","correct":false},{"text":"Aanbevelingen van klanten","correct":false}]', 'Een lijst met goedgekeurde leveranciers documenteert alle geverifieerde leveranciers die zijn beoordeeld en goedgekeurd op basis van voedselveiligheidscriteria.', 4),
('G-FS-009', 'nl', 'Hoe vaak moet de prestatie van leveranciers worden beoordeeld?', '[{"text":"Nooit","correct":false},{"text":"Periodiek (minimaal jaarlijks)","correct":true},{"text":"Alleen wanneer er problemen optreden","correct":false},{"text":"Om de 5 jaar","correct":false}]', 'De prestatie van leveranciers moet periodiek (minimaal jaarlijks) worden beoordeeld om voortdurende naleving van voedselveiligheidsnormen te waarborgen.', 5),
('G-FS-009', 'nl', 'Wat moet er worden gedaan als een levering niet aan de normen voldoet?', '[{"text":"Toch accepteren","correct":false},{"text":"Weigeren en de non-conformiteit documenteren","correct":true},{"text":"Onmiddellijk gebruiken","correct":false},{"text":"Apart opslaan zonder documentatie","correct":false}]', 'Niet-conforme leveringen moeten worden geweigerd en de non-conformiteit moet worden gedocumenteerd voor traceerbaarheid en leveranciersprestatieregistraties.', 6),
('G-FS-009', 'nl', 'Waarom is leverancierscontrole belangrijk voor HACCP?', '[{"text":"Het is niet gerelateerd","correct":false},{"text":"Leveranciers zijn de eerste stap in het beheersen van gevaren","correct":true},{"text":"Alleen voor kostenbeheersing","correct":false},{"text":"Alleen voor voorraad","correct":false}]', 'Leverancierscontrole is cruciaal voor HACCP omdat het voorkomt dat gevaren de voedselketen binnenkomen bij de eerste stap - inkoop.', 7),
('G-FS-009', 'nl', 'Welke documentatie moet bij nieuwe leveranciers worden opgevraagd?', '[{"text":"Alleen visitekaartjes","correct":false},{"text":"Gezondheidsregistratie, verzekering en voedselveiligheidscertificaten","correct":true},{"text":"Alleen een prijslijst","correct":false},{"text":"Alleen referenties","correct":false}]', 'Nieuwe leveranciers moeten gezondheidsregistratie (RGSEAA), aansprakelijkheidsverzekering en relevante voedselveiligheidscertificaten verstrekken vóór goedkeuring.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-009', 'de', 'Was muss über Lebensmittellieferanten überprüft werden?', '[{"text":"Nur ihre Preise","correct":false},{"text":"Gesundheitsregistrierung und Einhaltung der Vorschriften","correct":true},{"text":"Nur ihr Standort","correct":false},{"text":"Nur Lieferplan","correct":false}]', 'Die EU-Verordnung 852/2004 verlangt die Überprüfung, dass alle Lebensmittellieferanten registriert sind und die Lebensmittelsicherheitsvorschriften einhalten.', 1),
('G-FS-009', 'de', 'Was ist eine Gesundheitsregistrierungsnummer eines Lieferanten?', '[{"text":"Ihre Steuernummer","correct":false},{"text":"Offizielle Registrierung (RGSEAA) als Nachweis der Einhaltung","correct":true},{"text":"Mitarbeiterzahl","correct":false},{"text":"Gewerbelizenznummer","correct":false}]', 'Die RGSEAA-Nummer (Registro General Sanitario de Empresas Alimentarias) beweist, dass ein Lieferant offiziell registriert ist und Lebensmittelsicherheitsstandards erfüllt.', 2),
('G-FS-009', 'de', 'Was sollte bei Ankunft der Lieferung überprüft werden?', '[{"text":"Nur die Rechnung","correct":false},{"text":"Fahrzeugtemperaturen und Lieferbedingungen","correct":true},{"text":"Nur der Name des Fahrers","correct":false},{"text":"Nur die Uhrzeit","correct":false}]', 'Die Temperaturen des Lieferfahrzeugs müssen überprüft werden, um sicherzustellen, dass die Kühlkette während des Transports eingehalten wurde.', 3),
('G-FS-009', 'de', 'Was ist eine Liste genehmigter Lieferanten?', '[{"text":"Eine Wunschliste","correct":false},{"text":"Dokumentierte Liste überprüfter Lieferanten, die Sicherheitsstandards erfüllen","correct":true},{"text":"Ein Preisvergleich","correct":false},{"text":"Kundenempfehlungen","correct":false}]', 'Eine Liste genehmigter Lieferanten dokumentiert alle verifizierten Lieferanten, die auf der Grundlage von Lebensmittelsicherheitskriterien bewertet und genehmigt wurden.', 4),
('G-FS-009', 'de', 'Wie oft sollte die Lieferantenleistung überprüft werden?', '[{"text":"Nie","correct":false},{"text":"Regelmäßig (mindestens jährlich)","correct":true},{"text":"Nur wenn Probleme auftreten","correct":false},{"text":"Alle 5 Jahre","correct":false}]', 'Die Lieferantenleistung sollte regelmäßig (mindestens jährlich) überprüft werden, um die fortgesetzte Einhaltung der Lebensmittelsicherheitsstandards sicherzustellen.', 5),
('G-FS-009', 'de', 'Was sollte getan werden, wenn eine Lieferung nicht den Standards entspricht?', '[{"text":"Trotzdem akzeptieren","correct":false},{"text":"Ablehnen und die Nichtkonformität dokumentieren","correct":true},{"text":"Sofort verwenden","correct":false},{"text":"Separat lagern ohne Dokumentation","correct":false}]', 'Nicht konforme Lieferungen müssen abgelehnt werden und die Nichtkonformität muss für Rückverfolgbarkeit und Lieferantenleistungsaufzeichnungen dokumentiert werden.', 6),
('G-FS-009', 'de', 'Warum ist Lieferantenkontrolle für HACCP wichtig?', '[{"text":"Es ist nicht verwandt","correct":false},{"text":"Lieferanten sind der erste Schritt zur Kontrolle von Gefahren","correct":true},{"text":"Nur für Kostenkontrolle","correct":false},{"text":"Nur für Inventar","correct":false}]', 'Lieferantenkontrolle ist entscheidend für HACCP, da sie verhindert, dass Gefahren beim ersten Schritt - der Beschaffung - in die Lebensmittelkette gelangen.', 7),
('G-FS-009', 'de', 'Welche Dokumentation sollte von neuen Lieferanten angefordert werden?', '[{"text":"Nur Visitenkarten","correct":false},{"text":"Gesundheitsregistrierung, Versicherung und Lebensmittelsicherheitszertifikate","correct":true},{"text":"Nur eine Preisliste","correct":false},{"text":"Nur Referenzen","correct":false}]', 'Neue Lieferanten sollten Gesundheitsregistrierung (RGSEAA), Haftpflichtversicherung und relevante Lebensmittelsicherheitszertifikate vor der Genehmigung vorlegen.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-010', 'en', 'What must be checked on all deliveries upon arrival?', '[{"text":"Only the quantity","correct":false},{"text":"Temperature, packaging, expiry dates, and quality","correct":true},{"text":"Just the price","correct":false},{"text":"Only the supplier name","correct":false}]', 'RD 3484/2000 requires inspection of temperature compliance, packaging integrity, expiry dates, and organoleptic quality for all deliveries.', 1),
('G-FS-010', 'en', 'What temperature should chilled deliveries be upon arrival?', '[{"text":"Room temperature is acceptable","correct":false},{"text":"0-4°C","correct":true},{"text":"10°C or below","correct":false},{"text":"Any cold temperature","correct":false}]', 'Chilled products must arrive at 0-4°C to ensure the cold chain has been maintained during transport.', 2),
('G-FS-010', 'en', 'What should be done with products that do not meet acceptance criteria?', '[{"text":"Accept but use immediately","correct":false},{"text":"Document and return to supplier","correct":true},{"text":"Store separately and forget","correct":false},{"text":"Give to staff","correct":false}]', 'Non-conforming products must be documented (photos, temperature readings, description) and returned to the supplier with proper rejection documentation.', 3),
('G-FS-010', 'en', 'What is organoleptic quality assessment?', '[{"text":"Chemical testing only","correct":false},{"text":"Evaluation using senses: sight, smell, touch","correct":true},{"text":"Price comparison","correct":false},{"text":"Weight measurement","correct":false}]', 'Organoleptic assessment uses human senses (sight, smell, touch) to evaluate appearance, odor, texture, and color of products for signs of spoilage.', 4),
('G-FS-010', 'en', 'How soon should deliveries be stored after inspection?', '[{"text":"Within 24 hours","correct":false},{"text":"Immediately","correct":true},{"text":"When convenient","correct":false},{"text":"End of day","correct":false}]', 'Deliveries must be stored immediately in appropriate temperature conditions after inspection to prevent temperature abuse and quality deterioration.', 5),
('G-FS-010', 'en', 'What tool is essential for checking delivery temperatures?', '[{"text":"Visual inspection","correct":false},{"text":"Probe thermometer","correct":true},{"text":"Hand touch","correct":false},{"text":"Infrared only","correct":false}]', 'A probe thermometer must be used to check the internal temperature of chilled and frozen products upon delivery.', 6),
('G-FS-010', 'en', 'What information on packaging should be verified?', '[{"text":"Only the brand","correct":false},{"text":"Labeling, expiry dates, lot numbers, integrity","correct":true},{"text":"Just the color","correct":false},{"text":"Only the size","correct":false}]', 'Packaging must be checked for proper labeling, clear expiry dates, lot numbers for traceability, and physical integrity (no tears, damage, or contamination).', 7),
('G-FS-010', 'en', 'Why is immediate storage after reception important?', '[{"text":"To save space","correct":false},{"text":"To prevent temperature abuse and bacterial growth","correct":true},{"text":"For organization only","correct":false},{"text":"To look professional","correct":false}]', 'Immediate storage at correct temperatures prevents products from entering the danger zone (5-60°C) where bacteria multiply rapidly.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-010', 'es', '¿Qué debe comprobarse en todas las entregas a su llegada?', '[{"text":"Solo la cantidad","correct":false},{"text":"Temperatura, embalaje, fechas de caducidad y calidad","correct":true},{"text":"Solo el precio","correct":false},{"text":"Solo el nombre del proveedor","correct":false}]', 'El RD 3484/2000 requiere inspección del cumplimiento de temperatura, integridad del embalaje, fechas de caducidad y calidad organoléptica de todas las entregas.', 1),
('G-FS-010', 'es', '¿A qué temperatura deben llegar los productos refrigerados?', '[{"text":"Temperatura ambiente es aceptable","correct":false},{"text":"0-4°C","correct":true},{"text":"10°C o menos","correct":false},{"text":"Cualquier temperatura fría","correct":false}]', 'Los productos refrigerados deben llegar a 0-4°C para asegurar que la cadena de frío se ha mantenido durante el transporte.', 2),
('G-FS-010', 'es', '¿Qué debe hacerse con productos que no cumplen los criterios de aceptación?', '[{"text":"Aceptar pero usar inmediatamente","correct":false},{"text":"Documentar y devolver al proveedor","correct":true},{"text":"Almacenar por separado y olvidar","correct":false},{"text":"Dar al personal","correct":false}]', 'Los productos no conformes deben documentarse (fotos, lecturas de temperatura, descripción) y devolverse al proveedor con documentación adecuada de rechazo.', 3),
('G-FS-010', 'es', '¿Qué es la evaluación de calidad organoléptica?', '[{"text":"Solo análisis químico","correct":false},{"text":"Evaluación usando los sentidos: vista, olfato, tacto","correct":true},{"text":"Comparación de precios","correct":false},{"text":"Medición de peso","correct":false}]', 'La evaluación organoléptica usa los sentidos humanos (vista, olfato, tacto) para evaluar apariencia, olor, textura y color de productos en busca de signos de deterioro.', 4),
('G-FS-010', 'es', '¿Con qué rapidez deben almacenarse las entregas después de la inspección?', '[{"text":"En 24 horas","correct":false},{"text":"Inmediatamente","correct":true},{"text":"Cuando sea conveniente","correct":false},{"text":"Al final del día","correct":false}]', 'Las entregas deben almacenarse inmediatamente en condiciones de temperatura adecuadas después de la inspección para prevenir abuso de temperatura y deterioro de calidad.', 5),
('G-FS-010', 'es', '¿Qué herramienta es esencial para comprobar las temperaturas de entrega?', '[{"text":"Inspección visual","correct":false},{"text":"Termómetro de sonda","correct":true},{"text":"Tacto con la mano","correct":false},{"text":"Solo infrarrojo","correct":false}]', 'Debe utilizarse un termómetro de sonda para comprobar la temperatura interna de productos refrigerados y congelados a su llegada.', 6),
('G-FS-010', 'es', '¿Qué información del embalaje debe verificarse?', '[{"text":"Solo la marca","correct":false},{"text":"Etiquetado, fechas de caducidad, números de lote, integridad","correct":true},{"text":"Solo el color","correct":false},{"text":"Solo el tamaño","correct":false}]', 'El embalaje debe comprobarse en cuanto a etiquetado correcto, fechas de caducidad claras, números de lote para trazabilidad e integridad física (sin rasgaduras, daños o contaminación).', 7),
('G-FS-010', 'es', '¿Por qué es importante el almacenamiento inmediato después de la recepción?', '[{"text":"Para ahorrar espacio","correct":false},{"text":"Para prevenir abuso de temperatura y crecimiento bacteriano","correct":true},{"text":"Solo para organización","correct":false},{"text":"Para verse profesional","correct":false}]', 'El almacenamiento inmediato a temperaturas correctas previene que los productos entren en la zona de peligro (5-60°C) donde las bacterias se multiplican rápidamente.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-010', 'nl', 'Wat moet bij alle leveringen bij aankomst worden gecontroleerd?', '[{"text":"Alleen de hoeveelheid","correct":false},{"text":"Temperatuur, verpakking, vervaldata en kwaliteit","correct":true},{"text":"Alleen de prijs","correct":false},{"text":"Alleen de naam van de leverancier","correct":false}]', 'RD 3484/2000 vereist inspectie van temperatuurnaleving, verpakkingsintegriteit, vervaldata en organoleptische kwaliteit voor alle leveringen.', 1),
('G-FS-010', 'nl', 'Op welke temperatuur moeten gekoelde leveringen bij aankomst zijn?', '[{"text":"Kamertemperatuur is acceptabel","correct":false},{"text":"0-4°C","correct":true},{"text":"10°C of lager","correct":false},{"text":"Elke koude temperatuur","correct":false}]', 'Gekoelde producten moeten bij 0-4°C aankomen om ervoor te zorgen dat de koude keten tijdens transport is gehandhaafd.', 2),
('G-FS-010', 'nl', 'Wat moet er worden gedaan met producten die niet aan de acceptatiecriteria voldoen?', '[{"text":"Accepteren maar onmiddellijk gebruiken","correct":false},{"text":"Documenteren en retourneren naar leverancier","correct":true},{"text":"Apart opslaan en vergeten","correct":false},{"text":"Aan personeel geven","correct":false}]', 'Niet-conforme producten moeten worden gedocumenteerd (foto\'s, temperatuurmetingen, beschrijving) en geretourneerd naar de leverancier met de juiste afwijzingsdocumentatie.', 3),
('G-FS-010', 'nl', 'Wat is organoleptische kwaliteitsbeoordeling?', '[{"text":"Alleen chemische testen","correct":false},{"text":"Evaluatie met de zintuigen: zicht, geur, aanraking","correct":true},{"text":"Prijsvergelijking","correct":false},{"text":"Gewichtsmeting","correct":false}]', 'Organoleptische beoordeling gebruikt menselijke zintuigen (zicht, geur, aanraking) om uiterlijk, geur, textuur en kleur van producten te evalueren op tekenen van bederf.', 4),
('G-FS-010', 'nl', 'Hoe snel moeten leveringen na inspectie worden opgeslagen?', '[{"text":"Binnen 24 uur","correct":false},{"text":"Onmiddellijk","correct":true},{"text":"Wanneer het uitkomt","correct":false},{"text":"Einde van de dag","correct":false}]', 'Leveringen moeten onmiddellijk na inspectie worden opgeslagen onder de juiste temperatuuromstandigheden om temperatuurmisbruik en kwaliteitsverslechtering te voorkomen.', 5),
('G-FS-010', 'nl', 'Welk gereedschap is essentieel voor het controleren van leveringstemperaturen?', '[{"text":"Visuele inspectie","correct":false},{"text":"Steekthermometer","correct":true},{"text":"Aanraking met de hand","correct":false},{"text":"Alleen infrarood","correct":false}]', 'Een steekthermometer moet worden gebruikt om de interne temperatuur van gekoelde en bevroren producten bij levering te controleren.', 6),
('G-FS-010', 'nl', 'Welke informatie op de verpakking moet worden geverifieerd?', '[{"text":"Alleen het merk","correct":false},{"text":"Etikettering, vervaldata, lotnummers, integriteit","correct":true},{"text":"Alleen de kleur","correct":false},{"text":"Alleen de grootte","correct":false}]', 'Verpakking moet worden gecontroleerd op juiste etikettering, duidelijke vervaldata, lotnummers voor traceerbaarheid en fysieke integriteit (geen scheuren, schade of besmetting).', 7),
('G-FS-010', 'nl', 'Waarom is onmiddellijke opslag na ontvangst belangrijk?', '[{"text":"Om ruimte te besparen","correct":false},{"text":"Om temperatuurmisbruik en bacteriegroei te voorkomen","correct":true},{"text":"Alleen voor organisatie","correct":false},{"text":"Om professioneel te lijken","correct":false}]', 'Onmiddellijke opslag op de juiste temperaturen voorkomt dat producten in de gevarenzone (5-60°C) komen waar bacteriën zich snel vermenigvuldigen.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-010', 'de', 'Was muss bei allen Lieferungen bei Ankunft überprüft werden?', '[{"text":"Nur die Menge","correct":false},{"text":"Temperatur, Verpackung, Verfallsdaten und Qualität","correct":true},{"text":"Nur der Preis","correct":false},{"text":"Nur der Name des Lieferanten","correct":false}]', 'RD 3484/2000 verlangt die Überprüfung der Temperatureinhaltung, Verpackungsintegrität, Verfallsdaten und organoleptischen Qualität aller Lieferungen.', 1),
('G-FS-010', 'de', 'Bei welcher Temperatur sollten gekühlte Lieferungen bei Ankunft sein?', '[{"text":"Raumtemperatur ist akzeptabel","correct":false},{"text":"0-4°C","correct":true},{"text":"10°C oder darunter","correct":false},{"text":"Jede kalte Temperatur","correct":false}]', 'Gekühlte Produkte müssen bei 0-4°C ankommen, um sicherzustellen, dass die Kühlkette während des Transports eingehalten wurde.', 2),
('G-FS-010', 'de', 'Was sollte mit Produkten getan werden, die die Annahmekriterien nicht erfüllen?', '[{"text":"Akzeptieren, aber sofort verwenden","correct":false},{"text":"Dokumentieren und an Lieferanten zurücksenden","correct":true},{"text":"Separat lagern und vergessen","correct":false},{"text":"An Personal geben","correct":false}]', 'Nicht konforme Produkte müssen dokumentiert werden (Fotos, Temperaturmessungen, Beschreibung) und mit ordnungsgemäßer Ablehnungsdokumentation an den Lieferanten zurückgesendet werden.', 3),
('G-FS-010', 'de', 'Was ist organoleptische Qualitätsbewertung?', '[{"text":"Nur chemische Tests","correct":false},{"text":"Bewertung mit den Sinnen: Sicht, Geruch, Berührung","correct":true},{"text":"Preisvergleich","correct":false},{"text":"Gewichtsmessung","correct":false}]', 'Die organoleptische Bewertung nutzt menschliche Sinne (Sicht, Geruch, Berührung), um Aussehen, Geruch, Textur und Farbe von Produkten auf Anzeichen von Verderb zu bewerten.', 4),
('G-FS-010', 'de', 'Wie schnell sollten Lieferungen nach der Inspektion gelagert werden?', '[{"text":"Innerhalb von 24 Stunden","correct":false},{"text":"Sofort","correct":true},{"text":"Wenn es passt","correct":false},{"text":"Am Ende des Tages","correct":false}]', 'Lieferungen müssen sofort nach der Inspektion unter geeigneten Temperaturbedingungen gelagert werden, um Temperaturmissbrauch und Qualitätsverschlechterung zu verhindern.', 5),
('G-FS-010', 'de', 'Welches Werkzeug ist wesentlich für die Überprüfung von Liefertemperaturen?', '[{"text":"Visuelle Inspektion","correct":false},{"text":"Einstichthermometer","correct":true},{"text":"Handberührung","correct":false},{"text":"Nur Infrarot","correct":false}]', 'Ein Einstichthermometer muss verwendet werden, um die Innentemperatur von gekühlten und gefrorenen Produkten bei Lieferung zu überprüfen.', 6),
('G-FS-010', 'de', 'Welche Informationen auf der Verpackung sollten überprüft werden?', '[{"text":"Nur die Marke","correct":false},{"text":"Kennzeichnung, Verfallsdaten, Chargennummern, Integrität","correct":true},{"text":"Nur die Farbe","correct":false},{"text":"Nur die Größe","correct":false}]', 'Die Verpackung muss auf ordnungsgemäße Kennzeichnung, klare Verfallsdaten, Chargennummern für Rückverfolgbarkeit und physische Integrität (keine Risse, Schäden oder Kontamination) überprüft werden.', 7),
('G-FS-010', 'de', 'Warum ist sofortige Lagerung nach Annahme wichtig?', '[{"text":"Um Platz zu sparen","correct":false},{"text":"Um Temperaturmissbrauch und Bakterienwachstum zu verhindern","correct":true},{"text":"Nur für Organisation","correct":false},{"text":"Um professionell auszusehen","correct":false}]', 'Sofortige Lagerung bei korrekten Temperaturen verhindert, dass Produkte in die Gefahrenzone (5-60°C) gelangen, wo sich Bakterien schnell vermehren.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-011', 'en', 'What does FIFO stand for in food storage?', '[{"text":"Fast In, Fast Out","correct":false},{"text":"First In, First Out","correct":true},{"text":"Final Inspection, Final Output","correct":false},{"text":"Food Integrity, Food Order","correct":false}]', 'FIFO (First In, First Out) is the rotation system ensuring older stock is used before newer stock to minimize waste and maintain freshness.', 1),
('G-FS-011', 'en', 'Why must raw and cooked products be separated in storage?', '[{"text":"For organization only","correct":false},{"text":"To prevent cross-contamination","correct":true},{"text":"To save space","correct":false},{"text":"It is not necessary","correct":false}]', 'Separation prevents bacteria from raw foods (especially meat, poultry, fish) from contaminating ready-to-eat foods, a critical food safety practice.', 2),
('G-FS-011', 'en', 'Where should raw meat be stored in relation to cooked products?', '[{"text":"Above cooked products","correct":false},{"text":"Below cooked products","correct":true},{"text":"Mixed together","correct":false},{"text":"Location does not matter","correct":false}]', 'Raw meat must be stored on lower shelves below cooked and ready-to-eat foods to prevent drips and cross-contamination.', 3),
('G-FS-011', 'en', 'What information must be on all storage container labels?', '[{"text":"Only the product name","correct":false},{"text":"Content and date","correct":true},{"text":"Just the date","correct":false},{"text":"Labels are optional","correct":false}]', 'All containers must be labeled with both content (what it is) and date (when it was prepared or opened) for proper rotation and safety.', 4),
('G-FS-011', 'en', 'Why should food never be stored directly on the floor?', '[{"text":"It looks unprofessional","correct":false},{"text":"Risk of contamination from pests, cleaning, and moisture","correct":true},{"text":"To save floor space","correct":false},{"text":"It is actually acceptable","correct":false}]', 'Floor storage exposes food to contamination from pests, cleaning chemicals, flooding, and makes cleaning under shelving impossible.', 5),
('G-FS-011', 'en', 'How often should storage temperatures be checked?', '[{"text":"Weekly","correct":false},{"text":"Daily","correct":true},{"text":"Monthly","correct":false},{"text":"Only when problems occur","correct":false}]', 'Storage temperatures (refrigerators, freezers, dry storage) must be checked and recorded daily to ensure food safety.', 6),
('G-FS-011', 'en', 'What is the key principle of proper storage area maintenance?', '[{"text":"Keep doors open for air","correct":false},{"text":"Keep clean, organized, and at correct temperatures","correct":true},{"text":"Fill to maximum capacity","correct":false},{"text":"Maintenance is not important","correct":false}]', 'Proper storage requires cleanliness, organization, appropriate labeling, FIFO rotation, and maintaining correct temperatures at all times.', 7),
('G-FS-011', 'en', 'Why must all containers be covered in storage?', '[{"text":"To save space","correct":false},{"text":"To prevent contamination and dehydration","correct":true},{"text":"For aesthetic reasons","correct":false},{"text":"Covering is optional","correct":false}]', 'Covering containers prevents contamination from other foods, pests, or environmental sources, and prevents food from drying out.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-011', 'es', '¿Qué significa FIFO en almacenamiento de alimentos?', '[{"text":"Rápido Dentro, Rápido Fuera","correct":false},{"text":"Primero en Entrar, Primero en Salir","correct":true},{"text":"Inspección Final, Salida Final","correct":false},{"text":"Integridad Alimentaria, Orden Alimentario","correct":false}]', 'FIFO (First In, First Out / Primero en Entrar, Primero en Salir) es el sistema de rotación que asegura que el stock más antiguo se use antes que el más nuevo para minimizar desperdicios y mantener frescura.', 1),
('G-FS-011', 'es', '¿Por qué deben separarse los productos crudos y cocinados en el almacenamiento?', '[{"text":"Solo para organización","correct":false},{"text":"Para prevenir la contaminación cruzada","correct":true},{"text":"Para ahorrar espacio","correct":false},{"text":"No es necesario","correct":false}]', 'La separación previene que las bacterias de alimentos crudos (especialmente carne, aves, pescado) contaminen alimentos listos para consumir, una práctica crítica de seguridad alimentaria.', 2),
('G-FS-011', 'es', '¿Dónde debe almacenarse la carne cruda en relación con productos cocinados?', '[{"text":"Encima de productos cocinados","correct":false},{"text":"Debajo de productos cocinados","correct":true},{"text":"Mezclados juntos","correct":false},{"text":"La ubicación no importa","correct":false}]', 'La carne cruda debe almacenarse en estantes inferiores debajo de alimentos cocinados y listos para consumir para prevenir goteos y contaminación cruzada.', 3),
('G-FS-011', 'es', '¿Qué información debe tener todas las etiquetas de los contenedores de almacenamiento?', '[{"text":"Solo el nombre del producto","correct":false},{"text":"Contenido y fecha","correct":true},{"text":"Solo la fecha","correct":false},{"text":"Las etiquetas son opcionales","correct":false}]', 'Todos los contenedores deben etiquetarse con contenido (qué es) y fecha (cuándo se preparó o abrió) para una rotación y seguridad adecuadas.', 4),
('G-FS-011', 'es', '¿Por qué nunca debe almacenarse alimento directamente en el suelo?', '[{"text":"Parece poco profesional","correct":false},{"text":"Riesgo de contaminación por plagas, limpieza y humedad","correct":true},{"text":"Para ahorrar espacio en el suelo","correct":false},{"text":"En realidad es aceptable","correct":false}]', 'El almacenamiento en el suelo expone los alimentos a contaminación por plagas, productos químicos de limpieza, inundaciones y hace imposible la limpieza bajo las estanterías.', 5),
('G-FS-011', 'es', '¿Con qué frecuencia deben comprobarse las temperaturas de almacenamiento?', '[{"text":"Semanalmente","correct":false},{"text":"Diariamente","correct":true},{"text":"Mensualmente","correct":false},{"text":"Solo cuando ocurren problemas","correct":false}]', 'Las temperaturas de almacenamiento (refrigeradores, congeladores, almacenamiento seco) deben comprobarse y registrarse diariamente para asegurar la seguridad alimentaria.', 6),
('G-FS-011', 'es', '¿Cuál es el principio clave del mantenimiento adecuado de áreas de almacenamiento?', '[{"text":"Mantener puertas abiertas para el aire","correct":false},{"text":"Mantener limpio, organizado y a temperaturas correctas","correct":true},{"text":"Llenar a capacidad máxima","correct":false},{"text":"El mantenimiento no es importante","correct":false}]', 'El almacenamiento adecuado requiere limpieza, organización, etiquetado apropiado, rotación FIFO y mantenimiento de temperaturas correctas en todo momento.', 7),
('G-FS-011', 'es', '¿Por qué deben cubrirse todos los contenedores en el almacenamiento?', '[{"text":"Para ahorrar espacio","correct":false},{"text":"Para prevenir contaminación y deshidratación","correct":true},{"text":"Por razones estéticas","correct":false},{"text":"Cubrir es opcional","correct":false}]', 'Cubrir los contenedores previene la contaminación de otros alimentos, plagas o fuentes ambientales, y previene que los alimentos se sequen.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-011', 'nl', 'Wat betekent FIFO bij voedselopslag?', '[{"text":"Snel In, Snel Uit","correct":false},{"text":"Eerst In, Eerst Uit","correct":true},{"text":"Finale Inspectie, Finale Output","correct":false},{"text":"Voedselintegriteit, Voedselorde","correct":false}]', 'FIFO (First In, First Out / Eerst In, Eerst Uit) is het rotatiesysteem dat ervoor zorgt dat oudere voorraad wordt gebruikt vóór nieuwere voorraad om verspilling te minimaliseren en versheid te behouden.', 1),
('G-FS-011', 'nl', 'Waarom moeten rauwe en gekookte producten gescheiden worden opgeslagen?', '[{"text":"Alleen voor organisatie","correct":false},{"text":"Om kruisbesmetting te voorkomen","correct":true},{"text":"Om ruimte te besparen","correct":false},{"text":"Het is niet nodig","correct":false}]', 'Scheiding voorkomt dat bacteriën van rauw voedsel (vooral vlees, gevogelte, vis) kant-en-klare levensmiddelen besmetten, een cruciale voedselveiligheidspraktijk.', 2),
('G-FS-011', 'nl', 'Waar moet rauw vlees worden opgeslagen in relatie tot gekookte producten?', '[{"text":"Boven gekookte producten","correct":false},{"text":"Onder gekookte producten","correct":true},{"text":"Door elkaar gemengd","correct":false},{"text":"Locatie maakt niet uit","correct":false}]', 'Rauw vlees moet op lagere planken onder gekookt en kant-en-klaar voedsel worden opgeslagen om druppels en kruisbesmetting te voorkomen.', 3),
('G-FS-011', 'nl', 'Welke informatie moet op alle opslagcontainerlabels staan?', '[{"text":"Alleen de productnaam","correct":false},{"text":"Inhoud en datum","correct":true},{"text":"Alleen de datum","correct":false},{"text":"Labels zijn optioneel","correct":false}]', 'Alle containers moeten worden gelabeld met zowel inhoud (wat het is) als datum (wanneer het is bereid of geopend) voor juiste rotatie en veiligheid.', 4),
('G-FS-011', 'nl', 'Waarom mag voedsel nooit direct op de vloer worden opgeslagen?', '[{"text":"Het ziet er onprofessioneel uit","correct":false},{"text":"Risico op besmetting door ongedierte, reiniging en vocht","correct":true},{"text":"Om vloerruimte te besparen","correct":false},{"text":"Het is eigenlijk acceptabel","correct":false}]', 'Vloer opslag stelt voedsel bloot aan besmetting door ongedierte, schoonmaakmiddelen, overstromingen en maakt reiniging onder rekken onmogelijk.', 5),
('G-FS-011', 'nl', 'Hoe vaak moeten opslagtemperaturen worden gecontroleerd?', '[{"text":"Wekelijks","correct":false},{"text":"Dagelijks","correct":true},{"text":"Maandelijks","correct":false},{"text":"Alleen wanneer er problemen zijn","correct":false}]', 'Opslagtemperaturen (koelkasten, vriezers, droge opslag) moeten dagelijks worden gecontroleerd en geregistreerd om voedselveiligheid te waarborgen.', 6),
('G-FS-011', 'nl', 'Wat is het belangrijkste principe van goed onderhoud van opslagruimtes?', '[{"text":"Deuren open houden voor lucht","correct":false},{"text":"Schoon, georganiseerd en op de juiste temperaturen houden","correct":true},{"text":"Vullen tot maximale capaciteit","correct":false},{"text":"Onderhoud is niet belangrijk","correct":false}]', 'Goede opslag vereist netheid, organisatie, passende etikettering, FIFO-rotatie en het te allen tijde handhaven van de juiste temperaturen.', 7),
('G-FS-011', 'nl', 'Waarom moeten alle containers tijdens opslag worden afgedekt?', '[{"text":"Om ruimte te besparen","correct":false},{"text":"Om besmetting en uitdroging te voorkomen","correct":true},{"text":"Om esthetische redenen","correct":false},{"text":"Afdekken is optioneel","correct":false}]', 'Het afdekken van containers voorkomt besmetting van ander voedsel, ongedierte of omgevingsbronnen, en voorkomt dat voedsel uitdroogt.', 8);
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-FS-011', 'de', 'Wofür steht FIFO bei der Lebensmittellagerung?', '[{"text":"Schnell Rein, Schnell Raus","correct":false},{"text":"First In, First Out (Zuerst rein, zuerst raus)","correct":true},{"text":"Endkontrolle, Endausgabe","correct":false},{"text":"Lebensmittelintegrität, Lebensmittelordnung","correct":false}]', 'FIFO (First In, First Out / Zuerst rein, zuerst raus) ist das Rotationssystem, das sicherstellt, dass ältere Bestände vor neueren verwendet werden, um Abfall zu minimieren und Frische zu erhalten.', 1),
('G-FS-011', 'de', 'Warum müssen rohe und gekochte Produkte bei der Lagerung getrennt werden?', '[{"text":"Nur zur Organisation","correct":false},{"text":"Um Kreuzkontamination zu verhindern","correct":true},{"text":"Um Platz zu sparen","correct":false},{"text":"Es ist nicht notwendig","correct":false}]', 'Die Trennung verhindert, dass Bakterien von rohen Lebensmitteln (besonders Fleisch, Geflügel, Fisch) verzehrfertige Lebensmittel kontaminieren, eine kritische Lebensmittelsicherheitspraxis.', 2),
('G-FS-011', 'de', 'Wo sollte rohes Fleisch in Bezug auf gekochte Produkte gelagert werden?', '[{"text":"Über gekochten Produkten","correct":false},{"text":"Unter gekochten Produkten","correct":true},{"text":"Zusammen gemischt","correct":false},{"text":"Der Standort spielt keine Rolle","correct":false}]', 'Rohes Fleisch muss auf unteren Regalen unter gekochten und verzehrfertigen Lebensmitteln gelagert werden, um Tropfen und Kreuzkontamination zu verhindern.', 3),
('G-FS-011', 'de', 'Welche Informationen müssen auf allen Lagerbehälterbeschriftungen stehen?', '[{"text":"Nur der Produktname","correct":false},{"text":"Inhalt und Datum","correct":true},{"text":"Nur das Datum","correct":false},{"text":"Etiketten sind optional","correct":false}]', 'Alle Behälter müssen sowohl mit Inhalt (was es ist) als auch Datum (wann es zubereitet oder geöffnet wurde) beschriftet werden für ordnungsgemäße Rotation und Sicherheit.', 4),
('G-FS-011', 'de', 'Warum sollten Lebensmittel niemals direkt auf dem Boden gelagert werden?', '[{"text":"Es sieht unprofessionell aus","correct":false},{"text":"Kontaminationsrisiko durch Schädlinge, Reinigung und Feuchtigkeit","correct":true},{"text":"Um Bodenplatz zu sparen","correct":false},{"text":"Es ist eigentlich akzeptabel","correct":false}]', 'Bodenlagerung setzt Lebensmittel der Kontamination durch Schädlinge, Reinigungschemikalien, Überschwemmungen aus und macht die Reinigung unter Regalen unmöglich.', 5),
('G-FS-011', 'de', 'Wie oft sollten Lagertemperaturen überprüft werden?', '[{"text":"Wöchentlich","correct":false},{"text":"Täglich","correct":true},{"text":"Monatlich","correct":false},{"text":"Nur wenn Probleme auftreten","correct":false}]', 'Lagertemperaturen (Kühlschränke, Gefrierschränke, Trockenlagerung) müssen täglich überprüft und aufgezeichnet werden, um die Lebensmittelsicherheit zu gewährleisten.', 6),
('G-FS-011', 'de', 'Was ist das Schlüsselprinzip für die ordnungsgemäße Wartung von Lagerbereichen?', '[{"text":"Türen für Luft offen halten","correct":false},{"text":"Sauber, organisiert und bei korrekten Temperaturen halten","correct":true},{"text":"Bis zur maximalen Kapazität füllen","correct":false},{"text":"Wartung ist nicht wichtig","correct":false}]', 'Ordnungsgemäße Lagerung erfordert Sauberkeit, Organisation, angemessene Beschriftung, FIFO-Rotation und Aufrechterhaltung korrekter Temperaturen zu jeder Zeit.', 7),
('G-FS-011', 'de', 'Warum müssen alle Behälter bei der Lagerung abgedeckt werden?', '[{"text":"Um Platz zu sparen","correct":false},{"text":"Um Kontamination und Austrocknung zu verhindern","correct":true},{"text":"Aus ästhetischen Gründen","correct":false},{"text":"Abdecken ist optional","correct":false}]', 'Das Abdecken von Behältern verhindert Kontamination durch andere Lebensmittel, Schädlinge oder Umweltquellen und verhindert, dass Lebensmittel austrocknen.', 8);
