-- Training test questions for Documentation and Environmental guides
-- 10 guides × 8 questions × 4 languages = 320 rows

-- G-DOC-001: Mandatory Documentation Checklist
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-DOC-001', 'en', 'Which of the following is NOT part of the mandatory documentation for a hospitality establishment?', '[{"text":"APPCC plan and records","correct":false},{"text":"PRL Prevention Plan","correct":false},{"text":"Marketing strategy plan","correct":true},{"text":"Employee contracts and registrations","correct":false}]', 'Marketing strategy plans are not legally required documentation. APPCC, PRL, and employment records are mandatory.', 1),
('G-DOC-001', 'en', 'What is the RGSEAA?', '[{"text":"A food safety training certificate","correct":false},{"text":"Health registration number for food establishments","correct":true},{"text":"An employee insurance policy","correct":false},{"text":"A waste management permit","correct":false}]', 'RGSEAA (Registro General Sanitario de Empresas Alimentarias y Alimentos) is the official health registration required for all food businesses.', 2),
('G-DOC-001', 'en', 'Which insurance documentation must hospitality establishments maintain?', '[{"text":"Only fire insurance","correct":false},{"text":"Only liability insurance","correct":false},{"text":"All relevant insurance policies","correct":true},{"text":"No insurance is required","correct":false}]', 'Establishments must maintain documentation for all applicable insurance policies including liability, fire, and other relevant coverage.', 3),
('G-DOC-001', 'en', 'How long must time recording system records be kept?', '[{"text":"1 year","correct":false},{"text":"2 years","correct":false},{"text":"4 years","correct":true},{"text":"10 years","correct":false}]', 'According to RD-ley 8/2019, time recording records must be kept for 4 years and be available for labor inspection.', 4),
('G-DOC-001', 'en', 'What documentation proves compliance with fire safety regulations?', '[{"text":"Fire safety certificate","correct":true},{"text":"Insurance policy only","correct":false},{"text":"Building construction plans","correct":false},{"text":"Employee training records only","correct":false}]', 'A fire safety certificate issued by authorized technicians is required to prove compliance with fire safety regulations.', 5),
('G-DOC-001', 'en', 'Which document outlines workplace hazards and preventive measures?', '[{"text":"Employee handbook","correct":false},{"text":"PRL Prevention Plan","correct":true},{"text":"APPCC plan","correct":false},{"text":"Activity license","correct":false}]', 'The PRL Prevention Plan (Plan de Prevención de Riesgos Laborales) identifies workplace hazards and establishes preventive measures.', 6),
('G-DOC-001', 'en', 'What must be included in food handler certificate documentation?', '[{"text":"Only the certificate number","correct":false},{"text":"Copies of all staff certificates on file","correct":true},{"text":"Just a list of names","correct":false},{"text":"Only expired certificates","correct":false}]', 'The establishment must maintain copies of all valid food handler certificates for all employees who handle food.', 7),
('G-DOC-001', 'en', 'Which permits are required before opening a hospitality establishment?', '[{"text":"Activity license and opening permit","correct":true},{"text":"Only a business registration","correct":false},{"text":"Only health registration","correct":false},{"text":"No permits are required","correct":false}]', 'Both an activity license (licencia de actividad) and opening permit are required before operating a hospitality establishment.', 8),

('G-DOC-001', 'es', '¿Cuál de los siguientes NO forma parte de la documentación obligatoria para un establecimiento de hostelería?', '[{"text":"Plan APPCC y registros","correct":false},{"text":"Plan de Prevención de PRL","correct":false},{"text":"Plan de estrategia de marketing","correct":true},{"text":"Contratos de empleados y registros","correct":false}]', 'Los planes de estrategia de marketing no son documentación legalmente requerida. APPCC, PRL y registros laborales son obligatorios.', 1),
('G-DOC-001', 'es', '¿Qué es el RGSEAA?', '[{"text":"Un certificado de formación en seguridad alimentaria","correct":false},{"text":"Número de registro sanitario para establecimientos alimentarios","correct":true},{"text":"Una póliza de seguro para empleados","correct":false},{"text":"Un permiso de gestión de residuos","correct":false}]', 'RGSEAA (Registro General Sanitario de Empresas Alimentarias y Alimentos) es el registro sanitario oficial requerido para todos los negocios alimentarios.', 2),
('G-DOC-001', 'es', '¿Qué documentación de seguros deben mantener los establecimientos de hostelería?', '[{"text":"Solo seguro contra incendios","correct":false},{"text":"Solo seguro de responsabilidad civil","correct":false},{"text":"Todas las pólizas de seguro relevantes","correct":true},{"text":"No se requiere ningún seguro","correct":false}]', 'Los establecimientos deben mantener documentación de todas las pólizas aplicables incluyendo responsabilidad civil, incendios y otras coberturas relevantes.', 3),
('G-DOC-001', 'es', '¿Cuánto tiempo deben conservarse los registros del sistema de control horario?', '[{"text":"1 año","correct":false},{"text":"2 años","correct":false},{"text":"4 años","correct":true},{"text":"10 años","correct":false}]', 'Según el RD-ley 8/2019, los registros de control horario deben conservarse durante 4 años y estar disponibles para la inspección de trabajo.', 4),
('G-DOC-001', 'es', '¿Qué documentación demuestra el cumplimiento de las normativas contra incendios?', '[{"text":"Certificado de seguridad contra incendios","correct":true},{"text":"Solo la póliza de seguro","correct":false},{"text":"Planos de construcción del edificio","correct":false},{"text":"Solo registros de formación de empleados","correct":false}]', 'Se requiere un certificado de seguridad contra incendios emitido por técnicos autorizados para demostrar el cumplimiento de las normativas.', 5),
('G-DOC-001', 'es', '¿Qué documento detalla los riesgos laborales y las medidas preventivas?', '[{"text":"Manual del empleado","correct":false},{"text":"Plan de Prevención de PRL","correct":true},{"text":"Plan APPCC","correct":false},{"text":"Licencia de actividad","correct":false}]', 'El Plan de Prevención de Riesgos Laborales identifica los riesgos del lugar de trabajo y establece medidas preventivas.', 6),
('G-DOC-001', 'es', '¿Qué debe incluirse en la documentación del carnet de manipulador de alimentos?', '[{"text":"Solo el número de certificado","correct":false},{"text":"Copias de todos los carnets del personal archivadas","correct":true},{"text":"Solo una lista de nombres","correct":false},{"text":"Solo certificados caducados","correct":false}]', 'El establecimiento debe mantener copias de todos los carnets de manipulador válidos de todos los empleados que manipulan alimentos.', 7),
('G-DOC-001', 'es', '¿Qué permisos se requieren antes de abrir un establecimiento de hostelería?', '[{"text":"Licencia de actividad y permiso de apertura","correct":true},{"text":"Solo un registro mercantil","correct":false},{"text":"Solo registro sanitario","correct":false},{"text":"No se requieren permisos","correct":false}]', 'Se requieren tanto la licencia de actividad como el permiso de apertura antes de operar un establecimiento de hostelería.', 8),

('G-DOC-001', 'nl', 'Welke van de volgende behoort NIET tot de verplichte documentatie voor een horecagelegenheid?', '[{"text":"HACCP-plan en registraties","correct":false},{"text":"Arbeidsveiligheidsplan","correct":false},{"text":"Marketingstrategieplan","correct":true},{"text":"Arbeidscontracten en registraties","correct":false}]', 'Marketingstrategieplannen zijn geen wettelijk vereiste documentatie. HACCP, arbeidsveiligheid en arbeidsregistraties zijn verplicht.', 1),
('G-DOC-001', 'nl', 'Wat is het RGSEAA?', '[{"text":"Een voedselveiligheidstrainingsertificaat","correct":false},{"text":"Gezondheidsregistratienummer voor voedselbedrijven","correct":true},{"text":"Een werknemersverzekeringspolis","correct":false},{"text":"Een afvalbeheervergunning","correct":false}]', 'RGSEAA (Registro General Sanitario de Empresas Alimentarias y Alimentos) is de officiële gezondheidsregistratie vereist voor alle voedselbedrijven.', 2),
('G-DOC-001', 'nl', 'Welke verzekeringsdocumentatie moeten horecagelegenheden bijhouden?', '[{"text":"Alleen brandverzekering","correct":false},{"text":"Alleen aansprakelijkheidsverzekering","correct":false},{"text":"Alle relevante verzekeringspolissen","correct":true},{"text":"Geen verzekering is vereist","correct":false}]', 'Gelegenheden moeten documentatie bijhouden voor alle toepasselijke verzekeringspolissen, inclusief aansprakelijkheid, brand en andere relevante dekking.', 3),
('G-DOC-001', 'nl', 'Hoe lang moeten tijdregistratiesysteemgegevens worden bewaard?', '[{"text":"1 jaar","correct":false},{"text":"2 jaar","correct":false},{"text":"4 jaar","correct":true},{"text":"10 jaar","correct":false}]', 'Volgens RD-ley 8/2019 moeten tijdregistratiegegevens 4 jaar worden bewaard en beschikbaar zijn voor arbeidsinspectie.', 4),
('G-DOC-001', 'nl', 'Welke documentatie bewijst naleving van brandveiligheidsvoorschriften?', '[{"text":"Brandveiligheidscertificaat","correct":true},{"text":"Alleen verzekeringspolis","correct":false},{"text":"Bouwconstructietekeningen","correct":false},{"text":"Alleen werknemerstrainingsgegevens","correct":false}]', 'Een brandveiligheidscertificaat uitgegeven door geautoriseerde technici is vereist om naleving van brandveiligheidsvoorschriften te bewijzen.', 5),
('G-DOC-001', 'nl', 'Welk document beschrijft werkplekrisicos en preventieve maatregelen?', '[{"text":"Werknemershandboek","correct":false},{"text":"Arbeidsveiligheidsplan","correct":true},{"text":"HACCP-plan","correct":false},{"text":"Activiteitenvergunning","correct":false}]', 'Het arbeidsveiligheidsplan (Plan de Prevención de Riesgos Laborales) identificeert werkplekrisicos en stelt preventieve maatregelen vast.', 6),
('G-DOC-001', 'nl', 'Wat moet worden opgenomen in de documentatie van voedselverwerkingscertificaten?', '[{"text":"Alleen het certificaatnummer","correct":false},{"text":"Kopieën van alle personeelscertificaten in het archief","correct":true},{"text":"Alleen een namenlijst","correct":false},{"text":"Alleen verlopen certificaten","correct":false}]', 'De gelegenheid moet kopieën bijhouden van alle geldige voedselverwerkingscertificaten voor alle werknemers die voedsel hanteren.', 7),
('G-DOC-001', 'nl', 'Welke vergunningen zijn vereist voordat een horecagelegenheid wordt geopend?', '[{"text":"Activiteitenvergunning en openingsvergunning","correct":true},{"text":"Alleen een bedrijfsregistratie","correct":false},{"text":"Alleen gezondheidsregistratie","correct":false},{"text":"Geen vergunningen zijn vereist","correct":false}]', 'Zowel een activiteitenvergunning (licencia de actividad) als een openingsvergunning zijn vereist voordat een horecagelegenheid kan worden geëxploiteerd.', 8),

('G-DOC-001', 'de', 'Welches der folgenden gehört NICHT zur Pflichtdokumentation für einen Gastronomiebetrieb?', '[{"text":"HACCP-Plan und Aufzeichnungen","correct":false},{"text":"Arbeitsschutzplan","correct":false},{"text":"Marketingstrategie-Plan","correct":true},{"text":"Arbeitsverträge und Registrierungen","correct":false}]', 'Marketingstrategie-Pläne sind keine gesetzlich vorgeschriebene Dokumentation. HACCP, Arbeitsschutz und Arbeitsunterlagen sind Pflicht.', 1),
('G-DOC-001', 'de', 'Was ist das RGSEAA?', '[{"text":"Ein Lebensmittelsicherheits-Schulungszertifikat","correct":false},{"text":"Gesundheitsregistrierungsnummer für Lebensmittelbetriebe","correct":true},{"text":"Eine Mitarbeiterversicherungspolice","correct":false},{"text":"Eine Abfallentsorgungsgenehmigung","correct":false}]', 'RGSEAA (Registro General Sanitario de Empresas Alimentarias y Alimentos) ist die offizielle Gesundheitsregistrierung für alle Lebensmittelbetriebe.', 2),
('G-DOC-001', 'de', 'Welche Versicherungsdokumentation müssen Gastronomiebetriebe führen?', '[{"text":"Nur Feuerversicherung","correct":false},{"text":"Nur Haftpflichtversicherung","correct":false},{"text":"Alle relevanten Versicherungspolicen","correct":true},{"text":"Keine Versicherung ist erforderlich","correct":false}]', 'Betriebe müssen Dokumentation für alle anwendbaren Versicherungspolicen führen, einschließlich Haftpflicht, Feuer und andere relevante Deckung.', 3),
('G-DOC-001', 'de', 'Wie lange müssen Zeiterfassungssystem-Aufzeichnungen aufbewahrt werden?', '[{"text":"1 Jahr","correct":false},{"text":"2 Jahre","correct":false},{"text":"4 Jahre","correct":true},{"text":"10 Jahre","correct":false}]', 'Gemäß RD-ley 8/2019 müssen Zeiterfassungsaufzeichnungen 4 Jahre aufbewahrt und für die Arbeitsinspektion verfügbar sein.', 4),
('G-DOC-001', 'de', 'Welche Dokumentation beweist die Einhaltung von Brandschutzvorschriften?', '[{"text":"Brandschutzzertifikat","correct":true},{"text":"Nur Versicherungspolice","correct":false},{"text":"Gebäudekonstruktionspläne","correct":false},{"text":"Nur Mitarbeiterschulungsunterlagen","correct":false}]', 'Ein von autorisierten Technikern ausgestelltes Brandschutzzertifikat ist erforderlich, um die Einhaltung von Brandschutzvorschriften nachzuweisen.', 5),
('G-DOC-001', 'de', 'Welches Dokument beschreibt Arbeitsplatzgefahren und Präventivmaßnahmen?', '[{"text":"Mitarbeiterhandbuch","correct":false},{"text":"Arbeitsschutzplan","correct":true},{"text":"HACCP-Plan","correct":false},{"text":"Tätigkeitslizenz","correct":false}]', 'Der Arbeitsschutzplan (Plan de Prevención de Riesgos Laborales) identifiziert Arbeitsplatzgefahren und legt Präventivmaßnahmen fest.', 6),
('G-DOC-001', 'de', 'Was muss in der Dokumentation der Lebensmittelhygiene-Zertifikate enthalten sein?', '[{"text":"Nur die Zertifikatsnummer","correct":false},{"text":"Kopien aller Mitarbeiterzertifikate in der Akte","correct":true},{"text":"Nur eine Namensliste","correct":false},{"text":"Nur abgelaufene Zertifikate","correct":false}]', 'Der Betrieb muss Kopien aller gültigen Lebensmittelhygiene-Zertifikate für alle Mitarbeiter, die mit Lebensmitteln umgehen, aufbewahren.', 7),
('G-DOC-001', 'de', 'Welche Genehmigungen sind vor der Eröffnung eines Gastronomiebetriebs erforderlich?', '[{"text":"Tätigkeitslizenz und Eröffnungsgenehmigung","correct":true},{"text":"Nur eine Gewerbeanmeldung","correct":false},{"text":"Nur Gesundheitsregistrierung","correct":false},{"text":"Keine Genehmigungen sind erforderlich","correct":false}]', 'Sowohl eine Tätigkeitslizenz (licencia de actividad) als auch eine Eröffnungsgenehmigung sind vor dem Betrieb eines Gastronomiebetriebs erforderlich.', 8);

-- G-DOC-002: Training Records
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-DOC-002', 'en', 'What must be included in training records documentation?', '[{"text":"Only the training topic","correct":false},{"text":"Topic, date, duration, trainer, attendees, and content","correct":true},{"text":"Just attendee names","correct":false},{"text":"Only the date","correct":false}]', 'Complete training records must include the training topic, date, duration, trainer, attendees, and content covered.', 1),
('G-DOC-002', 'en', 'Who must sign training attendance records?', '[{"text":"Only the trainer","correct":false},{"text":"Only the manager","correct":false},{"text":"The participants who attended","correct":true},{"text":"No signatures are required","correct":false}]', 'Participants must sign attendance records to verify their participation in the training session.', 2),
('G-DOC-002', 'en', 'Which types of training must be documented?', '[{"text":"Only food safety training","correct":false},{"text":"Only occupational health training","correct":false},{"text":"Both food safety and occupational health training","correct":true},{"text":"No training needs to be documented","correct":false}]', 'Both food safety and occupational health training must be documented and records maintained for inspection.', 3),
('G-DOC-002', 'en', 'What is the purpose of documenting induction training for new staff?', '[{"text":"For payroll purposes only","correct":false},{"text":"To prove compliance and track employee preparation","correct":true},{"text":"It is not necessary to document","correct":false},{"text":"Only for insurance purposes","correct":false}]', 'Induction training documentation proves regulatory compliance and helps track that new employees are properly prepared before starting work.', 4),
('G-DOC-002', 'en', 'How should refresher training be recorded?', '[{"text":"It does not need to be recorded","correct":false},{"text":"With dates and topics covered","correct":true},{"text":"Only the year is needed","correct":false},{"text":"Only for new employees","correct":false}]', 'Refresher training must be recorded with specific dates and topics to demonstrate ongoing compliance with training requirements.', 5),
('G-DOC-002', 'en', 'Who can request to see training records?', '[{"text":"Only the owner","correct":false},{"text":"Regulatory inspectors","correct":true},{"text":"Customers","correct":false},{"text":"Competitors","correct":false}]', 'Training records must be available for inspection by regulatory authorities including health and labor inspectors.', 6),
('G-DOC-002', 'en', 'What should be done with certificates and qualifications?', '[{"text":"Throw them away after training","correct":false},{"text":"Keep them on file","correct":true},{"text":"Send them to employees only","correct":false},{"text":"They are not needed","correct":false}]', 'All certificates and qualifications must be kept on file as proof of training completion and regulatory compliance.', 7),
('G-DOC-002', 'en', 'According to which regulations must training be documented?', '[{"text":"Ley 31/1995 and EU 852/2004","correct":true},{"text":"No regulations require it","correct":false},{"text":"Only local municipal rules","correct":false},{"text":"Only company policy","correct":false}]', 'Training documentation is required under Ley 31/1995 (occupational health) and EU Regulation 852/2004 (food safety).', 8),

('G-DOC-002', 'es', '¿Qué debe incluirse en la documentación de registros de formación?', '[{"text":"Solo el tema de formación","correct":false},{"text":"Tema, fecha, duración, formador, asistentes y contenido","correct":true},{"text":"Solo nombres de asistentes","correct":false},{"text":"Solo la fecha","correct":false}]', 'Los registros completos de formación deben incluir el tema, fecha, duración, formador, asistentes y contenido cubierto.', 1),
('G-DOC-002', 'es', '¿Quién debe firmar los registros de asistencia a la formación?', '[{"text":"Solo el formador","correct":false},{"text":"Solo el gerente","correct":false},{"text":"Los participantes que asistieron","correct":true},{"text":"No se requieren firmas","correct":false}]', 'Los participantes deben firmar los registros de asistencia para verificar su participación en la sesión de formación.', 2),
('G-DOC-002', 'es', '¿Qué tipos de formación deben documentarse?', '[{"text":"Solo formación en seguridad alimentaria","correct":false},{"text":"Solo formación en salud ocupacional","correct":false},{"text":"Tanto seguridad alimentaria como salud ocupacional","correct":true},{"text":"No es necesario documentar ninguna formación","correct":false}]', 'Tanto la formación en seguridad alimentaria como en salud ocupacional deben documentarse y los registros mantenerse para inspección.', 3),
('G-DOC-002', 'es', '¿Cuál es el propósito de documentar la formación de inducción para nuevo personal?', '[{"text":"Solo para fines de nómina","correct":false},{"text":"Para demostrar cumplimiento y seguir la preparación del empleado","correct":true},{"text":"No es necesario documentar","correct":false},{"text":"Solo para fines de seguro","correct":false}]', 'La documentación de formación de inducción demuestra cumplimiento normativo y ayuda a rastrear que los nuevos empleados estén debidamente preparados antes de comenzar.', 4),
('G-DOC-002', 'es', '¿Cómo debe registrarse la formación de actualización?', '[{"text":"No necesita ser registrada","correct":false},{"text":"Con fechas y temas cubiertos","correct":true},{"text":"Solo se necesita el año","correct":false},{"text":"Solo para empleados nuevos","correct":false}]', 'La formación de actualización debe registrarse con fechas específicas y temas para demostrar cumplimiento continuo con los requisitos de formación.', 5),
('G-DOC-002', 'es', '¿Quién puede solicitar ver los registros de formación?', '[{"text":"Solo el propietario","correct":false},{"text":"Inspectores reguladores","correct":true},{"text":"Clientes","correct":false},{"text":"Competidores","correct":false}]', 'Los registros de formación deben estar disponibles para inspección por autoridades reguladoras incluyendo inspectores de salud y trabajo.', 6),
('G-DOC-002', 'es', '¿Qué debe hacerse con los certificados y cualificaciones?', '[{"text":"Tirarlos después de la formación","correct":false},{"text":"Mantenerlos archivados","correct":true},{"text":"Enviarlos solo a los empleados","correct":false},{"text":"No son necesarios","correct":false}]', 'Todos los certificados y cualificaciones deben mantenerse archivados como prueba de finalización de formación y cumplimiento normativo.', 7),
('G-DOC-002', 'es', '¿Según qué normativas debe documentarse la formación?', '[{"text":"Ley 31/1995 y UE 852/2004","correct":true},{"text":"Ninguna normativa lo requiere","correct":false},{"text":"Solo normas municipales locales","correct":false},{"text":"Solo política de empresa","correct":false}]', 'La documentación de formación es requerida bajo la Ley 31/1995 (salud ocupacional) y Reglamento UE 852/2004 (seguridad alimentaria).', 8),

('G-DOC-002', 'nl', 'Wat moet worden opgenomen in trainingsdocumentatie?', '[{"text":"Alleen het trainingsonderwerp","correct":false},{"text":"Onderwerp, datum, duur, trainer, deelnemers en inhoud","correct":true},{"text":"Alleen namen van deelnemers","correct":false},{"text":"Alleen de datum","correct":false}]', 'Complete trainingsregistraties moeten het onderwerp, datum, duur, trainer, deelnemers en behandelde inhoud bevatten.', 1),
('G-DOC-002', 'nl', 'Wie moet aanwezigheidslijsten voor training ondertekenen?', '[{"text":"Alleen de trainer","correct":false},{"text":"Alleen de manager","correct":false},{"text":"De deelnemers die aanwezig waren","correct":true},{"text":"Geen handtekeningen zijn vereist","correct":false}]', 'Deelnemers moeten aanwezigheidslijsten ondertekenen om hun deelname aan de trainingssessie te verifiëren.', 2),
('G-DOC-002', 'nl', 'Welke soorten training moeten worden gedocumenteerd?', '[{"text":"Alleen voedselveiligheidstraining","correct":false},{"text":"Alleen arbeidsgezondheid training","correct":false},{"text":"Zowel voedselveiligheid als arbeidsgezondheid training","correct":true},{"text":"Geen training hoeft te worden gedocumenteerd","correct":false}]', 'Zowel voedselveiligheid als arbeidsgezondheid training moeten worden gedocumenteerd en registraties bewaard voor inspectie.', 3),
('G-DOC-002', 'nl', 'Wat is het doel van het documenteren van introductietraining voor nieuw personeel?', '[{"text":"Alleen voor salarisadministratie","correct":false},{"text":"Om naleving te bewijzen en voorbereiding van werknemers te volgen","correct":true},{"text":"Het is niet nodig om te documenteren","correct":false},{"text":"Alleen voor verzekeringsdoeleinden","correct":false}]', 'Introductietrainingsdocumentatie bewijst naleving van regelgeving en helpt te volgen dat nieuwe werknemers goed zijn voorbereid voordat ze beginnen.', 4),
('G-DOC-002', 'nl', 'Hoe moet opfriscursus worden geregistreerd?', '[{"text":"Het hoeft niet te worden geregistreerd","correct":false},{"text":"Met datums en behandelde onderwerpen","correct":true},{"text":"Alleen het jaar is nodig","correct":false},{"text":"Alleen voor nieuwe werknemers","correct":false}]', 'Opfriscursus moet worden geregistreerd met specifieke datums en onderwerpen om voortdurende naleving van trainingsvereisten aan te tonen.', 5),
('G-DOC-002', 'nl', 'Wie kan verzoeken om trainingsregistraties te zien?', '[{"text":"Alleen de eigenaar","correct":false},{"text":"Regelgevende inspecteurs","correct":true},{"text":"Klanten","correct":false},{"text":"Concurrenten","correct":false}]', 'Trainingsregistraties moeten beschikbaar zijn voor inspectie door regelgevende autoriteiten, inclusief gezondheids- en arbeidsinspecteurs.', 6),
('G-DOC-002', 'nl', 'Wat moet er worden gedaan met certificaten en kwalificaties?', '[{"text":"Ze weggooien na training","correct":false},{"text":"Ze in het archief bewaren","correct":true},{"text":"Ze alleen naar werknemers sturen","correct":false},{"text":"Ze zijn niet nodig","correct":false}]', 'Alle certificaten en kwalificaties moeten in het archief worden bewaard als bewijs van trainingsvoltooiing en naleving van regelgeving.', 7),
('G-DOC-002', 'nl', 'Volgens welke regelgeving moet training worden gedocumenteerd?', '[{"text":"Ley 31/1995 en EU 852/2004","correct":true},{"text":"Geen regelgeving vereist dit","correct":false},{"text":"Alleen lokale gemeentelijke regels","correct":false},{"text":"Alleen bedrijfsbeleid","correct":false}]', 'Trainingsdocumentatie is vereist onder Ley 31/1995 (arbeidsgezondheid) en EU-verordening 852/2004 (voedselveiligheid).', 8),

('G-DOC-002', 'de', 'Was muss in der Schulungsdokumentation enthalten sein?', '[{"text":"Nur das Schulungsthema","correct":false},{"text":"Thema, Datum, Dauer, Trainer, Teilnehmer und Inhalt","correct":true},{"text":"Nur Namen der Teilnehmer","correct":false},{"text":"Nur das Datum","correct":false}]', 'Vollständige Schulungsaufzeichnungen müssen Thema, Datum, Dauer, Trainer, Teilnehmer und behandelten Inhalt enthalten.', 1),
('G-DOC-002', 'de', 'Wer muss Schulungsteilnahmelisten unterschreiben?', '[{"text":"Nur der Trainer","correct":false},{"text":"Nur der Manager","correct":false},{"text":"Die Teilnehmer, die anwesend waren","correct":true},{"text":"Keine Unterschriften sind erforderlich","correct":false}]', 'Teilnehmer müssen Anwesenheitslisten unterschreiben, um ihre Teilnahme an der Schulungssitzung zu bestätigen.', 2),
('G-DOC-002', 'de', 'Welche Arten von Schulungen müssen dokumentiert werden?', '[{"text":"Nur Lebensmittelsicherheitsschulung","correct":false},{"text":"Nur Arbeitsschutzschulung","correct":false},{"text":"Sowohl Lebensmittelsicherheit als auch Arbeitsschutz","correct":true},{"text":"Keine Schulung muss dokumentiert werden","correct":false}]', 'Sowohl Lebensmittelsicherheits- als auch Arbeitsschutzschulungen müssen dokumentiert und Aufzeichnungen für Inspektionen aufbewahrt werden.', 3),
('G-DOC-002', 'de', 'Was ist der Zweck der Dokumentation der Einarbeitung für neue Mitarbeiter?', '[{"text":"Nur für Lohnabrechnungszwecke","correct":false},{"text":"Um Compliance nachzuweisen und Mitarbeitervorbereitung zu verfolgen","correct":true},{"text":"Es ist nicht erforderlich zu dokumentieren","correct":false},{"text":"Nur für Versicherungszwecke","correct":false}]', 'Einarbeitungsdokumentation beweist die Einhaltung von Vorschriften und hilft nachzuverfolgen, dass neue Mitarbeiter vor Arbeitsbeginn ordnungsgemäß vorbereitet sind.', 4),
('G-DOC-002', 'de', 'Wie sollten Auffrischungsschulungen aufgezeichnet werden?', '[{"text":"Sie müssen nicht aufgezeichnet werden","correct":false},{"text":"Mit Daten und behandelten Themen","correct":true},{"text":"Nur das Jahr wird benötigt","correct":false},{"text":"Nur für neue Mitarbeiter","correct":false}]', 'Auffrischungsschulungen müssen mit spezifischen Daten und Themen aufgezeichnet werden, um die fortlaufende Einhaltung von Schulungsanforderungen nachzuweisen.', 5),
('G-DOC-002', 'de', 'Wer kann die Einsicht in Schulungsaufzeichnungen verlangen?', '[{"text":"Nur der Eigentümer","correct":false},{"text":"Aufsichtsbehörden","correct":true},{"text":"Kunden","correct":false},{"text":"Wettbewerber","correct":false}]', 'Schulungsaufzeichnungen müssen für Inspektionen durch Aufsichtsbehörden, einschließlich Gesundheits- und Arbeitsinspektoren, verfügbar sein.', 6),
('G-DOC-002', 'de', 'Was sollte mit Zertifikaten und Qualifikationen gemacht werden?', '[{"text":"Nach der Schulung wegwerfen","correct":false},{"text":"In der Akte aufbewahren","correct":true},{"text":"Nur an Mitarbeiter senden","correct":false},{"text":"Sie werden nicht benötigt","correct":false}]', 'Alle Zertifikate und Qualifikationen müssen in der Akte aufbewahrt werden als Nachweis der Schulungsvollendung und Einhaltung von Vorschriften.', 7),
('G-DOC-002', 'de', 'Gemäß welchen Vorschriften muss Schulung dokumentiert werden?', '[{"text":"Ley 31/1995 und EU 852/2004","correct":true},{"text":"Keine Vorschriften erfordern dies","correct":false},{"text":"Nur lokale kommunale Regeln","correct":false},{"text":"Nur Unternehmensrichtlinien","correct":false}]', 'Schulungsdokumentation ist erforderlich unter Ley 31/1995 (Arbeitsschutz) und EU-Verordnung 852/2004 (Lebensmittelsicherheit).', 8);

-- G-DOC-003: Annual Training Plan
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-DOC-003', 'en', 'What is the purpose of an annual training plan?', '[{"text":"To schedule vacation time","correct":false},{"text":"To ensure all staff receive necessary training throughout the year","correct":true},{"text":"To plan marketing activities","correct":false},{"text":"To reduce labor costs","correct":false}]', 'An annual training plan ensures all staff receive necessary food safety, health and safety, and skills training in an organized manner throughout the year.', 1),
('G-DOC-003', 'en', 'What should be assessed when creating a training plan?', '[{"text":"Only management preferences","correct":false},{"text":"Training needs for each role","correct":true},{"text":"Only budget constraints","correct":false},{"text":"Customer feedback only","correct":false}]', 'The annual training plan should assess specific training needs for each role to ensure relevant and effective training.', 2),
('G-DOC-003', 'en', 'Which types of training must be included in the annual plan?', '[{"text":"Only skills training","correct":false},{"text":"Food safety and PRL-specific training","correct":true},{"text":"Only management training","correct":false},{"text":"Training is optional","correct":false}]', 'The annual training plan must include mandatory food safety refresher training and PRL-specific (occupational health) training.', 3),
('G-DOC-003', 'en', 'According to which laws must an annual training plan be created?', '[{"text":"Ley 31/1995 Art. 19 and ET Art. 4.2.b","correct":true},{"text":"No legal requirement exists","correct":false},{"text":"Only company policy","correct":false},{"text":"Only health department rules","correct":false}]', 'Annual training plans are required under Ley 31/1995 Article 19 (occupational health) and Estatuto de los Trabajadores Article 4.2.b (worker rights).', 4),
('G-DOC-003', 'en', 'What should be tracked as part of the training plan?', '[{"text":"Only costs","correct":false},{"text":"Completion rates","correct":true},{"text":"Only dates","correct":false},{"text":"Nothing needs tracking","correct":false}]', 'Completion rates should be tracked to ensure all required training is completed and to identify any gaps.', 5),
('G-DOC-003', 'en', 'Why should training effectiveness be evaluated?', '[{"text":"It is not necessary to evaluate","correct":false},{"text":"To ensure training achieves its objectives and improves performance","correct":true},{"text":"Only for budget purposes","correct":false},{"text":"Only to satisfy inspectors","correct":false}]', 'Evaluating training effectiveness ensures that training achieves its objectives, improves performance, and provides value.', 6),
('G-DOC-003', 'en', 'What should be allocated in the annual training plan?', '[{"text":"Only time","correct":false},{"text":"Budget and schedule","correct":true},{"text":"Only personnel","correct":false},{"text":"Nothing needs to be allocated","correct":false}]', 'The annual training plan should allocate both budget and schedule for training sessions throughout the year.', 7),
('G-DOC-003', 'en', 'How often should food safety refresher training be scheduled?', '[{"text":"Every 5 years","correct":false},{"text":"Annually or as required","correct":true},{"text":"Never","correct":false},{"text":"Only once at hiring","correct":false}]', 'Food safety refresher training should be scheduled regularly, typically annually, to maintain knowledge and compliance.', 8),

('G-DOC-003', 'es', '¿Cuál es el propósito de un plan anual de formación?', '[{"text":"Programar tiempo de vacaciones","correct":false},{"text":"Asegurar que todo el personal reciba la formación necesaria durante el año","correct":true},{"text":"Planificar actividades de marketing","correct":false},{"text":"Reducir costos laborales","correct":false}]', 'Un plan anual de formación asegura que todo el personal reciba la formación necesaria en seguridad alimentaria, salud y seguridad, y habilidades de manera organizada durante el año.', 1),
('G-DOC-003', 'es', '¿Qué debe evaluarse al crear un plan de formación?', '[{"text":"Solo preferencias de la gerencia","correct":false},{"text":"Necesidades de formación para cada puesto","correct":true},{"text":"Solo restricciones presupuestarias","correct":false},{"text":"Solo comentarios de clientes","correct":false}]', 'El plan anual de formación debe evaluar las necesidades específicas de formación para cada puesto para asegurar una formación relevante y efectiva.', 2),
('G-DOC-003', 'es', '¿Qué tipos de formación deben incluirse en el plan anual?', '[{"text":"Solo formación de habilidades","correct":false},{"text":"Formación en seguridad alimentaria y PRL","correct":true},{"text":"Solo formación de gerencia","correct":false},{"text":"La formación es opcional","correct":false}]', 'El plan anual de formación debe incluir formación obligatoria de actualización en seguridad alimentaria y formación específica de PRL (salud ocupacional).', 3),
('G-DOC-003', 'es', '¿Según qué leyes debe crearse un plan anual de formación?', '[{"text":"Ley 31/1995 Art. 19 y ET Art. 4.2.b","correct":true},{"text":"No existe requisito legal","correct":false},{"text":"Solo política de empresa","correct":false},{"text":"Solo reglas del departamento de salud","correct":false}]', 'Los planes anuales de formación son requeridos bajo la Ley 31/1995 Artículo 19 (salud ocupacional) y Estatuto de los Trabajadores Artículo 4.2.b (derechos del trabajador).', 4),
('G-DOC-003', 'es', '¿Qué debe rastrearse como parte del plan de formación?', '[{"text":"Solo costos","correct":false},{"text":"Tasas de finalización","correct":true},{"text":"Solo fechas","correct":false},{"text":"No se necesita rastrear nada","correct":false}]', 'Las tasas de finalización deben rastrearse para asegurar que toda la formación requerida se complete y para identificar cualquier brecha.', 5),
('G-DOC-003', 'es', '¿Por qué debe evaluarse la efectividad de la formación?', '[{"text":"No es necesario evaluar","correct":false},{"text":"Para asegurar que la formación logre sus objetivos y mejore el rendimiento","correct":true},{"text":"Solo para fines presupuestarios","correct":false},{"text":"Solo para satisfacer a los inspectores","correct":false}]', 'Evaluar la efectividad de la formación asegura que la formación logre sus objetivos, mejore el rendimiento y proporcione valor.', 6),
('G-DOC-003', 'es', '¿Qué debe asignarse en el plan anual de formación?', '[{"text":"Solo tiempo","correct":false},{"text":"Presupuesto y calendario","correct":true},{"text":"Solo personal","correct":false},{"text":"No se necesita asignar nada","correct":false}]', 'El plan anual de formación debe asignar tanto presupuesto como calendario para las sesiones de formación durante el año.', 7),
('G-DOC-003', 'es', '¿Con qué frecuencia debe programarse la formación de actualización en seguridad alimentaria?', '[{"text":"Cada 5 años","correct":false},{"text":"Anualmente o según se requiera","correct":true},{"text":"Nunca","correct":false},{"text":"Solo una vez al contratar","correct":false}]', 'La formación de actualización en seguridad alimentaria debe programarse regularmente, típicamente anualmente, para mantener el conocimiento y el cumplimiento.', 8),

('G-DOC-003', 'nl', 'Wat is het doel van een jaarlijks trainingsplan?', '[{"text":"Vakantietijd plannen","correct":false},{"text":"Ervoor zorgen dat al het personeel het hele jaar door nodige training ontvangt","correct":true},{"text":"Marketingactiviteiten plannen","correct":false},{"text":"Arbeidskosten verlagen","correct":false}]', 'Een jaarlijks trainingsplan zorgt ervoor dat al het personeel op georganiseerde wijze gedurende het jaar de nodige voedselveiligheid, gezondheid en veiligheid, en vaardighedentraining ontvangt.', 1),
('G-DOC-003', 'nl', 'Wat moet worden beoordeeld bij het maken van een trainingsplan?', '[{"text":"Alleen managementvoorkeuren","correct":false},{"text":"Trainingsbehoeften voor elke rol","correct":true},{"text":"Alleen budgetbeperkingen","correct":false},{"text":"Alleen klantfeedback","correct":false}]', 'Het jaarlijkse trainingsplan moet specifieke trainingsbehoeften voor elke rol beoordelen om relevante en effectieve training te waarborgen.', 2),
('G-DOC-003', 'nl', 'Welke soorten training moeten worden opgenomen in het jaarplan?', '[{"text":"Alleen vaardighedentraining","correct":false},{"text":"Voedselveiligheid en specifieke arbeidsveiligheid training","correct":true},{"text":"Alleen managementtraining","correct":false},{"text":"Training is optioneel","correct":false}]', 'Het jaarlijkse trainingsplan moet verplichte opfriscursussen voor voedselveiligheid en specifieke arbeidsveiligheid (PRL) training bevatten.', 3),
('G-DOC-003', 'nl', 'Volgens welke wetten moet een jaarlijks trainingsplan worden gemaakt?', '[{"text":"Ley 31/1995 Art. 19 en ET Art. 4.2.b","correct":true},{"text":"Er bestaat geen wettelijke verplichting","correct":false},{"text":"Alleen bedrijfsbeleid","correct":false},{"text":"Alleen gezondheidsdepartementregels","correct":false}]', 'Jaarlijkse trainingsplannen zijn vereist onder Ley 31/1995 Artikel 19 (arbeidsgezondheid) en Estatuto de los Trabajadores Artikel 4.2.b (werknemersrechten).', 4),
('G-DOC-003', 'nl', 'Wat moet worden bijgehouden als onderdeel van het trainingsplan?', '[{"text":"Alleen kosten","correct":false},{"text":"Voltooiingspercentages","correct":true},{"text":"Alleen data","correct":false},{"text":"Niets hoeft te worden bijgehouden","correct":false}]', 'Voltooiingspercentages moeten worden bijgehouden om te garanderen dat alle vereiste training wordt voltooid en om hiaten te identificeren.', 5),
('G-DOC-003', 'nl', 'Waarom moet de effectiviteit van training worden geëvalueerd?', '[{"text":"Het is niet nodig om te evalueren","correct":false},{"text":"Om ervoor te zorgen dat training zijn doelstellingen bereikt en prestaties verbetert","correct":true},{"text":"Alleen voor budgetdoeleinden","correct":false},{"text":"Alleen om inspecteurs tevreden te stellen","correct":false}]', 'Het evalueren van trainingseffectiviteit zorgt ervoor dat training zijn doelstellingen bereikt, prestaties verbetert en waarde biedt.', 6),
('G-DOC-003', 'nl', 'Wat moet worden toegewezen in het jaarlijkse trainingsplan?', '[{"text":"Alleen tijd","correct":false},{"text":"Budget en schema","correct":true},{"text":"Alleen personeel","correct":false},{"text":"Niets hoeft te worden toegewezen","correct":false}]', 'Het jaarlijkse trainingsplan moet zowel budget als schema toewijzen voor trainingssessies gedurende het jaar.', 7),
('G-DOC-003', 'nl', 'Hoe vaak moet opfriscursus voor voedselveiligheid worden gepland?', '[{"text":"Elke 5 jaar","correct":false},{"text":"Jaarlijks of zoals vereist","correct":true},{"text":"Nooit","correct":false},{"text":"Slechts één keer bij indiensttreding","correct":false}]', 'Opfriscursus voor voedselveiligheid moet regelmatig worden gepland, meestal jaarlijks, om kennis en naleving te behouden.', 8),

('G-DOC-003', 'de', 'Was ist der Zweck eines jährlichen Schulungsplans?', '[{"text":"Urlaubszeit zu planen","correct":false},{"text":"Sicherzustellen, dass alle Mitarbeiter das ganze Jahr über erforderliche Schulungen erhalten","correct":true},{"text":"Marketingaktivitäten zu planen","correct":false},{"text":"Arbeitskosten zu senken","correct":false}]', 'Ein jährlicher Schulungsplan stellt sicher, dass alle Mitarbeiter auf organisierte Weise das ganze Jahr über erforderliche Lebensmittelsicherheits-, Gesundheits- und Sicherheits- sowie Kompetenzschulungen erhalten.', 1),
('G-DOC-003', 'de', 'Was sollte bei der Erstellung eines Schulungsplans bewertet werden?', '[{"text":"Nur Management-Präferenzen","correct":false},{"text":"Schulungsbedarf für jede Rolle","correct":true},{"text":"Nur Budgetbeschränkungen","correct":false},{"text":"Nur Kundenfeedback","correct":false}]', 'Der jährliche Schulungsplan sollte den spezifischen Schulungsbedarf für jede Rolle bewerten, um relevante und effektive Schulungen sicherzustellen.', 2),
('G-DOC-003', 'de', 'Welche Arten von Schulungen müssen im Jahresplan enthalten sein?', '[{"text":"Nur Kompetenzschulung","correct":false},{"text":"Lebensmittelsicherheit und spezifische Arbeitsschutzschulung","correct":true},{"text":"Nur Managementschulung","correct":false},{"text":"Schulung ist optional","correct":false}]', 'Der jährliche Schulungsplan muss obligatorische Auffrischungsschulungen zur Lebensmittelsicherheit und spezifische Arbeitsschutz-Schulungen (PRL) enthalten.', 3),
('G-DOC-003', 'de', 'Gemäß welchen Gesetzen muss ein jährlicher Schulungsplan erstellt werden?', '[{"text":"Ley 31/1995 Art. 19 und ET Art. 4.2.b","correct":true},{"text":"Es gibt keine gesetzliche Anforderung","correct":false},{"text":"Nur Unternehmensrichtlinien","correct":false},{"text":"Nur Gesundheitsamtsregeln","correct":false}]', 'Jährliche Schulungspläne sind erforderlich unter Ley 31/1995 Artikel 19 (Arbeitsschutz) und Estatuto de los Trabajadores Artikel 4.2.b (Arbeitnehmerrechte).', 4),
('G-DOC-003', 'de', 'Was sollte als Teil des Schulungsplans nachverfolgt werden?', '[{"text":"Nur Kosten","correct":false},{"text":"Abschlussquoten","correct":true},{"text":"Nur Daten","correct":false},{"text":"Nichts muss nachverfolgt werden","correct":false}]', 'Abschlussquoten sollten nachverfolgt werden, um sicherzustellen, dass alle erforderlichen Schulungen abgeschlossen werden und Lücken zu identifizieren.', 5),
('G-DOC-003', 'de', 'Warum sollte die Wirksamkeit der Schulung bewertet werden?', '[{"text":"Es ist nicht notwendig zu bewerten","correct":false},{"text":"Um sicherzustellen, dass Schulung ihre Ziele erreicht und Leistung verbessert","correct":true},{"text":"Nur für Budgetzwecke","correct":false},{"text":"Nur um Inspektoren zufriedenzustellen","correct":false}]', 'Die Bewertung der Schulungswirksamkeit stellt sicher, dass Schulungen ihre Ziele erreichen, die Leistung verbessern und Wert bieten.', 6),
('G-DOC-003', 'de', 'Was sollte im jährlichen Schulungsplan zugewiesen werden?', '[{"text":"Nur Zeit","correct":false},{"text":"Budget und Zeitplan","correct":true},{"text":"Nur Personal","correct":false},{"text":"Nichts muss zugewiesen werden","correct":false}]', 'Der jährliche Schulungsplan sollte sowohl Budget als auch Zeitplan für Schulungssitzungen im Laufe des Jahres zuweisen.', 7),
('G-DOC-003', 'de', 'Wie oft sollte Auffrischungsschulung zur Lebensmittelsicherheit geplant werden?', '[{"text":"Alle 5 Jahre","correct":false},{"text":"Jährlich oder nach Bedarf","correct":true},{"text":"Niemals","correct":false},{"text":"Nur einmal bei Einstellung","correct":false}]', 'Auffrischungsschulung zur Lebensmittelsicherheit sollte regelmäßig, typischerweise jährlich, geplant werden, um Wissen und Compliance zu erhalten.', 8);

-- G-DOC-004: Health Inspection Preparation
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-DOC-004', 'en', 'Can health inspections occur without prior notice?', '[{"text":"No, they must give 1 week notice","correct":false},{"text":"Yes, they can occur without notice","correct":true},{"text":"No, they must give 24 hours notice","correct":false},{"text":"They require manager approval first","correct":false}]', 'Health inspections can occur unannounced at any time. Establishments must always be prepared with current documentation.', 1),
('G-DOC-004', 'en', 'Which documentation must be up to date for health inspections?', '[{"text":"Only cleaning records","correct":false},{"text":"All food safety documentation including APPCC records","correct":true},{"text":"Only supplier lists","correct":false},{"text":"No documentation is required","correct":false}]', 'All food safety documentation must be current and accessible, including APPCC plans, temperature logs, cleaning records, and food handler certificates.', 2),
('G-DOC-004', 'en', 'What must be complete for health inspectors?', '[{"text":"Only the current day records","correct":false},{"text":"Temperature logs and cleaning records","correct":true},{"text":"Only financial records","correct":false},{"text":"Nothing specific is required","correct":false}]', 'Temperature logs and cleaning records must be complete and up to date for inspection.', 3),
('G-DOC-004', 'en', 'Where should food handler certificates be kept?', '[{"text":"At employees homes","correct":false},{"text":"Readily accessible at the establishment","correct":true},{"text":"Only in the cloud","correct":false},{"text":"They are not needed for inspection","correct":false}]', 'Food handler certificates must be readily accessible at the establishment for inspection by health authorities.', 4),
('G-DOC-004', 'en', 'Who should be designated to assist health inspectors?', '[{"text":"No one needs to be designated","correct":false},{"text":"Designated staff who know the food safety system","correct":true},{"text":"Only the cleaning crew","correct":false},{"text":"Any customer","correct":false}]', 'Designated staff members who are knowledgeable about the food safety system should be identified to accompany and assist inspectors.', 5),
('G-DOC-004', 'en', 'Under which regulation do health inspections occur?', '[{"text":"EU 882/2004 on official controls","correct":true},{"text":"No specific regulation exists","correct":false},{"text":"Only local municipal codes","correct":false},{"text":"Company policy only","correct":false}]', 'Health inspections are conducted under EU Regulation 882/2004 on official controls to verify compliance with food safety laws.', 6),
('G-DOC-004', 'en', 'What should the premises meet during inspection?', '[{"text":"Only basic cleanliness","correct":false},{"text":"Hygiene standards as required by regulations","correct":true},{"text":"No specific standards","correct":false},{"text":"Only appearance standards","correct":false}]', 'The premises must meet all hygiene standards as specified in food safety regulations including EU 852/2004.', 7),
('G-DOC-004', 'en', 'Why must APPCC records be current?', '[{"text":"It is not important","correct":false},{"text":"To demonstrate ongoing compliance with food safety management","correct":true},{"text":"Only for insurance purposes","correct":false},{"text":"Only for customer information","correct":false}]', 'APPCC records must be current to demonstrate that the food safety management system is being actively implemented and monitored.', 8),

('G-DOC-004', 'es', '¿Pueden ocurrir inspecciones sanitarias sin aviso previo?', '[{"text":"No, deben avisar con 1 semana de antelación","correct":false},{"text":"Sí, pueden ocurrir sin aviso","correct":true},{"text":"No, deben avisar con 24 horas de antelación","correct":false},{"text":"Requieren aprobación del gerente primero","correct":false}]', 'Las inspecciones sanitarias pueden ocurrir sin previo aviso en cualquier momento. Los establecimientos deben estar siempre preparados con documentación actualizada.', 1),
('G-DOC-004', 'es', '¿Qué documentación debe estar actualizada para inspecciones sanitarias?', '[{"text":"Solo registros de limpieza","correct":false},{"text":"Toda la documentación de seguridad alimentaria incluyendo registros APPCC","correct":true},{"text":"Solo listas de proveedores","correct":false},{"text":"No se requiere documentación","correct":false}]', 'Toda la documentación de seguridad alimentaria debe estar actualizada y accesible, incluyendo planes APPCC, registros de temperatura, registros de limpieza y carnets de manipulador.', 2),
('G-DOC-004', 'es', '¿Qué debe estar completo para los inspectores sanitarios?', '[{"text":"Solo los registros del día actual","correct":false},{"text":"Registros de temperatura y limpieza","correct":true},{"text":"Solo registros financieros","correct":false},{"text":"No se requiere nada específico","correct":false}]', 'Los registros de temperatura y limpieza deben estar completos y actualizados para la inspección.', 3),
('G-DOC-004', 'es', '¿Dónde deben guardarse los carnets de manipulador de alimentos?', '[{"text":"En las casas de los empleados","correct":false},{"text":"Fácilmente accesibles en el establecimiento","correct":true},{"text":"Solo en la nube","correct":false},{"text":"No son necesarios para la inspección","correct":false}]', 'Los carnets de manipulador de alimentos deben estar fácilmente accesibles en el establecimiento para inspección por las autoridades sanitarias.', 4),
('G-DOC-004', 'es', '¿Quién debe ser designado para asistir a los inspectores sanitarios?', '[{"text":"No se necesita designar a nadie","correct":false},{"text":"Personal designado que conozca el sistema de seguridad alimentaria","correct":true},{"text":"Solo el equipo de limpieza","correct":false},{"text":"Cualquier cliente","correct":false}]', 'Miembros del personal designados que conozcan el sistema de seguridad alimentaria deben ser identificados para acompañar y asistir a los inspectores.', 5),
('G-DOC-004', 'es', '¿Bajo qué normativa ocurren las inspecciones sanitarias?', '[{"text":"UE 882/2004 sobre controles oficiales","correct":true},{"text":"No existe normativa específica","correct":false},{"text":"Solo códigos municipales locales","correct":false},{"text":"Solo política de empresa","correct":false}]', 'Las inspecciones sanitarias se realizan bajo el Reglamento UE 882/2004 sobre controles oficiales para verificar el cumplimiento de las leyes de seguridad alimentaria.', 6),
('G-DOC-004', 'es', '¿Qué deben cumplir las instalaciones durante la inspección?', '[{"text":"Solo limpieza básica","correct":false},{"text":"Estándares de higiene según lo requerido por las normativas","correct":true},{"text":"Ningún estándar específico","correct":false},{"text":"Solo estándares de apariencia","correct":false}]', 'Las instalaciones deben cumplir todos los estándares de higiene especificados en las normativas de seguridad alimentaria incluyendo UE 852/2004.', 7),
('G-DOC-004', 'es', '¿Por qué deben estar actualizados los registros APPCC?', '[{"text":"No es importante","correct":false},{"text":"Para demostrar cumplimiento continuo con la gestión de seguridad alimentaria","correct":true},{"text":"Solo para fines de seguro","correct":false},{"text":"Solo para información del cliente","correct":false}]', 'Los registros APPCC deben estar actualizados para demostrar que el sistema de gestión de seguridad alimentaria está siendo activamente implementado y monitoreado.', 8),

('G-DOC-004', 'nl', 'Kunnen gezondheidsinspecties plaatsvinden zonder voorafgaande kennisgeving?', '[{"text":"Nee, ze moeten 1 week vooraf waarschuwen","correct":false},{"text":"Ja, ze kunnen onaangekondigd plaatsvinden","correct":true},{"text":"Nee, ze moeten 24 uur vooraf waarschuwen","correct":false},{"text":"Ze vereisen eerst goedkeuring van manager","correct":false}]', 'Gezondheidsinspecties kunnen op elk moment onaangekondigd plaatsvinden. Bedrijven moeten altijd voorbereid zijn met actuele documentatie.', 1),
('G-DOC-004', 'nl', 'Welke documentatie moet actueel zijn voor gezondheidsinspecties?', '[{"text":"Alleen schoonmaakregistraties","correct":false},{"text":"Alle voedselveiligheidsdocumentatie inclusief HACCP-registraties","correct":true},{"text":"Alleen leverancierslijsten","correct":false},{"text":"Geen documentatie is vereist","correct":false}]', 'Alle voedselveiligheidsdocumentatie moet actueel en toegankelijk zijn, inclusief HACCP-plannen, temperatuurlogboeken, schoonmaakregistraties en voedselverwerkingscertificaten.', 2),
('G-DOC-004', 'nl', 'Wat moet compleet zijn voor gezondheidsinspecteurs?', '[{"text":"Alleen de registraties van de huidige dag","correct":false},{"text":"Temperatuurlogboeken en schoonmaakregistraties","correct":true},{"text":"Alleen financiële records","correct":false},{"text":"Niets specifieks is vereist","correct":false}]', 'Temperatuurlogboeken en schoonmaakregistraties moeten compleet en actueel zijn voor inspectie.', 3),
('G-DOC-004', 'nl', 'Waar moeten voedselverwerkingscertificaten worden bewaard?', '[{"text":"Bij werknemers thuis","correct":false},{"text":"Direct toegankelijk in het bedrijf","correct":true},{"text":"Alleen in de cloud","correct":false},{"text":"Ze zijn niet nodig voor inspectie","correct":false}]', 'Voedselverwerkingscertificaten moeten direct toegankelijk zijn in het bedrijf voor inspectie door gezondheidsautoriteiten.', 4),
('G-DOC-004', 'nl', 'Wie moet worden aangewezen om gezondheidsinspecteurs bij te staan?', '[{"text":"Niemand hoeft te worden aangewezen","correct":false},{"text":"Aangewezen personeel dat het voedselveiligheidssysteem kent","correct":true},{"text":"Alleen het schoonmaakpersoneel","correct":false},{"text":"Elke klant","correct":false}]', 'Aangewezen personeelsleden die kennis hebben van het voedselveiligheidssysteem moeten worden geïdentificeerd om inspecteurs te begeleiden en bij te staan.', 5),
('G-DOC-004', 'nl', 'Onder welke regelgeving vinden gezondheidsinspecties plaats?', '[{"text":"EU 882/2004 over officiële controles","correct":true},{"text":"Er bestaat geen specifieke regelgeving","correct":false},{"text":"Alleen lokale gemeentelijke codes","correct":false},{"text":"Alleen bedrijfsbeleid","correct":false}]', 'Gezondheidsinspecties worden uitgevoerd onder EU-verordening 882/2004 over officiële controles om naleving van voedselveiligheidswetten te verifiëren.', 6),
('G-DOC-004', 'nl', 'Waaraan moeten de voorzieningen voldoen tijdens inspectie?', '[{"text":"Alleen basishygiëne","correct":false},{"text":"Hygiënenormen zoals vereist door regelgeving","correct":true},{"text":"Geen specifieke normen","correct":false},{"text":"Alleen uiterlijke normen","correct":false}]', 'De voorzieningen moeten voldoen aan alle hygiënenormen zoals gespecificeerd in voedselveiligheidsvoorschriften inclusief EU 852/2004.', 7),
('G-DOC-004', 'nl', 'Waarom moeten HACCP-registraties actueel zijn?', '[{"text":"Het is niet belangrijk","correct":false},{"text":"Om voortdurende naleving van voedselveiligheidsbeheer aan te tonen","correct":true},{"text":"Alleen voor verzekeringsdoeleinden","correct":false},{"text":"Alleen voor klantinformatie","correct":false}]', 'HACCP-registraties moeten actueel zijn om aan te tonen dat het voedselveiligheidsmanagementsysteem actief wordt geïmplementeerd en gemonitord.', 8),

('G-DOC-004', 'de', 'Können Gesundheitsinspektionen ohne Vorankündigung stattfinden?', '[{"text":"Nein, sie müssen 1 Woche vorher Bescheid geben","correct":false},{"text":"Ja, sie können ohne Ankündigung stattfinden","correct":true},{"text":"Nein, sie müssen 24 Stunden vorher Bescheid geben","correct":false},{"text":"Sie benötigen zuerst die Genehmigung des Managers","correct":false}]', 'Gesundheitsinspektionen können jederzeit unangekündigt stattfinden. Betriebe müssen immer mit aktueller Dokumentation vorbereitet sein.', 1),
('G-DOC-004', 'de', 'Welche Dokumentation muss für Gesundheitsinspektionen aktuell sein?', '[{"text":"Nur Reinigungsaufzeichnungen","correct":false},{"text":"Alle Lebensmittelsicherheitsdokumentation einschließlich HACCP-Aufzeichnungen","correct":true},{"text":"Nur Lieferantenlisten","correct":false},{"text":"Keine Dokumentation ist erforderlich","correct":false}]', 'Alle Lebensmittelsicherheitsdokumentation muss aktuell und zugänglich sein, einschließlich HACCP-Pläne, Temperaturprotokolle, Reinigungsaufzeichnungen und Lebensmittelhygiene-Zertifikate.', 2),
('G-DOC-004', 'de', 'Was muss für Gesundheitsinspektoren vollständig sein?', '[{"text":"Nur die Aufzeichnungen des aktuellen Tages","correct":false},{"text":"Temperaturprotokolle und Reinigungsaufzeichnungen","correct":true},{"text":"Nur Finanzunterlagen","correct":false},{"text":"Nichts Spezifisches ist erforderlich","correct":false}]', 'Temperaturprotokolle und Reinigungsaufzeichnungen müssen vollständig und aktuell für die Inspektion sein.', 3),
('G-DOC-004', 'de', 'Wo sollten Lebensmittelhygiene-Zertifikate aufbewahrt werden?', '[{"text":"Bei den Mitarbeitern zu Hause","correct":false},{"text":"Leicht zugänglich im Betrieb","correct":true},{"text":"Nur in der Cloud","correct":false},{"text":"Sie werden für Inspektionen nicht benötigt","correct":false}]', 'Lebensmittelhygiene-Zertifikate müssen im Betrieb leicht zugänglich sein für Inspektionen durch Gesundheitsbehörden.', 4),
('G-DOC-004', 'de', 'Wer sollte benannt werden, um Gesundheitsinspektoren zu unterstützen?', '[{"text":"Niemand muss benannt werden","correct":false},{"text":"Benannte Mitarbeiter, die das Lebensmittelsicherheitssystem kennen","correct":true},{"text":"Nur das Reinigungspersonal","correct":false},{"text":"Jeder Kunde","correct":false}]', 'Benannte Mitarbeiter, die über das Lebensmittelsicherheitssystem Bescheid wissen, sollten identifiziert werden, um Inspektoren zu begleiten und zu unterstützen.', 5),
('G-DOC-004', 'de', 'Unter welcher Verordnung finden Gesundheitsinspektionen statt?', '[{"text":"EU 882/2004 über amtliche Kontrollen","correct":true},{"text":"Es gibt keine spezifische Verordnung","correct":false},{"text":"Nur lokale kommunale Vorschriften","correct":false},{"text":"Nur Unternehmensrichtlinien","correct":false}]', 'Gesundheitsinspektionen werden unter EU-Verordnung 882/2004 über amtliche Kontrollen durchgeführt, um die Einhaltung von Lebensmittelsicherheitsgesetzen zu überprüfen.', 6),
('G-DOC-004', 'de', 'Was müssen die Räumlichkeiten während der Inspektion erfüllen?', '[{"text":"Nur Grundsauberkeit","correct":false},{"text":"Hygienestandards wie von Vorschriften gefordert","correct":true},{"text":"Keine spezifischen Standards","correct":false},{"text":"Nur Erscheinungsstandards","correct":false}]', 'Die Räumlichkeiten müssen alle Hygienestandards erfüllen, wie sie in Lebensmittelsicherheitsvorschriften einschließlich EU 852/2004 festgelegt sind.', 7),
('G-DOC-004', 'de', 'Warum müssen HACCP-Aufzeichnungen aktuell sein?', '[{"text":"Es ist nicht wichtig","correct":false},{"text":"Um fortlaufende Einhaltung des Lebensmittelsicherheitsmanagements nachzuweisen","correct":true},{"text":"Nur für Versicherungszwecke","correct":false},{"text":"Nur für Kundeninformationen","correct":false}]', 'HACCP-Aufzeichnungen müssen aktuell sein, um nachzuweisen, dass das Lebensmittelsicherheitsmanagementsystem aktiv implementiert und überwacht wird.', 8);

-- G-DOC-005: Labor Inspection Preparation
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-DOC-005', 'en', 'Can labor inspections arrive unannounced?', '[{"text":"No, they must give 1 month notice","correct":false},{"text":"Yes, they can arrive unannounced","correct":true},{"text":"No, they must schedule in advance","correct":false},{"text":"Only with police presence","correct":false}]', 'Labor inspections can arrive unannounced at any time. All required documentation must be maintained and readily accessible.', 1),
('G-DOC-005', 'en', 'What do labor inspections verify?', '[{"text":"Only food safety","correct":false},{"text":"Employment law, health and safety, and social security compliance","correct":true},{"text":"Only customer service","correct":false},{"text":"Only marketing practices","correct":false}]', 'Labor inspections verify compliance with employment law, health and safety regulations, and social security requirements.', 2),
('G-DOC-005', 'en', 'Which document is essential for labor inspectors?', '[{"text":"Customer reviews","correct":false},{"text":"Employment contracts","correct":true},{"text":"Menu plans","correct":false},{"text":"Marketing budgets","correct":false}]', 'Employment contracts for all staff must be accessible for labor inspectors to verify compliance with employment law.', 3),
('G-DOC-005', 'en', 'What must be operational during labor inspection?', '[{"text":"Coffee machine","correct":false},{"text":"Time recording system","correct":true},{"text":"Music system","correct":false},{"text":"Nothing specific","correct":false}]', 'The time recording system (registro de jornada) must be operational and showing current records for all employees.', 4),
('G-DOC-005', 'en', 'How long must payroll records be available?', '[{"text":"Only last month","correct":false},{"text":"Current year","correct":true},{"text":"Only last week","correct":false},{"text":"No specific period","correct":false}]', 'Payroll records (nóminas) for the current year must be available for inspection by labor authorities.', 5),
('G-DOC-005', 'en', 'What PRL documentation must be up to date?', '[{"text":"Only cleaning schedules","correct":false},{"text":"PRL Plan and risk assessments","correct":true},{"text":"Only vacation schedules","correct":false},{"text":"No PRL documentation needed","correct":false}]', 'The PRL Prevention Plan, risk assessments, and related occupational health documentation must be current and accessible.', 6),
('G-DOC-005', 'en', 'What must be verified regarding social security?', '[{"text":"Nothing about social security","correct":false},{"text":"All employees are registered with social security","correct":true},{"text":"Only manager registration","correct":false},{"text":"Social security is optional","correct":false}]', 'Labor inspectors will verify that all employees are properly registered with social security from their first day of work.', 7),
('G-DOC-005', 'en', 'Under which law do labor inspections operate?', '[{"text":"Ley 23/2015 de la Inspección de Trabajo","correct":true},{"text":"No specific law exists","correct":false},{"text":"Only company policy","correct":false},{"text":"Only health regulations","correct":false}]', 'Labor inspections operate under Ley 23/2015 de la Inspección de Trabajo y Seguridad Social (Labor Inspection Law).', 8),

('G-DOC-005', 'es', '¿Pueden las inspecciones de trabajo llegar sin previo aviso?', '[{"text":"No, deben avisar con 1 mes de antelación","correct":false},{"text":"Sí, pueden llegar sin aviso","correct":true},{"text":"No, deben programar con anticipación","correct":false},{"text":"Solo con presencia policial","correct":false}]', 'Las inspecciones de trabajo pueden llegar sin previo aviso en cualquier momento. Toda la documentación requerida debe mantenerse y ser fácilmente accesible.', 1),
('G-DOC-005', 'es', '¿Qué verifican las inspecciones de trabajo?', '[{"text":"Solo seguridad alimentaria","correct":false},{"text":"Derecho laboral, salud y seguridad, y cumplimiento de seguridad social","correct":true},{"text":"Solo servicio al cliente","correct":false},{"text":"Solo prácticas de marketing","correct":false}]', 'Las inspecciones de trabajo verifican el cumplimiento del derecho laboral, normativas de salud y seguridad, y requisitos de seguridad social.', 2),
('G-DOC-005', 'es', '¿Qué documento es esencial para los inspectores de trabajo?', '[{"text":"Reseñas de clientes","correct":false},{"text":"Contratos de trabajo","correct":true},{"text":"Planes de menú","correct":false},{"text":"Presupuestos de marketing","correct":false}]', 'Los contratos de trabajo de todo el personal deben ser accesibles para que los inspectores de trabajo verifiquen el cumplimiento del derecho laboral.', 3),
('G-DOC-005', 'es', '¿Qué debe estar operativo durante la inspección de trabajo?', '[{"text":"Cafetera","correct":false},{"text":"Sistema de registro horario","correct":true},{"text":"Sistema de música","correct":false},{"text":"Nada específico","correct":false}]', 'El sistema de registro horario (registro de jornada) debe estar operativo y mostrando registros actuales para todos los empleados.', 4),
('G-DOC-005', 'es', '¿Cuánto tiempo deben estar disponibles los registros de nómina?', '[{"text":"Solo el último mes","correct":false},{"text":"Año en curso","correct":true},{"text":"Solo la última semana","correct":false},{"text":"Ningún período específico","correct":false}]', 'Los registros de nómina del año en curso deben estar disponibles para inspección por las autoridades laborales.', 5),
('G-DOC-005', 'es', '¿Qué documentación de PRL debe estar actualizada?', '[{"text":"Solo horarios de limpieza","correct":false},{"text":"Plan de PRL y evaluaciones de riesgo","correct":true},{"text":"Solo horarios de vacaciones","correct":false},{"text":"No se necesita documentación de PRL","correct":false}]', 'El Plan de Prevención de PRL, evaluaciones de riesgo y documentación relacionada con salud ocupacional deben estar actualizados y accesibles.', 6),
('G-DOC-005', 'es', '¿Qué debe verificarse respecto a la seguridad social?', '[{"text":"Nada sobre seguridad social","correct":false},{"text":"Todos los empleados están registrados en la seguridad social","correct":true},{"text":"Solo registro del gerente","correct":false},{"text":"La seguridad social es opcional","correct":false}]', 'Los inspectores de trabajo verificarán que todos los empleados estén debidamente registrados en la seguridad social desde su primer día de trabajo.', 7),
('G-DOC-005', 'es', '¿Bajo qué ley operan las inspecciones de trabajo?', '[{"text":"Ley 23/2015 de la Inspección de Trabajo","correct":true},{"text":"No existe ley específica","correct":false},{"text":"Solo política de empresa","correct":false},{"text":"Solo normativas sanitarias","correct":false}]', 'Las inspecciones de trabajo operan bajo la Ley 23/2015 de la Inspección de Trabajo y Seguridad Social.', 8),

('G-DOC-005', 'nl', 'Kunnen arbeidsinspecties onaangekondigd arriveren?', '[{"text":"Nee, ze moeten 1 maand van tevoren waarschuwen","correct":false},{"text":"Ja, ze kunnen onaangekondigd arriveren","correct":true},{"text":"Nee, ze moeten vooraf plannen","correct":false},{"text":"Alleen met politieaanwezigheid","correct":false}]', 'Arbeidsinspecties kunnen op elk moment onaangekondigd arriveren. Alle vereiste documentatie moet worden onderhouden en direct toegankelijk zijn.', 1),
('G-DOC-005', 'nl', 'Wat verifiëren arbeidsinspecties?', '[{"text":"Alleen voedselveiligheid","correct":false},{"text":"Arbeidswetgeving, gezondheid en veiligheid, en naleving van sociale zekerheid","correct":true},{"text":"Alleen klantenservice","correct":false},{"text":"Alleen marketingpraktijken","correct":false}]', 'Arbeidsinspecties verifiëren naleving van arbeidswetgeving, gezondheids- en veiligheidsvoorschriften, en vereisten voor sociale zekerheid.', 2),
('G-DOC-005', 'nl', 'Welk document is essentieel voor arbeidsinspecteurs?', '[{"text":"Klantbeoordelingen","correct":false},{"text":"Arbeidscontracten","correct":true},{"text":"Menuplanning","correct":false},{"text":"Marketingbudgetten","correct":false}]', 'Arbeidscontracten voor al het personeel moeten toegankelijk zijn voor arbeidsinspecteurs om naleving van arbeidswetgeving te verifiëren.', 3),
('G-DOC-005', 'nl', 'Wat moet operationeel zijn tijdens arbeidsinspectie?', '[{"text":"Koffiemachine","correct":false},{"text":"Tijdregistratiesysteem","correct":true},{"text":"Muzieksysteem","correct":false},{"text":"Niets specifieks","correct":false}]', 'Het tijdregistratiesysteem (registro de jornada) moet operationeel zijn en actuele registraties tonen voor alle werknemers.', 4),
('G-DOC-005', 'nl', 'Hoe lang moeten salarisadministratie-gegevens beschikbaar zijn?', '[{"text":"Alleen de afgelopen maand","correct":false},{"text":"Huidig jaar","correct":true},{"text":"Alleen de afgelopen week","correct":false},{"text":"Geen specifieke periode","correct":false}]', 'Salarisadministratie-gegevens (nóminas) voor het huidige jaar moeten beschikbaar zijn voor inspectie door arbeidsautoriteiten.', 5),
('G-DOC-005', 'nl', 'Welke arbeidsveiligheidsdocumentatie moet actueel zijn?', '[{"text":"Alleen schoonmaakschema\'s","correct":false},{"text":"Arbeidsveiligheidsplan en risicobeoordeling","correct":true},{"text":"Alleen vakantieschema\'s","correct":false},{"text":"Geen arbeidsveiligheidsdocumentatie nodig","correct":false}]', 'Het arbeidsveiligheidsplan, risicobeoordelingen en gerelateerde arbeidsgezondheid documentatie moeten actueel en toegankelijk zijn.', 6),
('G-DOC-005', 'nl', 'Wat moet worden geverifieerd met betrekking tot sociale zekerheid?', '[{"text":"Niets over sociale zekerheid","correct":false},{"text":"Alle werknemers zijn geregistreerd bij sociale zekerheid","correct":true},{"text":"Alleen manager registratie","correct":false},{"text":"Sociale zekerheid is optioneel","correct":false}]', 'Arbeidsinspecteurs zullen verifiëren dat alle werknemers correct zijn geregistreerd bij sociale zekerheid vanaf hun eerste werkdag.', 7),
('G-DOC-005', 'nl', 'Onder welke wet opereren arbeidsinspecties?', '[{"text":"Ley 23/2015 de la Inspección de Trabajo","correct":true},{"text":"Er bestaat geen specifieke wet","correct":false},{"text":"Alleen bedrijfsbeleid","correct":false},{"text":"Alleen gezondheidsvoorschriften","correct":false}]', 'Arbeidsinspecties opereren onder Ley 23/2015 de la Inspección de Trabajo y Seguridad Social (Arbeidsinspectiewet).', 8),

('G-DOC-005', 'de', 'Können Arbeitsinspektionen unangekündigt eintreffen?', '[{"text":"Nein, sie müssen 1 Monat im Voraus Bescheid geben","correct":false},{"text":"Ja, sie können unangekündigt eintreffen","correct":true},{"text":"Nein, sie müssen im Voraus planen","correct":false},{"text":"Nur mit Polizeipräsenz","correct":false}]', 'Arbeitsinspektionen können jederzeit unangekündigt eintreffen. Alle erforderliche Dokumentation muss gepflegt und leicht zugänglich sein.', 1),
('G-DOC-005', 'de', 'Was verifizieren Arbeitsinspektionen?', '[{"text":"Nur Lebensmittelsicherheit","correct":false},{"text":"Arbeitsrecht, Gesundheit und Sicherheit und Sozialversicherungseinhaltung","correct":true},{"text":"Nur Kundenservice","correct":false},{"text":"Nur Marketingpraktiken","correct":false}]', 'Arbeitsinspektionen verifizieren die Einhaltung von Arbeitsrecht, Gesundheits- und Sicherheitsvorschriften und Sozialversicherungsanforderungen.', 2),
('G-DOC-005', 'de', 'Welches Dokument ist für Arbeitsinspektoren wesentlich?', '[{"text":"Kundenbewertungen","correct":false},{"text":"Arbeitsverträge","correct":true},{"text":"Menüpläne","correct":false},{"text":"Marketingbudgets","correct":false}]', 'Arbeitsverträge für alle Mitarbeiter müssen für Arbeitsinspektoren zugänglich sein, um die Einhaltung des Arbeitsrechts zu überprüfen.', 3),
('G-DOC-005', 'de', 'Was muss während der Arbeitsinspektion betriebsbereit sein?', '[{"text":"Kaffeemaschine","correct":false},{"text":"Zeiterfassungssystem","correct":true},{"text":"Musikanlage","correct":false},{"text":"Nichts Spezifisches","correct":false}]', 'Das Zeiterfassungssystem (registro de jornada) muss betriebsbereit sein und aktuelle Aufzeichnungen für alle Mitarbeiter anzeigen.', 4),
('G-DOC-005', 'de', 'Wie lange müssen Gehaltsabrechnungen verfügbar sein?', '[{"text":"Nur letzter Monat","correct":false},{"text":"Laufendes Jahr","correct":true},{"text":"Nur letzte Woche","correct":false},{"text":"Keine spezifische Periode","correct":false}]', 'Gehaltsabrechnungen (nóminas) für das laufende Jahr müssen für Inspektionen durch Arbeitsbehörden verfügbar sein.', 5),
('G-DOC-005', 'de', 'Welche Arbeitsschutzdokumentation muss aktuell sein?', '[{"text":"Nur Reinigungspläne","correct":false},{"text":"Arbeitsschutzplan und Risikobewertungen","correct":true},{"text":"Nur Urlaubspläne","correct":false},{"text":"Keine Arbeitsschutzdokumentation erforderlich","correct":false}]', 'Der Arbeitsschutzplan, Risikobewertungen und zugehörige Arbeitsgesundheit Dokumentation müssen aktuell und zugänglich sein.', 6),
('G-DOC-005', 'de', 'Was muss in Bezug auf Sozialversicherung verifiziert werden?', '[{"text":"Nichts über Sozialversicherung","correct":false},{"text":"Alle Mitarbeiter sind bei der Sozialversicherung registriert","correct":true},{"text":"Nur Manager-Registrierung","correct":false},{"text":"Sozialversicherung ist optional","correct":false}]', 'Arbeitsinspektoren werden verifizieren, dass alle Mitarbeiter ab ihrem ersten Arbeitstag ordnungsgemäß bei der Sozialversicherung registriert sind.', 7),
('G-DOC-005', 'de', 'Unter welchem Gesetz operieren Arbeitsinspektionen?', '[{"text":"Ley 23/2015 de la Inspección de Trabajo","correct":true},{"text":"Es gibt kein spezifisches Gesetz","correct":false},{"text":"Nur Unternehmensrichtlinien","correct":false},{"text":"Nur Gesundheitsvorschriften","correct":false}]', 'Arbeitsinspektionen operieren unter Ley 23/2015 de la Inspección de Trabajo y Seguridad Social (Arbeitsaufsichtsgesetz).', 8);

-- G-ENV-001: Waste Management
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-ENV-001', 'en', 'Under which law must businesses manage waste in Spain?', '[{"text":"No specific law exists","correct":false},{"text":"Ley 7/2022 de residuos y suelos contaminados","correct":true},{"text":"Only company policy","correct":false},{"text":"Only municipal suggestions","correct":false}]', 'Spain\'s waste law (Ley 7/2022 de residuos y suelos contaminados) requires businesses to minimize, separate, and properly dispose of waste.', 1),
('G-ENV-001', 'en', 'What are the minimum waste categories that must be separated?', '[{"text":"Only one general waste bin is needed","correct":false},{"text":"Organic, packaging, glass, paper/cardboard, and used oil","correct":true},{"text":"Only recyclables and trash","correct":false},{"text":"No separation is required","correct":false}]', 'Hospitality establishments must separate at minimum: organic waste, packaging, glass, paper/cardboard, and used oil into designated containers.', 2),
('G-ENV-001', 'en', 'What records must be maintained for waste management?', '[{"text":"No records are required","correct":false},{"text":"Waste generation records","correct":true},{"text":"Only financial records","correct":false},{"text":"Only customer feedback","correct":false}]', 'Establishments must maintain waste generation records to demonstrate compliance with waste management regulations.', 3),
('G-ENV-001', 'en', 'What must be minimized according to the waste law?', '[{"text":"Customer service","correct":false},{"text":"Single-use plastics","correct":true},{"text":"Menu options","correct":false},{"text":"Staff training","correct":false}]', 'The waste law requires hospitality businesses to minimize single-use plastics as part of waste prevention efforts.', 4),
('G-ENV-001', 'en', 'Who must collect waste from hospitality establishments?', '[{"text":"Any person","correct":false},{"text":"Authorized waste collection services","correct":true},{"text":"Customers","correct":false},{"text":"Collection is optional","correct":false}]', 'Businesses must contract authorized waste collection services for proper disposal of different waste categories.', 5),
('G-ENV-001', 'en', 'Why should waste separation instructions be displayed?', '[{"text":"It is not necessary to display them","correct":false},{"text":"To train all staff on proper waste separation","correct":true},{"text":"Only for decoration","correct":false},{"text":"Only for customer entertainment","correct":false}]', 'Clear waste separation instructions must be displayed to ensure all staff properly separate waste according to legal requirements.', 6),
('G-ENV-001', 'en', 'What is a key responsibility under the waste management law?', '[{"text":"Increase waste production","correct":false},{"text":"Minimize, separate, and dispose of waste properly","correct":true},{"text":"Ignore waste categories","correct":false},{"text":"Mix all waste together","correct":false}]', 'The law requires businesses to minimize waste generation, separate it properly, and ensure proper disposal through authorized channels.', 7),
('G-ENV-001', 'en', 'What should staff receive training on?', '[{"text":"Only cooking techniques","correct":false},{"text":"Waste separation procedures","correct":true},{"text":"Only customer service","correct":false},{"text":"Training is not required","correct":false}]', 'All staff must be trained on waste separation procedures to ensure compliance with waste management regulations.', 8),

('G-ENV-001', 'es', '¿Bajo qué ley deben las empresas gestionar residuos en España?', '[{"text":"No existe ley específica","correct":false},{"text":"Ley 7/2022 de residuos y suelos contaminados","correct":true},{"text":"Solo política de empresa","correct":false},{"text":"Solo sugerencias municipales","correct":false}]', 'La ley de residuos de España (Ley 7/2022 de residuos y suelos contaminados) requiere que las empresas minimicen, separen y eliminen adecuadamente los residuos.', 1),
('G-ENV-001', 'es', '¿Cuáles son las categorías mínimas de residuos que deben separarse?', '[{"text":"Solo se necesita un contenedor general de residuos","correct":false},{"text":"Orgánico, envases, vidrio, papel/cartón y aceite usado","correct":true},{"text":"Solo reciclables y basura","correct":false},{"text":"No se requiere separación","correct":false}]', 'Los establecimientos de hostelería deben separar al mínimo: residuos orgánicos, envases, vidrio, papel/cartón y aceite usado en contenedores designados.', 2),
('G-ENV-001', 'es', '¿Qué registros deben mantenerse para la gestión de residuos?', '[{"text":"No se requieren registros","correct":false},{"text":"Registros de generación de residuos","correct":true},{"text":"Solo registros financieros","correct":false},{"text":"Solo comentarios de clientes","correct":false}]', 'Los establecimientos deben mantener registros de generación de residuos para demostrar cumplimiento con las normativas de gestión de residuos.', 3),
('G-ENV-001', 'es', '¿Qué debe minimizarse según la ley de residuos?', '[{"text":"Servicio al cliente","correct":false},{"text":"Plásticos de un solo uso","correct":true},{"text":"Opciones de menú","correct":false},{"text":"Formación del personal","correct":false}]', 'La ley de residuos requiere que los negocios de hostelería minimicen los plásticos de un solo uso como parte de los esfuerzos de prevención de residuos.', 4),
('G-ENV-001', 'es', '¿Quién debe recoger los residuos de los establecimientos de hostelería?', '[{"text":"Cualquier persona","correct":false},{"text":"Servicios de recogida de residuos autorizados","correct":true},{"text":"Clientes","correct":false},{"text":"La recogida es opcional","correct":false}]', 'Las empresas deben contratar servicios de recogida de residuos autorizados para la eliminación adecuada de diferentes categorías de residuos.', 5),
('G-ENV-001', 'es', '¿Por qué deben mostrarse instrucciones de separación de residuos?', '[{"text":"No es necesario mostrarlas","correct":false},{"text":"Para formar a todo el personal en la separación adecuada de residuos","correct":true},{"text":"Solo para decoración","correct":false},{"text":"Solo para entretenimiento del cliente","correct":false}]', 'Las instrucciones claras de separación de residuos deben mostrarse para asegurar que todo el personal separe adecuadamente los residuos según los requisitos legales.', 6),
('G-ENV-001', 'es', '¿Cuál es una responsabilidad clave bajo la ley de gestión de residuos?', '[{"text":"Aumentar la producción de residuos","correct":false},{"text":"Minimizar, separar y eliminar residuos adecuadamente","correct":true},{"text":"Ignorar las categorías de residuos","correct":false},{"text":"Mezclar todos los residuos juntos","correct":false}]', 'La ley requiere que las empresas minimicen la generación de residuos, los separen adecuadamente y aseguren la eliminación adecuada a través de canales autorizados.', 7),
('G-ENV-001', 'es', '¿Sobre qué debe recibir formación el personal?', '[{"text":"Solo técnicas de cocina","correct":false},{"text":"Procedimientos de separación de residuos","correct":true},{"text":"Solo servicio al cliente","correct":false},{"text":"No se requiere formación","correct":false}]', 'Todo el personal debe recibir formación sobre procedimientos de separación de residuos para asegurar el cumplimiento de las normativas de gestión de residuos.', 8),

('G-ENV-001', 'nl', 'Onder welke wet moeten bedrijven afval beheren in Spanje?', '[{"text":"Er bestaat geen specifieke wet","correct":false},{"text":"Ley 7/2022 de residuos y suelos contaminados","correct":true},{"text":"Alleen bedrijfsbeleid","correct":false},{"text":"Alleen gemeentelijke suggesties","correct":false}]', 'De Spaanse afvalwet (Ley 7/2022 de residuos y suelos contaminados) vereist dat bedrijven afval minimaliseren, scheiden en op de juiste manier verwijderen.', 1),
('G-ENV-001', 'nl', 'Wat zijn de minimum afvalcategorieën die gescheiden moeten worden?', '[{"text":"Er is slechts één algemene afvalbak nodig","correct":false},{"text":"Organisch, verpakking, glas, papier/karton en gebruikt olie","correct":true},{"text":"Alleen recyclebaar en afval","correct":false},{"text":"Geen scheiding is vereist","correct":false}]', 'Horecagelegenheden moeten minimaal scheiden: organisch afval, verpakkingen, glas, papier/karton en gebruikte olie in aangewezen containers.', 2),
('G-ENV-001', 'nl', 'Welke registraties moeten worden bijgehouden voor afvalbeheer?', '[{"text":"Geen registraties zijn vereist","correct":false},{"text":"Afvalproductie registraties","correct":true},{"text":"Alleen financiële records","correct":false},{"text":"Alleen klantfeedback","correct":false}]', 'Gelegenheden moeten afvalproductie registraties bijhouden om naleving van afvalbeheersvoorschriften aan te tonen.', 3),
('G-ENV-001', 'nl', 'Wat moet worden geminimaliseerd volgens de afvalwet?', '[{"text":"Klantenservice","correct":false},{"text":"Wegwerpplastics","correct":true},{"text":"Menu-opties","correct":false},{"text":"Personeelstraining","correct":false}]', 'De afvalwet vereist dat horecabedrijven wegwerpplastics minimaliseren als onderdeel van afvalpreventie-inspanningen.', 4),
('G-ENV-001', 'nl', 'Wie moet afval ophalen van horecagelegenheden?', '[{"text":"Elke persoon","correct":false},{"text":"Geautoriseerde afvalinzamelingsdiensten","correct":true},{"text":"Klanten","correct":false},{"text":"Inzameling is optioneel","correct":false}]', 'Bedrijven moeten geautoriseerde afvalinzamelingsdiensten contracteren voor de juiste verwijdering van verschillende afvalcategorieën.', 5),
('G-ENV-001', 'nl', 'Waarom moeten afvalscheidingsinstructies worden getoond?', '[{"text":"Het is niet nodig om ze te tonen","correct":false},{"text":"Om al het personeel te trainen in de juiste afvalscheiding","correct":true},{"text":"Alleen voor decoratie","correct":false},{"text":"Alleen voor klantvermaak","correct":false}]', 'Duidelijke afvalscheidingsinstructies moeten worden getoond om ervoor te zorgen dat al het personeel afval correct scheidt volgens wettelijke vereisten.', 6),
('G-ENV-001', 'nl', 'Wat is een belangrijke verantwoordelijkheid onder de afvalbeheerwet?', '[{"text":"Afvalproductie verhogen","correct":false},{"text":"Afval minimaliseren, scheiden en correct verwijderen","correct":true},{"text":"Afvalcategorieën negeren","correct":false},{"text":"Al het afval samenvoegen","correct":false}]', 'De wet vereist dat bedrijven afvalproductie minimaliseren, het correct scheiden en zorgen voor juiste verwijdering via geautoriseerde kanalen.', 7),
('G-ENV-001', 'nl', 'Waarover moet het personeel training ontvangen?', '[{"text":"Alleen kooktechnieken","correct":false},{"text":"Afvalscheidingsprocedures","correct":true},{"text":"Alleen klantenservice","correct":false},{"text":"Training is niet vereist","correct":false}]', 'Al het personeel moet training ontvangen over afvalscheidingsprocedures om naleving van afvalbeheersvoorschriften te waarborgen.', 8),

('G-ENV-001', 'de', 'Unter welchem Gesetz müssen Unternehmen in Spanien Abfall verwalten?', '[{"text":"Es gibt kein spezifisches Gesetz","correct":false},{"text":"Ley 7/2022 de residuos y suelos contaminados","correct":true},{"text":"Nur Unternehmensrichtlinien","correct":false},{"text":"Nur kommunale Vorschläge","correct":false}]', 'Spaniens Abfallgesetz (Ley 7/2022 de residuos y suelos contaminados) erfordert, dass Unternehmen Abfall minimieren, trennen und ordnungsgemäß entsorgen.', 1),
('G-ENV-001', 'de', 'Was sind die Mindestabfallkategorien, die getrennt werden müssen?', '[{"text":"Es wird nur eine allgemeine Mülltonne benötigt","correct":false},{"text":"Organisch, Verpackung, Glas, Papier/Pappe und Altöl","correct":true},{"text":"Nur Recycling und Müll","correct":false},{"text":"Keine Trennung ist erforderlich","correct":false}]', 'Gastronomieeinrichtungen müssen mindestens trennen: organischer Abfall, Verpackungen, Glas, Papier/Pappe und Altöl in ausgewiesene Behälter.', 2),
('G-ENV-001', 'de', 'Welche Aufzeichnungen müssen für die Abfallwirtschaft geführt werden?', '[{"text":"Keine Aufzeichnungen sind erforderlich","correct":false},{"text":"Abfallerzeugungsaufzeichnungen","correct":true},{"text":"Nur Finanzunterlagen","correct":false},{"text":"Nur Kundenfeedback","correct":false}]', 'Einrichtungen müssen Abfallerzeugungsaufzeichnungen führen, um die Einhaltung von Abfallmanagementvorschriften nachzuweisen.', 3),
('G-ENV-001', 'de', 'Was muss gemäß dem Abfallgesetz minimiert werden?', '[{"text":"Kundenservice","correct":false},{"text":"Einwegplastik","correct":true},{"text":"Menüoptionen","correct":false},{"text":"Mitarbeiterschulung","correct":false}]', 'Das Abfallgesetz erfordert, dass Gastronomiebetriebe Einwegplastik als Teil der Abfallvermeidungsbemühungen minimieren.', 4),
('G-ENV-001', 'de', 'Wer muss Abfall von Gastronomiebetrieben abholen?', '[{"text":"Jede Person","correct":false},{"text":"Autorisierte Abfallentsorgungsdienste","correct":true},{"text":"Kunden","correct":false},{"text":"Abholung ist optional","correct":false}]', 'Unternehmen müssen autorisierte Abfallentsorgungsdienste für die ordnungsgemäße Entsorgung verschiedener Abfallkategorien beauftragen.', 5),
('G-ENV-001', 'de', 'Warum sollten Anweisungen zur Abfalltrennung angezeigt werden?', '[{"text":"Es ist nicht notwendig, sie anzuzeigen","correct":false},{"text":"Um alle Mitarbeiter in der ordnungsgemäßen Abfalltrennung zu schulen","correct":true},{"text":"Nur zur Dekoration","correct":false},{"text":"Nur zur Kundenunterhaltung","correct":false}]', 'Klare Anweisungen zur Abfalltrennung müssen angezeigt werden, um sicherzustellen, dass alle Mitarbeiter Abfall gemäß gesetzlichen Anforderungen ordnungsgemäß trennen.', 6),
('G-ENV-001', 'de', 'Was ist eine wichtige Verantwortung unter dem Abfallmanagementgesetz?', '[{"text":"Abfallproduktion erhöhen","correct":false},{"text":"Abfall minimieren, trennen und ordnungsgemäß entsorgen","correct":true},{"text":"Abfallkategorien ignorieren","correct":false},{"text":"Allen Abfall zusammenmischen","correct":false}]', 'Das Gesetz erfordert, dass Unternehmen Abfallerzeugung minimieren, ihn ordnungsgemäß trennen und die ordnungsgemäße Entsorgung über autorisierte Kanäle sicherstellen.', 7),
('G-ENV-001', 'de', 'Wozu sollten Mitarbeiter geschult werden?', '[{"text":"Nur Kochtechniken","correct":false},{"text":"Abfalltrennungsverfahren","correct":true},{"text":"Nur Kundenservice","correct":false},{"text":"Schulung ist nicht erforderlich","correct":false}]', 'Alle Mitarbeiter müssen über Abfalltrennungsverfahren geschult werden, um die Einhaltung von Abfallmanagementvorschriften sicherzustellen.', 8);

-- G-ENV-002: Waste Separation Protocol
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-ENV-002', 'en', 'What color container is used for organic waste?', '[{"text":"Yellow","correct":false},{"text":"Brown or green","correct":true},{"text":"Blue","correct":false},{"text":"Grey","correct":false}]', 'Organic waste should be disposed in the brown or green container, depending on the local municipality.', 1),
('G-ENV-002', 'en', 'What goes in the yellow container?', '[{"text":"Glass","correct":false},{"text":"Packaging materials","correct":true},{"text":"Paper and cardboard","correct":false},{"text":"Organic waste","correct":false}]', 'The yellow container is designated for packaging materials including plastics, cans, and packaging films.', 2),
('G-ENV-002', 'en', 'Where should glass waste be disposed?', '[{"text":"Yellow container","correct":false},{"text":"Green container","correct":true},{"text":"Brown container","correct":false},{"text":"Any container","correct":false}]', 'Glass waste should be disposed in the green container designated for glass materials.', 3),
('G-ENV-002', 'en', 'What goes in the blue container?', '[{"text":"Glass","correct":false},{"text":"Paper and cardboard","correct":true},{"text":"Packaging","correct":false},{"text":"General waste","correct":false}]', 'The blue container is designated for paper and cardboard materials.', 4),
('G-ENV-002', 'en', 'What should be done with used cooking oil?', '[{"text":"Pour it down the drain","correct":false},{"text":"Collect for authorized collector","correct":true},{"text":"Mix with organic waste","correct":false},{"text":"Throw in any container","correct":false}]', 'Used cooking oil must be collected separately and picked up by an authorized recycling collector - never poured down drains.', 5),
('G-ENV-002', 'en', 'What color container is for general non-recyclable waste?', '[{"text":"Yellow","correct":false},{"text":"Grey","correct":true},{"text":"Blue","correct":false},{"text":"Green","correct":false}]', 'The grey container is designated for general non-recyclable waste that doesn\'t fit other categories.', 6),
('G-ENV-002', 'en', 'Why is proper waste separation important?', '[{"text":"It is not important","correct":false},{"text":"It is a legal obligation and reduces recycling contamination","correct":true},{"text":"Only for aesthetics","correct":false},{"text":"Only to satisfy customers","correct":false}]', 'Proper waste separation is both a legal obligation and essential for effective recycling - contaminated waste fractions reduce recycling effectiveness.', 7),
('G-ENV-002', 'en', 'How should waste containers be labeled?', '[{"text":"No labels are needed","correct":false},{"text":"Clearly with waste type and color coding","correct":true},{"text":"Only with company logo","correct":false},{"text":"Labels are optional","correct":false}]', 'All waste containers must be clearly labeled with waste type and color coding to ensure proper separation by all staff.', 8),

('G-ENV-002', 'es', '¿Qué contenedor de color se usa para residuos orgánicos?', '[{"text":"Amarillo","correct":false},{"text":"Marrón o verde","correct":true},{"text":"Azul","correct":false},{"text":"Gris","correct":false}]', 'Los residuos orgánicos deben depositarse en el contenedor marrón o verde, dependiendo del municipio local.', 1),
('G-ENV-002', 'es', '¿Qué va en el contenedor amarillo?', '[{"text":"Vidrio","correct":false},{"text":"Materiales de envase","correct":true},{"text":"Papel y cartón","correct":false},{"text":"Residuos orgánicos","correct":false}]', 'El contenedor amarillo está designado para materiales de envase incluyendo plásticos, latas y films de embalaje.', 2),
('G-ENV-002', 'es', '¿Dónde deben depositarse los residuos de vidrio?', '[{"text":"Contenedor amarillo","correct":false},{"text":"Contenedor verde","correct":true},{"text":"Contenedor marrón","correct":false},{"text":"Cualquier contenedor","correct":false}]', 'Los residuos de vidrio deben depositarse en el contenedor verde designado para materiales de vidrio.', 3),
('G-ENV-002', 'es', '¿Qué va en el contenedor azul?', '[{"text":"Vidrio","correct":false},{"text":"Papel y cartón","correct":true},{"text":"Envases","correct":false},{"text":"Residuos generales","correct":false}]', 'El contenedor azul está designado para materiales de papel y cartón.', 4),
('G-ENV-002', 'es', '¿Qué debe hacerse con el aceite de cocina usado?', '[{"text":"Verterlo por el desagüe","correct":false},{"text":"Recogerlo para recogedor autorizado","correct":true},{"text":"Mezclarlo con residuos orgánicos","correct":false},{"text":"Tirarlo en cualquier contenedor","correct":false}]', 'El aceite de cocina usado debe recogerse por separado y ser recogido por un recolector de reciclaje autorizado - nunca verterlo por los desagües.', 5),
('G-ENV-002', 'es', '¿Qué contenedor de color es para residuos generales no reciclables?', '[{"text":"Amarillo","correct":false},{"text":"Gris","correct":true},{"text":"Azul","correct":false},{"text":"Verde","correct":false}]', 'El contenedor gris está designado para residuos generales no reciclables que no encajan en otras categorías.', 6),
('G-ENV-002', 'es', '¿Por qué es importante la separación adecuada de residuos?', '[{"text":"No es importante","correct":false},{"text":"Es una obligación legal y reduce la contaminación del reciclaje","correct":true},{"text":"Solo por estética","correct":false},{"text":"Solo para satisfacer a los clientes","correct":false}]', 'La separación adecuada de residuos es tanto una obligación legal como esencial para un reciclaje efectivo - las fracciones de residuos contaminadas reducen la efectividad del reciclaje.', 7),
('G-ENV-002', 'es', '¿Cómo deben etiquetarse los contenedores de residuos?', '[{"text":"No se necesitan etiquetas","correct":false},{"text":"Claramente con tipo de residuo y código de color","correct":true},{"text":"Solo con logo de empresa","correct":false},{"text":"Las etiquetas son opcionales","correct":false}]', 'Todos los contenedores de residuos deben estar claramente etiquetados con tipo de residuo y código de color para asegurar la separación adecuada por todo el personal.', 8),

('G-ENV-002', 'nl', 'Welke kleur container wordt gebruikt voor organisch afval?', '[{"text":"Geel","correct":false},{"text":"Bruin of groen","correct":true},{"text":"Blauw","correct":false},{"text":"Grijs","correct":false}]', 'Organisch afval moet worden gedeponeerd in de bruine of groene container, afhankelijk van de lokale gemeente.', 1),
('G-ENV-002', 'nl', 'Wat gaat er in de gele container?', '[{"text":"Glas","correct":false},{"text":"Verpakkingsmaterialen","correct":true},{"text":"Papier en karton","correct":false},{"text":"Organisch afval","correct":false}]', 'De gele container is bestemd voor verpakkingsmaterialen inclusief plastic, blikjes en verpakkingsfolie.', 2),
('G-ENV-002', 'nl', 'Waar moet glasafval worden gedeponeerd?', '[{"text":"Gele container","correct":false},{"text":"Groene container","correct":true},{"text":"Bruine container","correct":false},{"text":"Elke container","correct":false}]', 'Glasafval moet worden gedeponeerd in de groene container bestemd voor glasmaterialen.', 3),
('G-ENV-002', 'nl', 'Wat gaat er in de blauwe container?', '[{"text":"Glas","correct":false},{"text":"Papier en karton","correct":true},{"text":"Verpakking","correct":false},{"text":"Algemeen afval","correct":false}]', 'De blauwe container is bestemd voor papier en kartonmaterialen.', 4),
('G-ENV-002', 'nl', 'Wat moet er worden gedaan met gebruikt kookolie?', '[{"text":"Het in de afvoer gieten","correct":false},{"text":"Verzamelen voor geautoriseerde inzamelaar","correct":true},{"text":"Mengen met organisch afval","correct":false},{"text":"In elke container gooien","correct":false}]', 'Gebruikt kookolie moet apart worden verzameld en worden opgehaald door een geautoriseerde recycling-inzamelaar - nooit in afvoeren gieten.', 5),
('G-ENV-002', 'nl', 'Welke kleur container is voor algemeen niet-recyclebaar afval?', '[{"text":"Geel","correct":false},{"text":"Grijs","correct":true},{"text":"Blauw","correct":false},{"text":"Groen","correct":false}]', 'De grijze container is bestemd voor algemeen niet-recyclebaar afval dat niet in andere categorieën past.', 6),
('G-ENV-002', 'nl', 'Waarom is correcte afvalscheiding belangrijk?', '[{"text":"Het is niet belangrijk","correct":false},{"text":"Het is een wettelijke verplichting en vermindert recyclingverontreiniging","correct":true},{"text":"Alleen voor esthetiek","correct":false},{"text":"Alleen om klanten tevreden te stellen","correct":false}]', 'Correcte afvalscheiding is zowel een wettelijke verplichting als essentieel voor effectieve recycling - verontreinigde afvalfracties verminderen de recyclingeffectiviteit.', 7),
('G-ENV-002', 'nl', 'Hoe moeten afvalcontainers worden gelabeld?', '[{"text":"Geen labels zijn nodig","correct":false},{"text":"Duidelijk met afvaltype en kleurcodering","correct":true},{"text":"Alleen met bedrijfslogo","correct":false},{"text":"Labels zijn optioneel","correct":false}]', 'Alle afvalcontainers moeten duidelijk worden gelabeld met afvaltype en kleurcodering om correcte scheiding door al het personeel te waarborgen.', 8),

('G-ENV-002', 'de', 'Welche Farbe hat der Container für organische Abfälle?', '[{"text":"Gelb","correct":false},{"text":"Braun oder grün","correct":true},{"text":"Blau","correct":false},{"text":"Grau","correct":false}]', 'Organische Abfälle sollten im braunen oder grünen Container entsorgt werden, abhängig von der lokalen Gemeinde.', 1),
('G-ENV-002', 'de', 'Was gehört in den gelben Container?', '[{"text":"Glas","correct":false},{"text":"Verpackungsmaterialien","correct":true},{"text":"Papier und Pappe","correct":false},{"text":"Organische Abfälle","correct":false}]', 'Der gelbe Container ist für Verpackungsmaterialien einschließlich Kunststoffe, Dosen und Verpackungsfolien bestimmt.', 2),
('G-ENV-002', 'de', 'Wo sollte Glasabfall entsorgt werden?', '[{"text":"Gelber Container","correct":false},{"text":"Grüner Container","correct":true},{"text":"Brauner Container","correct":false},{"text":"Jeder Container","correct":false}]', 'Glasabfall sollte im grünen Container entsorgt werden, der für Glasmaterialien bestimmt ist.', 3),
('G-ENV-002', 'de', 'Was gehört in den blauen Container?', '[{"text":"Glas","correct":false},{"text":"Papier und Pappe","correct":true},{"text":"Verpackung","correct":false},{"text":"Allgemeiner Müll","correct":false}]', 'Der blaue Container ist für Papier- und Pappematerialien bestimmt.', 4),
('G-ENV-002', 'de', 'Was sollte mit gebrauchtem Speiseöl gemacht werden?', '[{"text":"Es in den Abfluss gießen","correct":false},{"text":"Für autorisierten Sammler sammeln","correct":true},{"text":"Mit organischem Abfall mischen","correct":false},{"text":"In jeden Container werfen","correct":false}]', 'Gebrauchtes Speiseöl muss separat gesammelt und von einem autorisierten Recycling-Sammler abgeholt werden - niemals in Abflüsse gießen.', 5),
('G-ENV-002', 'de', 'Welche Farbe hat der Container für allgemeinen nicht-recycelbaren Müll?', '[{"text":"Gelb","correct":false},{"text":"Grau","correct":true},{"text":"Blau","correct":false},{"text":"Grün","correct":false}]', 'Der graue Container ist für allgemeinen nicht-recycelbaren Müll bestimmt, der nicht in andere Kategorien passt.', 6),
('G-ENV-002', 'de', 'Warum ist ordnungsgemäße Abfalltrennung wichtig?', '[{"text":"Es ist nicht wichtig","correct":false},{"text":"Es ist eine gesetzliche Verpflichtung und reduziert Recycling-Kontamination","correct":true},{"text":"Nur zur Ästhetik","correct":false},{"text":"Nur um Kunden zufriedenzustellen","correct":false}]', 'Ordnungsgemäße Abfalltrennung ist sowohl eine gesetzliche Verpflichtung als auch wesentlich für effektives Recycling - kontaminierte Abfallfraktionen reduzieren die Recyclingeffektivität.', 7),
('G-ENV-002', 'de', 'Wie sollten Abfallbehälter gekennzeichnet sein?', '[{"text":"Keine Etiketten werden benötigt","correct":false},{"text":"Deutlich mit Abfallart und Farbcodierung","correct":true},{"text":"Nur mit Firmenlogo","correct":false},{"text":"Etiketten sind optional","correct":false}]', 'Alle Abfallbehälter müssen deutlich mit Abfallart und Farbcodierung gekennzeichnet sein, um ordnungsgemäße Trennung durch alle Mitarbeiter sicherzustellen.', 8);

-- G-ENV-003: Used Oil Management
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-ENV-003', 'en', 'What is prohibited for used cooking oil disposal?', '[{"text":"Recycling it","correct":false},{"text":"Pouring it down drains","correct":true},{"text":"Collecting it in containers","correct":false},{"text":"Giving it to authorized collectors","correct":false}]', 'Used cooking oil must never be poured down drains as it causes environmental contamination and pipe blockages. It must be collected separately.', 1),
('G-ENV-003', 'en', 'How much water can one liter of oil contaminate?', '[{"text":"10 liters","correct":false},{"text":"1,000 liters","correct":true},{"text":"100 liters","correct":false},{"text":"It does not contaminate water","correct":false}]', 'One liter of used cooking oil can contaminate up to 1,000 liters of water, making proper disposal critical for environmental protection.', 2),
('G-ENV-003', 'en', 'Who should collect used cooking oil?', '[{"text":"Any person","correct":false},{"text":"Authorized recycling companies","correct":true},{"text":"Regular waste collectors","correct":false},{"text":"No collection is needed","correct":false}]', 'Used cooking oil must be picked up by authorized recycling companies that specialize in oil collection and recycling.', 3),
('G-ENV-003', 'en', 'What records must be kept for used oil disposal?', '[{"text":"No records are needed","correct":false},{"text":"Collection receipts and records","correct":true},{"text":"Only verbal confirmation","correct":false},{"text":"Only internal notes","correct":false}]', 'Collection receipts and disposal records must be kept to demonstrate compliance with waste management regulations.', 4),
('G-ENV-003', 'en', 'Where should used oil containers be stored?', '[{"text":"Anywhere convenient","correct":false},{"text":"In a secure area","correct":true},{"text":"Next to drains","correct":false},{"text":"Storage is not important","correct":false}]', 'Used oil containers must be stored in a secure area to prevent spills, leaks, or unauthorized access.', 5),
('G-ENV-003', 'en', 'Under which regulations is used oil management required?', '[{"text":"No regulations exist","correct":false},{"text":"Ley 7/2022 and RD 679/2006","correct":true},{"text":"Only suggestions","correct":false},{"text":"Only company policy","correct":false}]', 'Used oil management is regulated under Ley 7/2022 (waste law) and RD 679/2006 (specific waste oil regulation).', 6),
('G-ENV-003', 'en', 'When should frying oil be changed?', '[{"text":"Never","correct":false},{"text":"Before degradation occurs","correct":true},{"text":"Only when black","correct":false},{"text":"Once per year","correct":false}]', 'Frying oil should be changed before significant degradation occurs, both for food safety and to facilitate recycling.', 7),
('G-ENV-003', 'en', 'What is the proper method for used oil collection?', '[{"text":"Mix with organic waste","correct":false},{"text":"Collect in designated containers","correct":true},{"text":"Pour into packaging waste","correct":false},{"text":"Burn it on site","correct":false}]', 'Used cooking oil must be collected in designated containers specifically for oil waste, kept separate from all other waste types.', 8),

('G-ENV-003', 'es', '¿Qué está prohibido para la eliminación de aceite de cocina usado?', '[{"text":"Reciclarlo","correct":false},{"text":"Verterlo por los desagües","correct":true},{"text":"Recogerlo en contenedores","correct":false},{"text":"Entregarlo a recolectores autorizados","correct":false}]', 'El aceite de cocina usado nunca debe verterse por los desagües ya que causa contaminación ambiental y obstrucciones de tuberías. Debe recogerse por separado.', 1),
('G-ENV-003', 'es', '¿Cuánta agua puede contaminar un litro de aceite?', '[{"text":"10 litros","correct":false},{"text":"1.000 litros","correct":true},{"text":"100 litros","correct":false},{"text":"No contamina el agua","correct":false}]', 'Un litro de aceite de cocina usado puede contaminar hasta 1.000 litros de agua, haciendo que la eliminación adecuada sea crítica para la protección ambiental.', 2),
('G-ENV-003', 'es', '¿Quién debe recoger el aceite de cocina usado?', '[{"text":"Cualquier persona","correct":false},{"text":"Empresas de reciclaje autorizadas","correct":true},{"text":"Recolectores de residuos regulares","correct":false},{"text":"No se necesita recogida","correct":false}]', 'El aceite de cocina usado debe ser recogido por empresas de reciclaje autorizadas que se especializan en recogida y reciclaje de aceite.', 3),
('G-ENV-003', 'es', '¿Qué registros deben mantenerse para la eliminación de aceite usado?', '[{"text":"No se necesitan registros","correct":false},{"text":"Recibos de recogida y registros","correct":true},{"text":"Solo confirmación verbal","correct":false},{"text":"Solo notas internas","correct":false}]', 'Los recibos de recogida y registros de eliminación deben mantenerse para demostrar cumplimiento con las normativas de gestión de residuos.', 4),
('G-ENV-003', 'es', '¿Dónde deben almacenarse los contenedores de aceite usado?', '[{"text":"En cualquier lugar conveniente","correct":false},{"text":"En un área segura","correct":true},{"text":"Junto a los desagües","correct":false},{"text":"El almacenamiento no es importante","correct":false}]', 'Los contenedores de aceite usado deben almacenarse en un área segura para prevenir derrames, fugas o acceso no autorizado.', 5),
('G-ENV-003', 'es', '¿Bajo qué normativas se requiere la gestión de aceite usado?', '[{"text":"No existen normativas","correct":false},{"text":"Ley 7/2022 y RD 679/2006","correct":true},{"text":"Solo sugerencias","correct":false},{"text":"Solo política de empresa","correct":false}]', 'La gestión de aceite usado está regulada bajo la Ley 7/2022 (ley de residuos) y RD 679/2006 (regulación específica de aceites residuales).', 6),
('G-ENV-003', 'es', '¿Cuándo debe cambiarse el aceite de fritura?', '[{"text":"Nunca","correct":false},{"text":"Antes de que ocurra degradación","correct":true},{"text":"Solo cuando esté negro","correct":false},{"text":"Una vez al año","correct":false}]', 'El aceite de fritura debe cambiarse antes de que ocurra degradación significativa, tanto por seguridad alimentaria como para facilitar el reciclaje.', 7),
('G-ENV-003', 'es', '¿Cuál es el método adecuado para la recogida de aceite usado?', '[{"text":"Mezclar con residuos orgánicos","correct":false},{"text":"Recoger en contenedores designados","correct":true},{"text":"Verter en residuos de envases","correct":false},{"text":"Quemarlo en el sitio","correct":false}]', 'El aceite de cocina usado debe recogerse en contenedores designados específicamente para residuos de aceite, mantenido separado de todos los demás tipos de residuos.', 8),

('G-ENV-003', 'nl', 'Wat is verboden voor de verwijdering van gebruikt kookolie?', '[{"text":"Het recyclen","correct":false},{"text":"Het in afvoeren gieten","correct":true},{"text":"Het verzamelen in containers","correct":false},{"text":"Het geven aan geautoriseerde inzamelaars","correct":false}]', 'Gebruikt kookolie mag nooit in afvoeren worden gegoten omdat het milieuvervuiling en verstoppingen van leidingen veroorzaakt. Het moet apart worden verzameld.', 1),
('G-ENV-003', 'nl', 'Hoeveel water kan één liter olie verontreinigen?', '[{"text":"10 liter","correct":false},{"text":"1.000 liter","correct":true},{"text":"100 liter","correct":false},{"text":"Het verontreinigt geen water","correct":false}]', 'Eén liter gebruikt kookolie kan tot 1.000 liter water verontreinigen, waardoor juiste verwijdering cruciaal is voor milieubescherming.', 2),
('G-ENV-003', 'nl', 'Wie moet gebruikt kookolie ophalen?', '[{"text":"Elke persoon","correct":false},{"text":"Geautoriseerde recyclingbedrijven","correct":true},{"text":"Reguliere afvalinzamelaars","correct":false},{"text":"Geen inzameling is nodig","correct":false}]', 'Gebruikt kookolie moet worden opgehaald door geautoriseerde recyclingbedrijven die gespecialiseerd zijn in olie-inzameling en recycling.', 3),
('G-ENV-003', 'nl', 'Welke registraties moeten worden bijgehouden voor verwijdering van gebruikt olie?', '[{"text":"Geen registraties zijn nodig","correct":false},{"text":"Inzamelingsbewijzen en registraties","correct":true},{"text":"Alleen mondelinge bevestiging","correct":false},{"text":"Alleen interne notities","correct":false}]', 'Inzamelingsbewijzen en verwijderingsregistraties moeten worden bijgehouden om naleving van afvalbeheersvoorschriften aan te tonen.', 4),
('G-ENV-003', 'nl', 'Waar moeten gebruikte oliecontainers worden opgeslagen?', '[{"text":"Overal waar het handig is","correct":false},{"text":"In een beveiligd gebied","correct":true},{"text":"Naast afvoeren","correct":false},{"text":"Opslag is niet belangrijk","correct":false}]', 'Gebruikte oliecontainers moeten in een beveiligd gebied worden opgeslagen om morsen, lekken of ongeautoriseerde toegang te voorkomen.', 5),
('G-ENV-003', 'nl', 'Onder welke regelgeving is beheer van gebruikt olie vereist?', '[{"text":"Er bestaan geen regelgevingen","correct":false},{"text":"Ley 7/2022 en RD 679/2006","correct":true},{"text":"Alleen suggesties","correct":false},{"text":"Alleen bedrijfsbeleid","correct":false}]', 'Beheer van gebruikt olie is gereguleerd onder Ley 7/2022 (afvalwet) en RD 679/2006 (specifieke afvalolie-regelgeving).', 6),
('G-ENV-003', 'nl', 'Wanneer moet frituurolie worden vervangen?', '[{"text":"Nooit","correct":false},{"text":"Voordat afbraak optreedt","correct":true},{"text":"Alleen wanneer het zwart is","correct":false},{"text":"Eén keer per jaar","correct":false}]', 'Frituurolie moet worden vervangen voordat significante afbraak optreedt, zowel voor voedselveiligheid als om recycling te faciliteren.', 7),
('G-ENV-003', 'nl', 'Wat is de juiste methode voor inzameling van gebruikt olie?', '[{"text":"Mengen met organisch afval","correct":false},{"text":"Verzamelen in aangewezen containers","correct":true},{"text":"Gieten in verpakkingsafval","correct":false},{"text":"Ter plaatse verbranden","correct":false}]', 'Gebruikt kookolie moet worden verzameld in aangewezen containers specifiek voor olieafval, gescheiden van alle andere afvaltypes.', 8),

('G-ENV-003', 'de', 'Was ist bei der Entsorgung von gebrauchtem Speiseöl verboten?', '[{"text":"Es zu recyceln","correct":false},{"text":"Es in Abflüsse zu gießen","correct":true},{"text":"Es in Behältern zu sammeln","correct":false},{"text":"Es autorisierten Sammlern zu geben","correct":false}]', 'Gebrauchtes Speiseöl darf niemals in Abflüsse gegossen werden, da es Umweltverschmutzung und Rohrverstopfungen verursacht. Es muss separat gesammelt werden.', 1),
('G-ENV-003', 'de', 'Wie viel Wasser kann ein Liter Öl kontaminieren?', '[{"text":"10 Liter","correct":false},{"text":"1.000 Liter","correct":true},{"text":"100 Liter","correct":false},{"text":"Es kontaminiert kein Wasser","correct":false}]', 'Ein Liter gebrauchtes Speiseöl kann bis zu 1.000 Liter Wasser kontaminieren, was die ordnungsgemäße Entsorgung für den Umweltschutz kritisch macht.', 2),
('G-ENV-003', 'de', 'Wer sollte gebrauchtes Speiseöl sammeln?', '[{"text":"Jede Person","correct":false},{"text":"Autorisierte Recyclingunternehmen","correct":true},{"text":"Reguläre Müllsammler","correct":false},{"text":"Keine Sammlung ist erforderlich","correct":false}]', 'Gebrauchtes Speiseöl muss von autorisierten Recyclingunternehmen abgeholt werden, die auf Ölsammlung und Recycling spezialisiert sind.', 3),
('G-ENV-003', 'de', 'Welche Aufzeichnungen müssen für die Entsorgung von gebrauchtem Öl geführt werden?', '[{"text":"Keine Aufzeichnungen sind erforderlich","correct":false},{"text":"Sammelquittungen und Aufzeichnungen","correct":true},{"text":"Nur mündliche Bestätigung","correct":false},{"text":"Nur interne Notizen","correct":false}]', 'Sammelquittungen und Entsorgungsaufzeichnungen müssen geführt werden, um die Einhaltung von Abfallmanagementvorschriften nachzuweisen.', 4),
('G-ENV-003', 'de', 'Wo sollten Behälter für gebrauchtes Öl gelagert werden?', '[{"text":"Überall, wo es bequem ist","correct":false},{"text":"In einem sicheren Bereich","correct":true},{"text":"Neben Abflüssen","correct":false},{"text":"Lagerung ist nicht wichtig","correct":false}]', 'Behälter für gebrauchtes Öl müssen in einem sicheren Bereich gelagert werden, um Verschüttungen, Lecks oder unbefugten Zugang zu verhindern.', 5),
('G-ENV-003', 'de', 'Unter welchen Vorschriften ist das Management von gebrauchtem Öl erforderlich?', '[{"text":"Es gibt keine Vorschriften","correct":false},{"text":"Ley 7/2022 und RD 679/2006","correct":true},{"text":"Nur Vorschläge","correct":false},{"text":"Nur Unternehmensrichtlinien","correct":false}]', 'Das Management von gebrauchtem Öl ist unter Ley 7/2022 (Abfallgesetz) und RD 679/2006 (spezifische Altölverordnung) geregelt.', 6),
('G-ENV-003', 'de', 'Wann sollte Frittieröl gewechselt werden?', '[{"text":"Niemals","correct":false},{"text":"Bevor Abbau auftritt","correct":true},{"text":"Nur wenn es schwarz ist","correct":false},{"text":"Einmal pro Jahr","correct":false}]', 'Frittieröl sollte gewechselt werden, bevor signifikanter Abbau auftritt, sowohl für Lebensmittelsicherheit als auch um Recycling zu erleichtern.', 7),
('G-ENV-003', 'de', 'Was ist die richtige Methode für die Sammlung von gebrauchtem Öl?', '[{"text":"Mit organischem Abfall mischen","correct":false},{"text":"In ausgewiesenen Behältern sammeln","correct":true},{"text":"In Verpackungsabfall gießen","correct":false},{"text":"Vor Ort verbrennen","correct":false}]', 'Gebrauchtes Speiseöl muss in ausgewiesenen Behältern speziell für Ölabfall gesammelt werden, getrennt von allen anderen Abfallarten.', 8);

-- G-ENV-004: Energy Efficiency
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-ENV-004', 'en', 'Under which regulations is energy efficiency required?', '[{"text":"No regulations exist","correct":false},{"text":"RD 390/2021 and EU 2012/27","correct":true},{"text":"Only suggestions","correct":false},{"text":"Only company preference","correct":false}]', 'Energy efficiency is regulated under RD 390/2021 (Spanish energy efficiency) and EU Directive 2012/27 on energy efficiency.', 1),
('G-ENV-004', 'en', 'What type of lighting should be used throughout?', '[{"text":"Incandescent bulbs","correct":false},{"text":"LED lighting","correct":true},{"text":"Halogen lights","correct":false},{"text":"Lighting choice does not matter","correct":false}]', 'LED lighting should be used throughout the establishment as it is significantly more energy-efficient than other lighting options.', 2),
('G-ENV-004', 'en', 'What should be done with equipment when not in use?', '[{"text":"Leave it running","correct":false},{"text":"Turn it off","correct":true},{"text":"Put it on standby only","correct":false},{"text":"It does not matter","correct":false}]', 'Equipment should be turned off when not in use to reduce energy consumption and costs.', 3),
('G-ENV-004', 'en', 'Why should refrigerator doors be kept closed?', '[{"text":"To maintain optimal temperature and energy efficiency","correct":true},{"text":"It is not important","correct":false},{"text":"Only for food safety","correct":false},{"text":"Only for appearance","correct":false}]', 'Keeping refrigerator doors closed maintains optimal temperature, prevents energy waste, and ensures food safety.', 4),
('G-ENV-004', 'en', 'What is essential for energy efficiency?', '[{"text":"Ignoring equipment issues","correct":false},{"text":"Regular equipment maintenance","correct":true},{"text":"Using old equipment","correct":false},{"text":"Maintenance is not needed","correct":false}]', 'Regular equipment maintenance is essential for energy efficiency as well-maintained equipment operates more efficiently.', 5),
('G-ENV-004', 'en', 'How often should energy consumption be monitored?', '[{"text":"Never","correct":false},{"text":"Monthly","correct":true},{"text":"Every 5 years","correct":false},{"text":"Monitoring is not required","correct":false}]', 'Energy consumption should be monitored monthly to identify trends, inefficiencies, and opportunities for improvement.', 6),
('G-ENV-004', 'en', 'What are the benefits of energy efficiency?', '[{"text":"Only appearance improvements","correct":false},{"text":"Reduced costs and environmental impact","correct":true},{"text":"No real benefits","correct":false},{"text":"Only regulatory compliance","correct":false}]', 'Energy efficiency reduces operational costs and environmental impact while also ensuring regulatory compliance.', 7),
('G-ENV-004', 'en', 'What temperature settings should be maintained?', '[{"text":"Any temperature is fine","correct":false},{"text":"Optimal temperature settings","correct":true},{"text":"Always maximum cold","correct":false},{"text":"Temperature does not matter","correct":false}]', 'Maintaining optimal temperature settings balances food safety requirements with energy efficiency.', 8),

('G-ENV-004', 'es', '¿Bajo qué normativas se requiere eficiencia energética?', '[{"text":"No existen normativas","correct":false},{"text":"RD 390/2021 y UE 2012/27","correct":true},{"text":"Solo sugerencias","correct":false},{"text":"Solo preferencia de empresa","correct":false}]', 'La eficiencia energética está regulada bajo el RD 390/2021 (eficiencia energética española) y Directiva UE 2012/27 sobre eficiencia energética.', 1),
('G-ENV-004', 'es', '¿Qué tipo de iluminación debe usarse en todo el establecimiento?', '[{"text":"Bombillas incandescentes","correct":false},{"text":"Iluminación LED","correct":true},{"text":"Luces halógenas","correct":false},{"text":"La elección de iluminación no importa","correct":false}]', 'La iluminación LED debe usarse en todo el establecimiento ya que es significativamente más eficiente energéticamente que otras opciones de iluminación.', 2),
('G-ENV-004', 'es', '¿Qué debe hacerse con el equipo cuando no está en uso?', '[{"text":"Dejarlo funcionando","correct":false},{"text":"Apagarlo","correct":true},{"text":"Ponerlo solo en espera","correct":false},{"text":"No importa","correct":false}]', 'El equipo debe apagarse cuando no esté en uso para reducir el consumo de energía y los costos.', 3),
('G-ENV-004', 'es', '¿Por qué deben mantenerse cerradas las puertas de los refrigeradores?', '[{"text":"Para mantener temperatura óptima y eficiencia energética","correct":true},{"text":"No es importante","correct":false},{"text":"Solo por seguridad alimentaria","correct":false},{"text":"Solo por apariencia","correct":false}]', 'Mantener cerradas las puertas de los refrigeradores mantiene la temperatura óptima, previene desperdicio de energía y asegura seguridad alimentaria.', 4),
('G-ENV-004', 'es', '¿Qué es esencial para la eficiencia energética?', '[{"text":"Ignorar problemas de equipo","correct":false},{"text":"Mantenimiento regular del equipo","correct":true},{"text":"Usar equipo antiguo","correct":false},{"text":"No se necesita mantenimiento","correct":false}]', 'El mantenimiento regular del equipo es esencial para la eficiencia energética ya que el equipo bien mantenido opera más eficientemente.', 5),
('G-ENV-004', 'es', '¿Con qué frecuencia debe monitorearse el consumo de energía?', '[{"text":"Nunca","correct":false},{"text":"Mensualmente","correct":true},{"text":"Cada 5 años","correct":false},{"text":"No se requiere monitoreo","correct":false}]', 'El consumo de energía debe monitorearse mensualmente para identificar tendencias, ineficiencias y oportunidades de mejora.', 6),
('G-ENV-004', 'es', '¿Cuáles son los beneficios de la eficiencia energética?', '[{"text":"Solo mejoras de apariencia","correct":false},{"text":"Costos reducidos e impacto ambiental","correct":true},{"text":"No hay beneficios reales","correct":false},{"text":"Solo cumplimiento normativo","correct":false}]', 'La eficiencia energética reduce los costos operativos y el impacto ambiental mientras también asegura el cumplimiento normativo.', 7),
('G-ENV-004', 'es', '¿Qué ajustes de temperatura deben mantenerse?', '[{"text":"Cualquier temperatura está bien","correct":false},{"text":"Ajustes de temperatura óptimos","correct":true},{"text":"Siempre frío máximo","correct":false},{"text":"La temperatura no importa","correct":false}]', 'Mantener ajustes de temperatura óptimos equilibra los requisitos de seguridad alimentaria con la eficiencia energética.', 8),

('G-ENV-004', 'nl', 'Onder welke regelgeving is energie-efficiëntie vereist?', '[{"text":"Er bestaan geen regelgevingen","correct":false},{"text":"RD 390/2021 en EU 2012/27","correct":true},{"text":"Alleen suggesties","correct":false},{"text":"Alleen bedrijfsvoorkeur","correct":false}]', 'Energie-efficiëntie is gereguleerd onder RD 390/2021 (Spaanse energie-efficiëntie) en EU-richtlijn 2012/27 over energie-efficiëntie.', 1),
('G-ENV-004', 'nl', 'Welk type verlichting moet overal worden gebruikt?', '[{"text":"Gloeilampen","correct":false},{"text":"LED-verlichting","correct":true},{"text":"Halogeenlampen","correct":false},{"text":"Verlichtingskeuze maakt niet uit","correct":false}]', 'LED-verlichting moet overal in het bedrijf worden gebruikt omdat het aanzienlijk energie-efficiënter is dan andere verlichtingsopties.', 2),
('G-ENV-004', 'nl', 'Wat moet er worden gedaan met apparatuur wanneer deze niet in gebruik is?', '[{"text":"Het laten draaien","correct":false},{"text":"Het uitzetten","correct":true},{"text":"Het alleen op stand-by zetten","correct":false},{"text":"Het maakt niet uit","correct":false}]', 'Apparatuur moet worden uitgezet wanneer deze niet in gebruik is om energieverbruik en kosten te verminderen.', 3),
('G-ENV-004', 'nl', 'Waarom moeten koelkastdeuren gesloten worden gehouden?', '[{"text":"Om optimale temperatuur en energie-efficiëntie te behouden","correct":true},{"text":"Het is niet belangrijk","correct":false},{"text":"Alleen voor voedselveiligheid","correct":false},{"text":"Alleen voor uiterlijk","correct":false}]', 'Het gesloten houden van koelkastdeuren behoudt de optimale temperatuur, voorkomt energieverspilling en waarborgt voedselveiligheid.', 4),
('G-ENV-004', 'nl', 'Wat is essentieel voor energie-efficiëntie?', '[{"text":"Apparatuurproblemen negeren","correct":false},{"text":"Regelmatig onderhoud van apparatuur","correct":true},{"text":"Oude apparatuur gebruiken","correct":false},{"text":"Onderhoud is niet nodig","correct":false}]', 'Regelmatig onderhoud van apparatuur is essentieel voor energie-efficiëntie omdat goed onderhouden apparatuur efficiënter werkt.', 5),
('G-ENV-004', 'nl', 'Hoe vaak moet energieverbruik worden gemonitord?', '[{"text":"Nooit","correct":false},{"text":"Maandelijks","correct":true},{"text":"Om de 5 jaar","correct":false},{"text":"Monitoring is niet vereist","correct":false}]', 'Energieverbruik moet maandelijks worden gemonitord om trends, inefficiënties en verbetermogelijkheden te identificeren.', 6),
('G-ENV-004', 'nl', 'Wat zijn de voordelen van energie-efficiëntie?', '[{"text":"Alleen uiterlijke verbeteringen","correct":false},{"text":"Verlaagde kosten en milieu-impact","correct":true},{"text":"Geen echte voordelen","correct":false},{"text":"Alleen naleving van regelgeving","correct":false}]', 'Energie-efficiëntie vermindert operationele kosten en milieu-impact en zorgt ook voor naleving van regelgeving.', 7),
('G-ENV-004', 'nl', 'Welke temperatuurinstellingen moeten worden gehandhaafd?', '[{"text":"Elke temperatuur is prima","correct":false},{"text":"Optimale temperatuurinstellingen","correct":true},{"text":"Altijd maximaal koud","correct":false},{"text":"Temperatuur maakt niet uit","correct":false}]', 'Het handhaven van optimale temperatuurinstellingen balanceert voedselveiligheidsvereisten met energie-efficiëntie.', 8),

('G-ENV-004', 'de', 'Unter welchen Vorschriften ist Energieeffizienz erforderlich?', '[{"text":"Es gibt keine Vorschriften","correct":false},{"text":"RD 390/2021 und EU 2012/27","correct":true},{"text":"Nur Vorschläge","correct":false},{"text":"Nur Unternehmenspräferenz","correct":false}]', 'Energieeffizienz ist unter RD 390/2021 (spanische Energieeffizienz) und EU-Richtlinie 2012/27 über Energieeffizienz geregelt.', 1),
('G-ENV-004', 'de', 'Welche Art von Beleuchtung sollte überall verwendet werden?', '[{"text":"Glühbirnen","correct":false},{"text":"LED-Beleuchtung","correct":true},{"text":"Halogenlampen","correct":false},{"text":"Beleuchtungswahl spielt keine Rolle","correct":false}]', 'LED-Beleuchtung sollte im gesamten Betrieb verwendet werden, da sie deutlich energieeffizienter ist als andere Beleuchtungsoptionen.', 2),
('G-ENV-004', 'de', 'Was sollte mit Geräten gemacht werden, wenn sie nicht in Gebrauch sind?', '[{"text":"Sie laufen lassen","correct":false},{"text":"Sie ausschalten","correct":true},{"text":"Sie nur auf Standby stellen","correct":false},{"text":"Es spielt keine Rolle","correct":false}]', 'Geräte sollten ausgeschaltet werden, wenn sie nicht in Gebrauch sind, um Energieverbrauch und Kosten zu reduzieren.', 3),
('G-ENV-004', 'de', 'Warum sollten Kühlschranktüren geschlossen gehalten werden?', '[{"text":"Um optimale Temperatur und Energieeffizienz zu erhalten","correct":true},{"text":"Es ist nicht wichtig","correct":false},{"text":"Nur für Lebensmittelsicherheit","correct":false},{"text":"Nur für Aussehen","correct":false}]', 'Das Geschlossenhalten von Kühlschranktüren erhält die optimale Temperatur, verhindert Energieverschwendung und gewährleistet Lebensmittelsicherheit.', 4),
('G-ENV-004', 'de', 'Was ist essentiell für Energieeffizienz?', '[{"text":"Geräteprobleme ignorieren","correct":false},{"text":"Regelmäßige Gerätewartung","correct":true},{"text":"Alte Geräte verwenden","correct":false},{"text":"Wartung ist nicht erforderlich","correct":false}]', 'Regelmäßige Gerätewartung ist essentiell für Energieeffizienz, da gut gewartete Geräte effizienter arbeiten.', 5),
('G-ENV-004', 'de', 'Wie oft sollte der Energieverbrauch überwacht werden?', '[{"text":"Niemals","correct":false},{"text":"Monatlich","correct":true},{"text":"Alle 5 Jahre","correct":false},{"text":"Überwachung ist nicht erforderlich","correct":false}]', 'Der Energieverbrauch sollte monatlich überwacht werden, um Trends, Ineffizienzen und Verbesserungsmöglichkeiten zu identifizieren.', 6),
('G-ENV-004', 'de', 'Was sind die Vorteile von Energieeffizienz?', '[{"text":"Nur Aussehensverbesserungen","correct":false},{"text":"Reduzierte Kosten und Umweltauswirkungen","correct":true},{"text":"Keine echten Vorteile","correct":false},{"text":"Nur Einhaltung von Vorschriften","correct":false}]', 'Energieeffizienz reduziert Betriebskosten und Umweltauswirkungen und gewährleistet gleichzeitig die Einhaltung von Vorschriften.', 7),
('G-ENV-004', 'de', 'Welche Temperatureinstellungen sollten beibehalten werden?', '[{"text":"Jede Temperatur ist in Ordnung","correct":false},{"text":"Optimale Temperatureinstellungen","correct":true},{"text":"Immer maximale Kälte","correct":false},{"text":"Temperatur spielt keine Rolle","correct":false}]', 'Die Beibehaltung optimaler Temperatureinstellungen balanciert Lebensmittelsicherheitsanforderungen mit Energieeffizienz.', 8);

-- G-ENV-005: Food Waste Reduction Program
INSERT INTO training_test_questions (guide_code, language, question, options, explanation, sort_order) VALUES
('G-ENV-005', 'en', 'What is the first step in a food waste reduction program?', '[{"text":"Buying more food","correct":false},{"text":"Auditing food waste to identify main sources","correct":true},{"text":"Ignoring waste","correct":false},{"text":"Only tracking financial losses","correct":false}]', 'A food waste audit identifies the main sources and types of waste, providing the data needed to develop effective reduction strategies.', 1),
('G-ENV-005', 'en', 'Under which laws is food waste reduction required?', '[{"text":"No laws require it","correct":false},{"text":"Ley 1/2025 and EU farm-to-fork strategy","correct":true},{"text":"Only suggestions","correct":false},{"text":"Only company preference","correct":false}]', 'Food waste reduction is required under Spain\'s Ley 1/2025 (food waste law) and supported by the EU farm-to-fork strategy.', 2),
('G-ENV-005', 'en', 'How should menus be planned to reduce waste?', '[{"text":"Without consideration of waste","correct":false},{"text":"To use ingredients efficiently","correct":true},{"text":"With maximum variety only","correct":false},{"text":"Planning does not matter","correct":false}]', 'Menus should be planned to use ingredients efficiently across multiple dishes, reducing waste from unused ingredients.', 3),
('G-ENV-005', 'en', 'What should staff be trained on for waste reduction?', '[{"text":"Only cooking speed","correct":false},{"text":"Correct portion sizes","correct":true},{"text":"Only presentation","correct":false},{"text":"Training is not needed","correct":false}]', 'Staff should be trained on correct portion sizes to prevent over-portioning, which is a major source of plate waste.', 4),
('G-ENV-005', 'en', 'What can be implemented where feasible to reduce organic waste?', '[{"text":"Nothing can be done","correct":false},{"text":"Composting","correct":true},{"text":"Burning waste","correct":false},{"text":"Burying waste","correct":false}]', 'Composting organic waste where feasible reduces landfill waste and creates valuable compost for gardening.', 5),
('G-ENV-005', 'en', 'What partnerships help reduce food waste?', '[{"text":"No partnerships are helpful","correct":false},{"text":"Food donation organizations","correct":true},{"text":"Only with competitors","correct":false},{"text":"Partnerships are not relevant","correct":false}]', 'Partnering with food donation organizations allows surplus food to be given to those in need rather than being wasted.', 6),
('G-ENV-005', 'en', 'What should be set and monitored for waste reduction?', '[{"text":"No targets are needed","correct":false},{"text":"Waste reduction targets","correct":true},{"text":"Only financial targets","correct":false},{"text":"Only customer satisfaction targets","correct":false}]', 'Setting specific waste reduction targets and monitoring progress helps ensure continuous improvement in waste reduction efforts.', 7),
('G-ENV-005', 'en', 'Beyond legal compliance, what does a food waste reduction program demonstrate?', '[{"text":"Nothing important","correct":false},{"text":"Environmental responsibility","correct":true},{"text":"Only cost-cutting","correct":false},{"text":"Only regulatory avoidance","correct":false}]', 'A food waste reduction program demonstrates environmental responsibility and corporate social responsibility beyond minimum legal compliance.', 8),

('G-ENV-005', 'es', '¿Cuál es el primer paso en un programa de reducción de desperdicio de alimentos?', '[{"text":"Comprar más comida","correct":false},{"text":"Auditar el desperdicio de alimentos para identificar fuentes principales","correct":true},{"text":"Ignorar el desperdicio","correct":false},{"text":"Solo rastrear pérdidas financieras","correct":false}]', 'Una auditoría de desperdicio de alimentos identifica las fuentes y tipos principales de desperdicio, proporcionando los datos necesarios para desarrollar estrategias efectivas de reducción.', 1),
('G-ENV-005', 'es', '¿Bajo qué leyes se requiere la reducción de desperdicio de alimentos?', '[{"text":"Ninguna ley lo requiere","correct":false},{"text":"Ley 1/2025 y estrategia de la granja a la mesa de la UE","correct":true},{"text":"Solo sugerencias","correct":false},{"text":"Solo preferencia de empresa","correct":false}]', 'La reducción de desperdicio de alimentos es requerida bajo la Ley 1/2025 de España (ley de desperdicio alimentario) y apoyada por la estrategia de la granja a la mesa de la UE.', 2),
('G-ENV-005', 'es', '¿Cómo deben planificarse los menús para reducir el desperdicio?', '[{"text":"Sin considerar el desperdicio","correct":false},{"text":"Para usar ingredientes eficientemente","correct":true},{"text":"Con variedad máxima solamente","correct":false},{"text":"La planificación no importa","correct":false}]', 'Los menús deben planificarse para usar ingredientes eficientemente en múltiples platos, reduciendo el desperdicio de ingredientes no utilizados.', 3),
('G-ENV-005', 'es', '¿Sobre qué debe formarse al personal para la reducción de desperdicio?', '[{"text":"Solo velocidad de cocina","correct":false},{"text":"Tamaños de porción correctos","correct":true},{"text":"Solo presentación","correct":false},{"text":"No se necesita formación","correct":false}]', 'El personal debe formarse sobre tamaños de porción correctos para prevenir sobre-porcionamiento, que es una fuente principal de desperdicio de platos.', 4),
('G-ENV-005', 'es', '¿Qué puede implementarse donde sea factible para reducir residuos orgánicos?', '[{"text":"No se puede hacer nada","correct":false},{"text":"Compostaje","correct":true},{"text":"Quemar residuos","correct":false},{"text":"Enterrar residuos","correct":false}]', 'El compostaje de residuos orgánicos donde sea factible reduce los residuos de vertedero y crea compost valioso para jardinería.', 5),
('G-ENV-005', 'es', '¿Qué asociaciones ayudan a reducir el desperdicio de alimentos?', '[{"text":"Ninguna asociación es útil","correct":false},{"text":"Organizaciones de donación de alimentos","correct":true},{"text":"Solo con competidores","correct":false},{"text":"Las asociaciones no son relevantes","correct":false}]', 'Asociarse con organizaciones de donación de alimentos permite que el excedente de comida se entregue a quienes lo necesitan en lugar de desperdiciarse.', 6),
('G-ENV-005', 'es', '¿Qué debe establecerse y monitorearse para la reducción de desperdicio?', '[{"text":"No se necesitan objetivos","correct":false},{"text":"Objetivos de reducción de desperdicio","correct":true},{"text":"Solo objetivos financieros","correct":false},{"text":"Solo objetivos de satisfacción del cliente","correct":false}]', 'Establecer objetivos específicos de reducción de desperdicio y monitorear el progreso ayuda a asegurar mejora continua en los esfuerzos de reducción de desperdicio.', 7),
('G-ENV-005', 'es', 'Más allá del cumplimiento legal, ¿qué demuestra un programa de reducción de desperdicio de alimentos?', '[{"text":"Nada importante","correct":false},{"text":"Responsabilidad ambiental","correct":true},{"text":"Solo reducción de costos","correct":false},{"text":"Solo evasión normativa","correct":false}]', 'Un programa de reducción de desperdicio de alimentos demuestra responsabilidad ambiental y responsabilidad social corporativa más allá del cumplimiento legal mínimo.', 8),

('G-ENV-005', 'nl', 'Wat is de eerste stap in een programma voor vermindering van voedselverspilling?', '[{"text":"Meer voedsel kopen","correct":false},{"text":"Voedselverspilling auditen om hoofdbronnen te identificeren","correct":true},{"text":"Afval negeren","correct":false},{"text":"Alleen financiële verliezen volgen","correct":false}]', 'Een voedselverspillingsaudit identificeert de hoofdbronnen en types van afval, en biedt de data die nodig is om effectieve verminderingsstrategieën te ontwikkelen.', 1),
('G-ENV-005', 'nl', 'Onder welke wetten is vermindering van voedselverspilling vereist?', '[{"text":"Geen wetten vereisen dit","correct":false},{"text":"Ley 1/2025 en EU boer-tot-bord strategie","correct":true},{"text":"Alleen suggesties","correct":false},{"text":"Alleen bedrijfsvoorkeur","correct":false}]', 'Vermindering van voedselverspilling is vereist onder Spanje\'s Ley 1/2025 (voedselverspillingswet) en ondersteund door de EU boer-tot-bord strategie.', 2),
('G-ENV-005', 'nl', 'Hoe moeten menu\'s worden gepland om afval te verminderen?', '[{"text":"Zonder rekening te houden met afval","correct":false},{"text":"Om ingrediënten efficiënt te gebruiken","correct":true},{"text":"Met alleen maximale variatie","correct":false},{"text":"Planning maakt niet uit","correct":false}]', 'Menu\'s moeten worden gepland om ingrediënten efficiënt te gebruiken over meerdere gerechten, waardoor afval van ongebruikte ingrediënten wordt verminderd.', 3),
('G-ENV-005', 'nl', 'Waarover moet het personeel worden getraind voor afvalvermindering?', '[{"text":"Alleen kooksnelheid","correct":false},{"text":"Correcte portiegroottes","correct":true},{"text":"Alleen presentatie","correct":false},{"text":"Training is niet nodig","correct":false}]', 'Personeel moet worden getraind over correcte portiegroottes om te grote porties te voorkomen, wat een belangrijke bron van bordafval is.', 4),
('G-ENV-005', 'nl', 'Wat kan waar mogelijk worden geïmplementeerd om organisch afval te verminderen?', '[{"text":"Niets kan worden gedaan","correct":false},{"text":"Composteren","correct":true},{"text":"Afval verbranden","correct":false},{"text":"Afval begraven","correct":false}]', 'Het composteren van organisch afval waar mogelijk vermindert stortplaatsafval en creëert waardevolle compost voor tuinieren.', 5),
('G-ENV-005', 'nl', 'Welke partnerschappen helpen voedselverspilling te verminderen?', '[{"text":"Geen partnerschappen zijn nuttig","correct":false},{"text":"Voedseldonatieorganisaties","correct":true},{"text":"Alleen met concurrenten","correct":false},{"text":"Partnerschappen zijn niet relevant","correct":false}]', 'Samenwerken met voedseldonatieorganisaties maakt het mogelijk dat overtollig voedsel aan behoeftigen wordt gegeven in plaats van verspild te worden.', 6),
('G-ENV-005', 'nl', 'Wat moet worden vastgesteld en gemonitord voor afvalvermindering?', '[{"text":"Geen doelen zijn nodig","correct":false},{"text":"Afvalverminderingsdoelen","correct":true},{"text":"Alleen financiële doelen","correct":false},{"text":"Alleen klanttevredenheidsdoelen","correct":false}]', 'Het stellen van specifieke afvalverminderingsdoelen en het monitoren van voortgang helpt zorgen voor voortdurende verbetering in afvalverminderingsinspanningen.', 7),
('G-ENV-005', 'nl', 'Wat toont een programma voor vermindering van voedselverspilling aan, naast wettelijke naleving?', '[{"text":"Niets belangrijks","correct":false},{"text":"Milieuverantwoordelijkheid","correct":true},{"text":"Alleen kostenbesparingen","correct":false},{"text":"Alleen vermijding van regelgeving","correct":false}]', 'Een programma voor vermindering van voedselverspilling toont milieuverantwoordelijkheid en maatschappelijk verantwoord ondernemen aan, naast minimale wettelijke naleving.', 8),

('G-ENV-005', 'de', 'Was ist der erste Schritt in einem Programm zur Reduzierung von Lebensmittelverschwendung?', '[{"text":"Mehr Essen kaufen","correct":false},{"text":"Lebensmittelverschwendung auditieren, um Hauptquellen zu identifizieren","correct":true},{"text":"Abfall ignorieren","correct":false},{"text":"Nur finanzielle Verluste verfolgen","correct":false}]', 'Ein Lebensmittelverschwendungsaudit identifiziert die Hauptquellen und Arten von Abfall und liefert die Daten, die für die Entwicklung effektiver Reduzierungsstrategien erforderlich sind.', 1),
('G-ENV-005', 'de', 'Unter welchen Gesetzen ist Reduzierung von Lebensmittelverschwendung erforderlich?', '[{"text":"Keine Gesetze erfordern dies","correct":false},{"text":"Ley 1/2025 und EU Vom Hof auf den Tisch Strategie","correct":true},{"text":"Nur Vorschläge","correct":false},{"text":"Nur Unternehmenspräferenz","correct":false}]', 'Reduzierung von Lebensmittelverschwendung ist unter Spaniens Ley 1/2025 (Lebensmittelverschwendungsgesetz) erforderlich und wird durch die EU Vom Hof auf den Tisch Strategie unterstützt.', 2),
('G-ENV-005', 'de', 'Wie sollten Menüs geplant werden, um Abfall zu reduzieren?', '[{"text":"Ohne Berücksichtigung von Abfall","correct":false},{"text":"Um Zutaten effizient zu nutzen","correct":true},{"text":"Nur mit maximaler Vielfalt","correct":false},{"text":"Planung spielt keine Rolle","correct":false}]', 'Menüs sollten geplant werden, um Zutaten effizient über mehrere Gerichte hinweg zu nutzen und Abfall von ungenutzten Zutaten zu reduzieren.', 3),
('G-ENV-005', 'de', 'Wozu sollte das Personal für Abfallreduzierung geschult werden?', '[{"text":"Nur Kochgeschwindigkeit","correct":false},{"text":"Korrekte Portionsgrößen","correct":true},{"text":"Nur Präsentation","correct":false},{"text":"Schulung ist nicht erforderlich","correct":false}]', 'Personal sollte über korrekte Portionsgrößen geschult werden, um Überportionierung zu verhindern, die eine Hauptquelle für Tellerabfall ist.', 4),
('G-ENV-005', 'de', 'Was kann implementiert werden, wo machbar, um organischen Abfall zu reduzieren?', '[{"text":"Nichts kann getan werden","correct":false},{"text":"Kompostierung","correct":true},{"text":"Abfall verbrennen","correct":false},{"text":"Abfall vergraben","correct":false}]', 'Kompostierung von organischem Abfall, wo machbar, reduziert Deponieabfall und schafft wertvollen Kompost für Gartenarbeit.', 5),
('G-ENV-005', 'de', 'Welche Partnerschaften helfen, Lebensmittelverschwendung zu reduzieren?', '[{"text":"Keine Partnerschaften sind hilfreich","correct":false},{"text":"Lebensmittelspende-Organisationen","correct":true},{"text":"Nur mit Wettbewerbern","correct":false},{"text":"Partnerschaften sind nicht relevant","correct":false}]', 'Die Partnerschaft mit Lebensmittelspende-Organisationen ermöglicht es, überschüssige Lebensmittel an Bedürftige zu geben, anstatt sie zu verschwenden.', 6),
('G-ENV-005', 'de', 'Was sollte festgelegt und überwacht werden für Abfallreduzierung?', '[{"text":"Keine Ziele sind erforderlich","correct":false},{"text":"Abfallreduzierungsziele","correct":true},{"text":"Nur finanzielle Ziele","correct":false},{"text":"Nur Kundenzufriedenheitsziele","correct":false}]', 'Das Festlegen spezifischer Abfallreduzierungsziele und die Überwachung des Fortschritts hilft, kontinuierliche Verbesserung bei Abfallreduzierungsbemühungen sicherzustellen.', 7),
('G-ENV-005', 'de', 'Was zeigt ein Programm zur Reduzierung von Lebensmittelverschwendung über die gesetzliche Einhaltung hinaus?', '[{"text":"Nichts Wichtiges","correct":false},{"text":"Umweltverantwortung","correct":true},{"text":"Nur Kostensenkung","correct":false},{"text":"Nur Vermeidung von Vorschriften","correct":false}]', 'Ein Programm zur Reduzierung von Lebensmittelverschwendung zeigt Umweltverantwortung und soziale Unternehmensverantwortung über die minimale gesetzliche Einhaltung hinaus.', 8);
